import "./App.css";
import { Routes, Route, Navigate } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import NotFound from "./pages/404";
import Customers from "./pages/customers/Customers";
import CreateCustomer from "./pages/customers/CreateCustomer";
import CustomerDetails from "./pages/customers/CustomerDetails";
import EditCustomer from "./pages/customers/EditCustomer";
import Products from "./pages/products/Products";
import NewProduct from "./pages/products/NewProduct";
import ProductView from "./pages/products/ProductView";
import EditProduct from "./pages/products/EditProduct";
import ServicesView from "./pages/services/ServicesView";
import Services from "./pages/services/Services";
import NewService from "./pages/services/NewService";
import EditService from "./pages/services/EditService";
import Invoices from "./pages/invoices/Invoices";
import ShowInvoice from "./pages/invoices/ShowInvoice";
import NewInvoice from "./pages/invoices/NewInvoice";
import Orders from "./pages/orders/Orders";
import NewOrder from "./pages/orders/NewOrder";
import OrderDetails from "./pages/orders/OrderDetails";
import AdminDashboard from "./pages/admin/AdminDashboard";
import { Suspense, useEffect } from "react";
import CustomersData from "./pages/admin/CustomersData";
import AdminRevenue from "./pages/admin/AdminRevenue";
import AdminFabrics from "./pages/admin/AdminFabrics";
import AdminInvoiceData from "./pages/admin/AdminInvoiceData";
import AdminServiceTimes from "./pages/admin/AdminServiceTimes";
import AdminTailorDetails from "./pages/admin/AdminTailorDetails";
import AdminTailors from "./pages/admin/AdminTailors";
import AdminProducts from "./pages/admin/products/AdminProducts";
import AdminProductsView from "./pages/admin/products/AdminProductsView";
import ForgotPassword from "./pages/users/ForgotPassword";
import ResetPassword from "./pages/users/ResetPassword";
import RegisterUser from "./pages/users/RegisterUser";
import CreateMaster from "./pages/users/CreateMaster";
import EditUser from "./pages/users/EditUser";
import axios from "axios";
import { useStoreActions, useStoreState } from "easy-peasy";
import DependentsPrompt from "./pages/customers/dependents/DependentsPrompt";
import CreateDependent from "./pages/customers/dependents/CreateDependent";
import ProtectedRoute from "./ProtectedRoute";
import { jwtDecode } from "jwt-decode";

function App() {
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamcotailor";
  const authKey = localStorage.getItem("jwt");
  const selectUserLanguage = useStoreActions(
    (state) => state.selectUserLanguage
  );
  const user = useStoreState((state) => state.user);
  const setUser = useStoreActions((state) => state.setUser);

  useEffect(() => {
    document.title = "Khamco Shop";

    const getUser = () => {
      if (authKey) {
        try {
          const decoded = jwtDecode(authKey);
          setUser({
            id: decoded.id,
            email: decoded.email,
            role: decoded.role,
            language: decoded.language,
          });
        } catch (error) {
          console.error("Invalid token");
          // localStorage.removeItem("jwt");
        }
      }
    };
    getUser();
  }, [authKey, setUser, user]);

  const isValid = (userRole) => {
    const validUsers = ["master", "tailor", "admin"];

    if (validUsers.includes(userRole)) {
      return true;
    }

    return false;
  };

  const isAdmin = (userRole) => {
    const validUsers = ["master", "admin"];

    if(validUsers.includes(userRole)){
      return true;
    }

    return false;
  }

  return (
    <Suspense
      fallback={
        <div>
          <div className="fas fa-rotate animate-spin"></div>
        </div>
      }
    >
      <Routes>
        <Route
          path="/"
          element={user.role === "" ? <Home /> : <Navigate to="/users/login" />}
        />
        {/* <Route
          path="/"
          element={
            <ProtectedRoute
              roles={["tailor", "admin", "master"]}
              user={user}
              element={Home}
            />
          }
        /> */}
        {/* Customer Routes */}
        {/* <Route
          path="/customers"
          element={authKey ? <Customers /> : <Navigate to="/users/login" />}
        /> */}
        <Route
          path="/customers"
          element={
            <ProtectedRoute
              roles={["tailor", "admin", "master"]}
              user={user}
              element={Customers}
            />
          }
        />
        {/* <Route exact path="/customers" element={<Customers />} /> */}
        <Route
          exact
          path="/customers/new-customer"
          element={<CreateCustomer />}
        />
        <Route exact path="/customers/:id" element={<CustomerDetails />} />
        <Route exact path="/customers/edit/:id" element={<EditCustomer />} />
        <Route
          exact
          path="/customers/dependents/dependents-prompt/:id"
          element={<DependentsPrompt />}
        />
        <Route
          exact
          path="/customers/create-dependent/:id"
          element={<CreateDependent />}
        />
        {/* Product Routes */}
        <Route exact path="/products" element={<Products />} />
        <Route exact path="/products/new" element={<NewProduct />} />
        <Route exact path="/products/:id" element={<ProductView />} />
        <Route exact path="/products/edit/:id" element={<EditProduct />} />
        {/* Services Routes */}
        <Route exact path="/services" element={<Services />} />
        <Route exact path="/services/new" element={<NewService />} />
        <Route exact path="/services/:id" element={<ServicesView />} />
        <Route exact path="/services/edit/:id" element={<EditService />} />
        {/* Invoices Routes */}
        <Route exact path="/invoices" element={<Invoices />} />
        <Route exact path="/invoices/:id" element={<ShowInvoice />} />
        <Route exact path="/invoices/new" element={<NewInvoice />} />
        {/* Orders Routes */}
        <Route exact path="/orders" element={<Orders />} />
        <Route exact path="/orders/new-order" element={<NewOrder />} />
        <Route exact path="/orders/:id" element={<OrderDetails />} />
        {/* Admin Routes */}
        <Route exact path="/admin/dashboard" element={<AdminDashboard />} />
        <Route exact path="/admin/customers-data" element={<CustomersData />} />
        <Route exact path="/admin/revenue" element={<AdminRevenue />} />
        <Route exact path="/admin/fabrics" element={<AdminFabrics />} />
        <Route
          exact
          path="/admin/invoice-data"
          element={<AdminInvoiceData />}
        />
        <Route
          exact
          path="/admin/service-times"
          element={<AdminServiceTimes />}
        />
        <Route exact path="/admin/tailors" element={<AdminTailors />} />
        <Route exact path="/admin/users/:id" element={<AdminTailorDetails />} />
        <Route exact path="/admin/products" element={<AdminProducts />} />
        <Route
          exact
          path="/admin/products/:id"
          element={<AdminProductsView />}
        />
        {/* Users Routes */}
        <Route exact path="/users/login" element={<Login />} />
        <Route
          exact
          path="/users/forgot-password"
          element={<ForgotPassword />}
        />
        <Route exact path="/users/reset-password" element={<ResetPassword />} />
        <Route exact path="/users/register-user" element={<RegisterUser />} />
        <Route exact path="/users/create-master" element={<CreateMaster />} />
        <Route exact path="/users/edit-user/:id" element={<EditUser />} />
        {/* Misc Routes */}
        <Route exact path="/unauthorized" element={<NotFound />} />
        <Route exact path="*" element={<NotFound />} />
      </Routes>
    </Suspense>
  );
}

export default App;
