import React, { useEffect, useState } from "react";
import ServiceForm from "./ServiceForm";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";

const NewService = () => {
  const [service, setService] = useState("");
  const [price, setPrice] = useState("");
  const [date, setDate] = useState(null);
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/services"
    : "https://khamco-shop-backend.onrender.com/services";

  useEffect(() => {
    document.title = "New Service - Khamco Shop";
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    if (!service || !price || !date) {
      toast.warn("Please enter all fields.");
      setLoading(false);
      return;
    }
    await axios
      .post(
        apiUrl,
        {
          service: service,
          price: price,
          date: date,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        navigate("/services");
        setLoading(false);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section className="p-[4rem] grid justify-center items-center">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link to="/services" className="text-[blue] no-underline hover:underline cursor-pointer">
              <i className="fas fa-arrow-left"></i> Services
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">New Service</h1>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <ServiceForm
              setService={setService}
              setPrice={setPrice}
              setDate={setDate}
              loading={loading}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewService;
