import { Link, useNavigate, useParams } from "react-router-dom";
import FormFields from "./Form_Fields";
import { useStoreState } from "easy-peasy";
import axios from "axios";
import { toast } from "react-toastify";
import {
  hasBottom,
  hasHeadWear,
  hasTop,
  isFootWear,
  isVest,
} from "../../functions";

const EditCustomer = () => {
  const { id } = useParams();
  const serverUrl = "http://localhost:5000";
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/invoices`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/invoices`;
  const authKey = localStorage.getItem("jwt");
  const [defaultMilitary, setDefaultStatus] = useState("civilian");
  const [defaultMembership, setDefaultMembership] = useState("Basic");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("Al Aasima");
  const [area, setArea] = useState("Abdulla Al-Salem");
  const [country, setCountry] = useState("Kuwait");
  const [militaryStatus, setMilitaryStatus] = useState("civilian");
  const [membershipStatus, setMembershipStatus] = useState("Basic");
  const [militaryNumber, setMilitaryNumber] = useState("");
  const [militaryRank, setMilitaryRank] = useState("");
  const [militaryDivision, setMilitaryDivision] = useState("");
  const [date, setDate] = useState(new Date());
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  // Measurement fields
  const [waist, setWaist] = useState(0);
  const [naturalWaist, setNaturalWaist] = useState(0);
  const [garmentLength, setGarmentLength] = useState(0);
  const [halfAcross, setHalfAcross] = useState(0);
  const [chest, setChest] = useState(0);
  const [trouserWaistPosition, setTrouserWaistPosition] = useState(0);
  const [seat, setSeat] = useState(0);
  const [neck, setNeck] = useState(0);
  const [shoulder, setShoulder] = useState(0);
  const [sleeveLength, setSleeveLength] = useState(0);
  const [sideSeamLength, setSideSeamLength] = useState(0);
  const [bodyRise, setBodyRise] = useState(0);
  const [insideLeg, setInsideLeg] = useState(0);
  const [wrist, setWrist] = useState(0);
  const [topArm, setTopArm] = useState(0);
  const [scyeDepth, setScyeDepth] = useState(0);
  const [armSleeveHole, setArmSleeveHole] = useState(0);
  const [frontalCollar, setFrontalCollar] = useState(0);
  const [hip, setHip] = useState(0);
  const [frontRise, setFrontRise] = useState(0);
  const [backRise, setBackRise] = useState(0);
  const [thigh, setThigh] = useState(0);
  const [knee, setKnee] = useState(0);
  const [hem, setHem] = useState(0);
  const [outseam, setOutseam] = useState(0);
  const [footSize, setFootSize] = useState(0);
  const [headSize, setHeadSize] = useState(0);
  const [areas, setAreas] = useState([
    "Abdulla Al-Salem",
    "Adailiya",
    "Bnaid Al-Qar",
    "Daʿiya",
    "Dasma",
    "Doha",
    "Doha Port",
    "Faiha'",
    "Failaka",
    "Granada",
    "Jaber Al-Ahmad City",
    "Jibla",
    "Kaifan",
    "Khaldiya",
    "Mansūriya",
    "Mirgab",
    "Nahdha",
    "North West Sulaibikhat",
    "Nuzha",
    "Qadsiya",
    "Qurtuba",
    "Rawda",
    "Shamiya",
    "Sharq",
    "Shuwaikh",
    "Shuwaikh Industrial Area",
    "Shuwaikh Port",
    "Sulaibikhat",
    "Surra",
    "Umm an Namil Island",
    "Yarmouk",
  ]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = `Edit Customer Details - ${
      customer && customer._id
    } Khamco Shop`;

    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
        })
        .catch((error) => {
          toast.error(error.message);
          setCustomer(null);
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();

    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, customer, userLanguage]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    await axios
      .post(
        apiUrl,
        {
          name: customerName,
          surname: customerSurname,
          email: customerEmail,
          phone: customerPhone,
          house_unit: houseUnit,
          governorate: governorate,
          area: area,
          country: country,
          military_status: militaryStatus,
          membership_status: membershipStatus,
          date_created: date,
          military_idnumber: militaryNumber,
          military_rank: militaryRank,
          military_department: militaryDivision,
          description: description,
          clothing_options: clothingOptions,
          waist: waist,
          natural_waist: naturalWaist,
          garment_length: garmentLength,
          half_across: halfAcross,
          chest: chest,
          trouser_waist_position: trouserWaistPosition,
          seat: seat,
          neck: neck,
          shoulder: shoulder,
          sleeve_length: sleeveLength,
          side_seam_length: sideSeamLength,
          body_rise: bodyRise,
          inside_leg: insideLeg,
          wrist: wrist,
          top_arm: topArm,
          scye_depth: scyeDepth,
          arm_sleeve_hole: armSleeveHole,
          frontal_collar: frontalCollar,
          hip: hip,
          front_rise: frontRise,
          back_rise: backRise,
          thigh: thigh,
          knee: knee,
          hem: hem,
          outseam: outseam,
          foot_size: footSize,
          head_size: headSize,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        navigate(
          `/customers/${response.data.customer._id}`
        );
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        toast.error(error.message);
      });
  };

  return (
    <>
      <Navbar />
      <main>
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          {customer && (
            <>
              <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
                <Link to={`${serverUrl}/customers/${customer._id}`}>
                  <i className="fas fa-arrow-left"></i> Customer {customer.name}{" "}
                  {customer.surname}
                </Link>
              </div>
              <h1 className="title">Edit Customer Details</h1>
              <form
                onSubmit={handleSubmit}
                className="flex flex-col justify-center items-center w-[90vw]"
              >
                <FormFields
                  defaultMilitary={defaultMilitary}
                  setDefaultStatus={setDefaultStatus}
                  defaultMembership={defaultMembership}
                  setDefaultMembership={setDefaultMembership}
                  setCustomerName={setCustomerName}
                  setCustomerSurname={setCustomerSurname}
                  setCustomerEmail={setCustomerEmail}
                  setCustomerPhone={setCustomerPhone}
                  setHouseUnit={setHouseUnit}
                  setGovernorate={setGovernorate}
                  setArea={setArea}
                  setCountry={setCountry}
                  militaryStatus={militaryStatus}
                  setMilitaryStatus={setMilitaryStatus}
                  setMilitaryNumber={setMilitaryNumber}
                  setMilitaryRank={setMilitaryRank}
                  setMilitaryDivision={setMilitaryDivision}
                  setMembershipStatus={setMembershipStatus}
                  setDate={setDate}
                  clothingOptions={clothingOptions}
                  setClothingOptions={setClothingOptions}
                  setDescription={setDescription}
                  areas={areas}
                  setAreas={setAreas}
                />
                {clothingOptions.length > 0 && (
                  <>
                    {/* Tops and Pants */}
                    {(hasTop(clothingOptions) ||
                      hasBottom(clothingOptions) ||
                      isVest(clothingOptions)) && (
                      <div className="form-row">
                        <input
                          type="number"
                          step={0.1}
                          placeholder="Waist"
                          onChange={(e) => setWaist(e.target.value)}
                        />
                      </div>
                    )}
                    {/* Shirts (long sleeve and short) */}
                    {(hasTop(clothingOptions) || isVest(clothingOptions)) && (
                      <>
                        <div className="form-row">
                          <input
                            type="number"
                            step={0.1}
                            placeholder="Natural Waist Length"
                            onChange={(e) => setNaturalWaist(e.target.value)}
                          />
                        </div>
                        <div className="form-row">
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Garment Length"
                              onChange={(e) => setGarmentLength(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Half Across Back"
                              onChange={(e) => setHalfAcross(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Chest"
                              onChange={(e) => setChest(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Trouser Waist Position"
                              onChange={(e) =>
                                setTrouserWaistPosition(e.target.value)
                              }
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Seat"
                              onChange={(e) => setSeat(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Neck"
                              onChange={(e) => setNeck(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Shoulder"
                              onChange={(e) => setShoulder(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Sleeve Length"
                              onChange={(e) => setSleeveLength(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Side Seam Length"
                              onChange={(e) =>
                                setSideSeamLength(e.target.value)
                              }
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Body Rise"
                              onChange={(e) => setBodyRise(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Inside Leg"
                              onChange={(e) => setInsideLeg(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Wrist"
                              onChange={(e) => setWrist(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Top Arm"
                              onChange={(e) => setTopArm(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Scye Depth"
                              onChange={(e) => setScyeDepth(e.target.value)}
                            />
                          </div>
                          {/* Vests only */}
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Sleeve Hole"
                              onChange={(e) => setArmSleeveHole(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Collar Opening"
                              onChange={(e) => setFrontalCollar(e.target.value)}
                            />
                          </div>
                        </div>
                      </>
                    )}
                    {/* Pants/Trousers: */}
                    {hasBottom(clothingOptions) && (
                      <>
                        <div className="form-row">
                          <input
                            type="number"
                            step={0.1}
                            placeholder="Hip (Inches)"
                            onChange={(e) => setHip(e.target.value)}
                          />
                        </div>
                        <div className="form-row">
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Front Rise"
                              onChange={(e) => setFrontRise(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Back Rise"
                              onChange={(e) => setBackRise(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Thigh"
                              onChange={(e) => setThigh(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Knee"
                              onChange={(e) => setKnee(e.target.value)}
                            />
                          </div>
                          <div className="inline">
                            <input
                              type="number"
                              step={0.1}
                              placeholder="Hem"
                              onChange={(e) => setHem(e.target.value)}
                            />
                            <input
                              type="number"
                              step={0.1}
                              placeholder=" Outseam"
                              onChange={(e) => setOutseam(e.target.value)}
                            />
                          </div>
                        </div>
                      </>
                    )}
                    {/* Hats */}
                    {hasHeadWear(clothingOptions) && (
                      <div className="form-row">
                        <input
                          type="number"
                          step={0.1}
                          placeholder="Head Size"
                          onChange={(e) => setHeadSize(e.target.value)}
                        />
                      </div>
                    )}
                    {/* Footwear */}
                    {isFootWear(clothingOptions) && (
                      <div className="form-row">
                        <input
                          type="number"
                          step={0.1}
                          placeholder="Foot Size"
                          onChange={(e) => setFootSize(e.target.value)}
                        />
                      </div>
                    )}
                  </>
                )}
                <div className="form-row">
                  <textarea
                    placeholder="Extra notes (optional)"
                    onChange={(e) => setDescription(e.target.value)}
                  ></textarea>
                </div>
                <div className="form-row">
                  <button type="submit">
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      "Submit"
                    )}
                  </button>
                </div>
              </form>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditCustomer;
