import { Link, useNavigate, useParams } from "react-router-dom";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useEffect, useRef, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { applyAnimation, hasRoles } from "../../utilities";
import { useTranslation } from "react-i18next";

const UserProfile = () => {
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [allUsers, setAllUsers] = useState([]);
  const [customers, setCustomers] = useState([]);
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [activeModalOrderId, setActiveModalOrderId] = useState({});
  const [newOrders, setNewOrders] = useState(null);
  const [currentOrders, setCurrentOrders] = useState(null);
  const [completedOrders, setCompletedOrders] = useState(null);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [tailor, setTailor] = useState("");
  const [driver, setDriver] = useState("");
  const [userType, setUserType] = useState("");
  const [allOrders, setAllOrders] = useState([]);
  const userRef = useRef(null);
  const [requiresAlteration, setRequiresAlteration] = useState(false);
  const [orderComplete, setOrderComplete] = useState(false);
  const { t } = useTranslation();

  useEffect(() => {
    const controller = new AbortController();
    const signal = controller.signal;

    const fetchData = async () => {
      try {
        const response = await axios.get(
          `${serverUrl}/admin/users/profile/${id}`,
          {
            headers: { Authorization: `Bearer ${authKey}` },
            signal,
          }
        );
        if (!signal.aborted) {
          document.title = `Tailor Dashboard - ${response.data.user.name}`;
          setIsLoading(false);
          setUser(response.data.user);
          setCustomers(response.data.customers);
          updateOrders(response.data.user_orders, response.data.user);
          setAllUsers(response.data.all_users);
          setFilteredUsers(response.data.all_users);
          setUserType(response.data.user.role);
          setAllOrders(response.data.all_orders);
        }
      } catch (error) {
        if (!signal.aborted) {
          setIsLoading(false);
          if (
            error.response?.status === 403 ||
            error.response?.status === 404
          ) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response?.data?.message || error.message);
          }
        }
      }
    };

    fetchData();

    return () => {
      controller.abort(); // Cancel the Axios request if the component unmounts
    };
  }, [authKey, id, navigate, serverUrl]);

  const updateOrders = (orders, user) => {
    let newOrders = [];
    let currentOrders = [];
    let completedOrders = [];

    // Sets to keep track of unique order IDs
    const newOrderIds = new Set();
    const currentOrderIds = new Set();
    const completedOrderIds = new Set();

    // Construct the user's full name for comparison
    const fullName = `${user.name} ${user.surname}`;

    // Check if the user is an administrator
    const isAdmin = user.role.some((role) => role.type === "Administrator");

    // If user is an administrator, they can see all orders
    // if (isAdmin) {
      // completedOrders = orders;
    // } else {
      // Non-admin users, filter orders based on their assignments
      orders.forEach((order) => {
        if (order.measurer === fullName) {
          // Measurer logic
          if (
            (order.measurer_accepted === "Accepted" &&
              !order.measuring_complete) ||
            order.order_status === "QA/QC Assigned"
          ) {
            if (!currentOrderIds.has(order.uniqueId)) {
              currentOrders.push(order);
              currentOrderIds.add(order.uniqueId);
            }
          }
          if (
            (order.measurer_accepted === "Accepted" &&
              order.measuring_complete) ||
            order.qa_qc_complete
          ) {
            if (!completedOrderIds.has(order.uniqueId)) {
              completedOrders.push(order);
              completedOrderIds.add(order.uniqueId);
            }
          }
        }

        if (order.cutter === fullName) {
          // Cutter logic
          if (
            order.cutter_accepted === "Not Accepted" &&
            order.order_status === "Cutting Assigned" &&
            !newOrderIds.has(order.uniqueId)
          ) {
            newOrders.push(order);
            newOrderIds.add(order.uniqueId);
          }
          if (
            order.cutter_accepted === "Accepted" &&
            !order.cutting_complete &&
            !currentOrderIds.has(order.uniqueId)
          ) {
            currentOrders.push(order);
            currentOrderIds.add(order.uniqueId);
          }
          if (
            order.cutter_accepted === "Accepted" &&
            order.cutting_complete &&
            !completedOrderIds.has(order.uniqueId)
          ) {
            completedOrders.push(order);
            completedOrderIds.add(order.uniqueId);
          }
        }

        if (order.tailor === fullName) {
          // Tailor logic
          if (
            order.tailor_accepted === "Not Accepted" &&
            order.order_status === "Tailoring Assigned" &&
            !newOrderIds.has(order.uniqueId)
          ) {
            newOrders.push(order);
            newOrderIds.add(order.uniqueId);
          }
          if (
            order.tailor_accepted === "Accepted" &&
            !order.tailoring_complete &&
            !currentOrderIds.has(order.uniqueId)
          ) {
            currentOrders.push(order);
            currentOrderIds.add(order.uniqueId);
          }
          if (
            order.tailor_accepted === "Accepted" &&
            order.tailoring_complete &&
            !completedOrderIds.has(order.uniqueId)
          ) {
            completedOrders.push(order);
            completedOrderIds.add(order.uniqueId);
          }
        }

        if (order.driver === fullName) {
          // Driver logic
          if (
            order.driver_accepted === "Not Accepted" &&
            !newOrderIds.has(order.uniqueId)
          ) {
            newOrders.push(order);
            newOrderIds.add(order.uniqueId);
          }
          if (
            order.driver_accepted === "Accepted" &&
            !order.driving_complete &&
            !currentOrderIds.has(order.uniqueId)
          ) {
            currentOrders.push(order);
            currentOrderIds.add(order.uniqueId);
          }
          if (
            order.driver_accepted === "Accepted" &&
            order.driving_complete &&
            !completedOrderIds.has(order.uniqueId)
          ) {
            completedOrders.push(order);
            completedOrderIds.add(order.uniqueId);
          }
        }
      });
    // }

    // Update state with the combined order lists
    setNewOrders(newOrders);
    setCurrentOrders(currentOrders);
    setCompletedOrders(completedOrders);
  };

  const renderImage = () => {
    if (user.picture && user.picture.data) {
      // Convert the binary data to base64
      const binary = new Uint8Array(user.picture.data).reduce(
        (acc, byte) => acc + String.fromCharCode(byte),
        ""
      );
      const base64String = btoa(binary);

      return (
        <img
          src={`data:${user.photoType};base64,${base64String}`}
          alt={user.name}
          loading="lazy"
          className="w-full md:w-2/4 h-[40vh] object-contain order-1 md:order-2"
        />
      );
    } else {
      return (
        <img
          src="https://user-images.githubusercontent.com/11250/39013954-f5091c3a-43e6-11e8-9cac-37cf8e8c8e4e.jpg"
          alt="Generic user"
          className="w-full md:w-2/4 h-[40vh] object-contain order-1 md:order-2"
        />
      );
    }
  };

  const newOrderResponse = async (choice, orderId) => {
    const currentOrder = allOrders.find((order) => order._id === orderId);
    const formData = {}; // Use a plain object instead

    if (
      hasRoles(["Cutter"], user) &&
      currentOrder.overall_state === "Cutting"
    ) {
      formData.cutter_accepted = choice;
    } else if (
      hasRoles(["Tailor"], user) &&
      currentOrder.overall_state === "Tailoring"
    ) {
      formData.tailor_accepted = choice;
    } else if (
      hasRoles(["Driver"], user) &&
      currentOrder.overall_state === "Driving"
    ) {
      formData.driver_accepted = choice;
    }

    try {
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${authKey}`,
            "Content-Type": "application/json", // Set content type to JSON
          },
        }
      );
      toast.success(
        `Order has been ${choice === "Accepted" ? "Accepted" : "Rejected"}`
      );
      setActiveModalOrderId({}); // Close the modal
      updateOrders(response.data.orders, response.data.user);
    } catch (error) {
      console.error(error);
      if (error.response?.status === 403 || error.response?.status === 404) {
        toast.warn("You are not authorized to access that page");
        navigate("/");
      } else {
        toast.error(error.response?.data?.message || error.message);
      }
    }
  };

  const updateCurrentOrder = async (choice, orderId) => {
    try {
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        {
          order_started: choice,
          finished_by: `${user.name} ${user.surname}`,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(
        `Process ${choice === "Started" ? "has started" : "is finished"}`
      );
      setActiveModalOrderId({}); // Close the modal
      updateOrders(response.data.orders, response.data.user);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 404) {
        toast.warn("You are not authorized to access that page");
        navigate("/");
      } else {
        toast.error(error.response?.data?.message || error.message);
      }
    }
  };

  const assignOrder = async (orderId, order_complete, assign_qa_qc, alter) => {
    const current_order = allOrders.find((order) => order._id === orderId);

    // Validating roles
    const roleValidation = () => {
      if (current_order.overall_state === "Measuring" && !cutter) {
        toast.info("Please select a Cutter from the list");
        return false;
      }
      if (current_order.overall_state === "Cutting" && !tailor) {
        toast.info("Please select a Tailor from the list");
        return false;
      }
      if (current_order.overall_state === "QA/QC" && !driver) {
        toast.info("Please select a Driver from the list");
        return false;
      }
      return true;
    };

    // Exit if role validation fails
    if (!roleValidation()) return;

    try {
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        {
          assigned_by: `${user.name} ${user.surname}`,
          measurer,
          cutter,
          tailor,
          driver,
          qa_qc: measurer,
          requires_alteration: alter,
          order_complete,
          assign_qa_qc,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );

      toast.success(response.data.message);

      // Reset form and states
      setMeasurer("");
      setCutter("");
      setTailor("");
      setDriver("");
      setActiveModalOrderId({}); // Close the modal
      updateOrders(response.data.orders, response.data.user);

      // Clear the userRef if it exists
      if (userRef.current) {
        userRef.current.value = ""; // Clear the select input
      }

      window.location.reload();
    } catch (error) {
      console.error(error);
      if (error.response?.status === 403 || error.response?.status === 404) {
        toast.warn("You are not authorized to access that page");
        navigate("/");
      } else {
        toast.error(error.response?.data?.message || error.message);
      }
    }
  };

  const findCustomer = (order_customer_id) => {
    const customer = customers.find(
      (customer) => customer.uniqueId === order_customer_id
    );
    return customer ? customer._id : null;
  };

  // Determine the user value based on the currently authenticated user
  const determineUserValue = (person, order) => {
    const fullName = `${user.name} ${user.surname}`;
    if (order.measurer === fullName && order.overall_state === "Measuring") {
      setCutter(person);
    } else if (order.cutter === fullName && order.overall_state === "Cutting") {
      setTailor(person);
    } else if (order.qa_qc === fullName && order.overall_state === "QA/QC") {
      setDriver(person);
    }
  };

  const updateUsers = (order) => {
    const fullName = `${user.name} ${user.surname}`;
    if (order.measurer === fullName && order.overall_state === "Measuring") {
      setFilteredUsers(
        allUsers.filter((user) => user.role.some((r) => r.type === "Cutter"))
      );
    } else if (order.cutter === fullName && order.overall_state === "Cutting") {
      setFilteredUsers(
        allUsers.filter((user) => user.role.some((r) => r.type === "Tailor"))
      );
    } else if (order.qa_qc === fullName && order.overall_state === "QA/QC") {
      setFilteredUsers(
        allUsers.filter((user) => user.role.some((r) => r.type === "Driver"))
      );
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem] font-arial">
        <h1 className="text-center text-[2rem] font-minimoBold">Dashboard</h1>
        {isLoading && (
          <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto text-center">
            <thead className="text-khamco-primary font-bold bg-[#ddd]">
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                <td className="text-center p-2">Loading...</td>
              </tr>
            </thead>
            <tbody>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
            </tbody>
          </table>
        )}
        {user && (
          <>
            {/* User Details Section */}
            <section
              className={`w-3/4 m-auto flex justify-between items-center my-[2rem] flex-wrap p-[1rem] gap-[2rem] md:gap-0`}
            >
              <div className="flex flex-col gap-[1rem] order-2 md:order-1">
                <span className="flex gap-[0.5rem]">
                  <strong>{t("Name")}:</strong> {`${user.name} ${user.surname}`}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>{t("Email")}:</strong> {user.email}
                </span>
                <span className="flex gap-[2rem]">
                  <strong>{t("Position")}:</strong>
                  <ul className="list-disc">
                    {user.role.map((r, index) => (
                      <li key={index}>{r.type}</li>
                    ))}
                  </ul>
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>{t("ID Number")}:</strong> {user._id}
                </span>
                <span>
                  <strong>{t("Date Joined")}:</strong>{" "}
                  {new Date(user.date).toLocaleDateString("en-us", {
                    month: "long",
                    day: "numeric",
                    year: "numeric",
                  })}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>{t("Preferred Language")}:</strong>{" "}
                  {user.language === "en" && `${t("English")}`}
                  {user.language === "ur" && `${t("Urdu")}`}
                  {user.language === "ar" && `${t("Arabic")}`}
                  {user.language === "hi" && `${t("Hindi")}`}
                </span>
              </div>
              {renderImage()}
            </section>
            {/* {userType.some((r) => r.type !== "Administrator") && ( */}
              <section>
                <>
                  {newOrders && newOrders.length > 0 ? (
                    <>
                      <h1 className="text-center text-[2rem] font-minimoBold">
                        {t("New Orders")}
                      </h1>
                      <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                        <thead className="text-khamco-primary font-bold bg-[#ddd]">
                          <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                            <th className="text-center p-2">
                              {t("Order No.")}
                            </th>
                            <th className="text-center p-2">
                              {t("Date Assigned")}
                            </th>
                            <th className="text-center p-2">
                              {t("Assigned By")}
                            </th>
                            <th className="text-center p-2">
                              {t("Order Details")}
                            </th>
                            <th className="text-center p-2">
                              {t("Accept/Reject")}
                            </th>
                          </tr>
                        </thead>
                        <tbody>
                          {newOrders.map((order, index) => (
                            <>
                              <tr
                                key={index}
                                className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                              >
                                <td className="text-center p-2">
                                  {order.uniqueId}
                                </td>
                                <td className="text-center p-2">
                                  {new Date(
                                    order.date_assigned
                                  ).toLocaleDateString("en-us", {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  })}
                                </td>
                                <td className="text-center p-2">
                                  {order.assigned_by || "--"}
                                </td>
                                <td className="text-center p-2">
                                  <Link
                                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                    to={`/orders/${order._id}`}
                                    target="_blank"
                                  >
                                    View
                                  </Link>
                                </td>
                                <td className="text-center p-2">
                                  <button
                                    className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                    onClick={() =>
                                      setActiveModalOrderId({
                                        orderID: order._id,
                                        type: "new order",
                                      })
                                    }
                                  >
                                    Action
                                  </button>
                                </td>
                              </tr>
                              <div
                                className={`fixed z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md border border-[black] bg-[white] ${
                                  activeModalOrderId.orderID === order._id &&
                                  activeModalOrderId.type === "new order"
                                    ? "block"
                                    : "hidden"
                                }`}
                              >
                                <button
                                  className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                                  onClick={() => setActiveModalOrderId({})}
                                >
                                  <i className="fas fa-times"></i>
                                </button>
                                <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                                  {t("Select an Action")}
                                </h4>
                                <h4>Order: {order.uniqueId}</h4>
                                <div className="flex my-[2rem] justify-center gap-[2rem] flex-wrap">
                                  <button
                                    className="p-[1rem] bg-[lawngreen] rounded-lg hover:opacity-70 flex-auto"
                                    onClick={() =>
                                      newOrderResponse("Accepted", order._id)
                                    }
                                  >
                                    {t("Accept")}
                                  </button>
                                  <button
                                    className="p-[1rem] bg-[crimson] text-[white] rounded-lg hover:opacity-70 flex-auto"
                                    onClick={() =>
                                      newOrderResponse(
                                        "Not Accepted",
                                        order._id
                                      )
                                    }
                                  >
                                    {t("Reject")}
                                  </button>
                                </div>
                              </div>
                            </>
                          ))}
                        </tbody>
                      </table>
                    </>
                  ) : (
                    <div className="border-2 border-[orangered] my-[2rem]">
                      <p className="text-center text-2xl my-[2rem] animate-pulse text-[orangered]">
                        {t("You have no new orders...")}
                      </p>
                    </div>
                  )}
                </>
              </section>
            {/* )} */}
            {/* {userType.some((r) => r.type !== "Administrator") && ( */}
              <section>
                <>
                  {currentOrders && currentOrders.length > 0 ? (
                    <>
                      <h1 className="text-center text-[2rem] font-minimoBold">
                        {t("Current Orders")}
                      </h1>
                      <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                        <thead className="text-khamco-primary font-bold bg-[#ddd]">
                          <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                            <th className="text-center p-2">
                              {t("Order No.")}
                            </th>
                            <th className="text-center p-2">
                              {t("Date Assigned")}
                            </th>
                            <th className="text-center p-2">
                              {t("Measurements")}
                            </th>
                            <th className="text-center p-2">
                              {t("Order Details")}
                            </th>
                            <th className="text-center p-2">
                              {t("Order Progress")}
                            </th>
                          </tr>
                        </thead>
                        <tbody>
                          {currentOrders.map((order, index) => (
                            <>
                              <tr
                                key={index}
                                className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                              >
                                <td className="text-center p-2">
                                  {order.uniqueId}
                                </td>
                                <td className="text-center p-2">
                                  {new Date(
                                    order.date_assigned
                                  ).toLocaleDateString("en-us", {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  })}
                                </td>
                                <td className="text-center p-2">
                                  <Link
                                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                    to={`/customers/${findCustomer(
                                      order.customer_id
                                    )}/measurements`}
                                    target="_blank"
                                  >
                                    {t("View")}
                                  </Link>
                                </td>
                                <td className="text-center p-2 relative">
                                  <Link
                                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                    to={`/orders/${order._id}`}
                                    target="_blank"
                                  >
                                    {t("View")}
                                  </Link>
                                  {order.qa_qc && !order.qa_qc_complete && (
                                    <span className="absolute text-sm top-0 right-0 p-1 bg-[gold] rounded-lg font-bold animate-pulse">
                                      QA/QC
                                    </span>
                                  )}
                                  {order.order_status === "Requires Alteration" && (
                                    <span className="absolute text-sm top-0 right-0 p-1 bg-[red] rounded-lg font-bold animate-pulse">
                                      Alteration
                                    </span>
                                  )}
                                </td>
                                <td className="text-center p-2">
                                  <button
                                    className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                    onClick={() =>
                                      setActiveModalOrderId({
                                        orderID: order._id,
                                        type: "current order",
                                      })
                                    }
                                  >
                                    Action
                                  </button>
                                </td>
                              </tr>
                              <div
                                className={`fixed z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md border border-[black] bg-[white] ${
                                  activeModalOrderId.orderID === order._id &&
                                  activeModalOrderId.type === "current order"
                                    ? "block"
                                    : "hidden"
                                }`}
                              >
                                <button
                                  className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                                  onClick={() => setActiveModalOrderId({})}
                                >
                                  <i className="fas fa-times"></i>
                                </button>
                                <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                                  {t("Select an Action")}
                                </h4>
                                <h4>Order: {order.uniqueId}</h4>
                                <div className="flex my-[2rem] justify-center gap-[2rem] flex-wrap">
                                  {order.order_started === "Not Started" && (
                                    <button
                                      className="p-[1rem] bg-[lawngreen] rounded-lg hover:opacity-70 flex-auto"
                                      onClick={() =>
                                        updateCurrentOrder("Started", order._id)
                                      }
                                    >
                                      {t("Start Process")}
                                    </button>
                                  )}
                                  {order.order_started === "Started" && (
                                    <button
                                      className="p-[1rem] bg-[crimson] text-[white] rounded-lg hover:opacity-70 flex-auto"
                                      onClick={() =>
                                        updateCurrentOrder(
                                          "Finished",
                                          order._id
                                        )
                                      }
                                    >
                                      {t("Finish Process")}
                                    </button>
                                  )}
                                </div>
                              </div>
                            </>
                          ))}
                        </tbody>
                      </table>
                    </>
                  ) : (
                    <div className="border-2 border-[orangered] my-[2rem]">
                      <p className="text-center text-2xl my-[2rem] animate-pulse text-[orangered]">
                        {t("You have not accepted any orders...")}
                      </p>
                    </div>
                  )}
                </>
              </section>
            {/* )} */}
            <section>
              {completedOrders && completedOrders.length > 0 ? (
                <>
                  <h1 className="text-center text-[2rem] font-minimoBold">
                    {hasRoles(["Administrator"], user)
                      ? `${t("Order List")}`
                      : `${t("Completed Orders")}`}
                  </h1>
                  <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                    <thead className="text-khamco-primary font-bold bg-[#ddd]">
                      <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                        <th className="text-center p-2">{t("Order No.")}</th>
                        <th className="text-center p-2">
                          {t("Date Completed")}
                        </th>
                        <th className="text-center p-2">
                          {t("Order Details")}
                        </th>
                        <th className="text-center p-2">{t("Order Status")}</th>
                        <th className="text-center p-2">{t("Assign User")}</th>
                      </tr>
                    </thead>
                    <tbody>
                      {completedOrders.map((order, index) => (
                        <>
                          <tr
                            key={index}
                            className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                          >
                            <td className="text-center p-2">
                              {order.uniqueId}
                            </td>
                            <td className="text-center p-2">
                              {order.date_completed
                                ? new Date(
                                    order.date_completed
                                  ).toLocaleDateString("en-us", {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  })
                                : "--"}
                            </td>
                            <td className="text-center p-2">
                              <Link
                                className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                to={`/orders/${order._id}`}
                                target="_blank"
                              >
                                View
                              </Link>
                            </td>
                            <td className="text-center">
                              <span
                                className={`p-3 rounded-2xl ${applyAnimation(
                                  order.order_status
                                )}`}
                              >
                                {order.order_status}
                              </span>
                            </td>
                            <td className="text-center p-2">
                              <button
                                className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                onClick={() => {
                                  if (
                                    order.assigned_to ===
                                    `${user.name} ${user.surname}`
                                  ) {
                                    setActiveModalOrderId({
                                      orderID: order._id,
                                      type: "completed-order",
                                    });
                                    updateUsers(order);
                                  } else {
                                    toast.warn("Action not allowed");
                                  }
                                }}
                              >
                                {hasRoles(["Driver"], user) &&
                                order.overall_state === "Driving"
                                  ? "Update"
                                  : "Assign"}
                              </button>
                            </td>
                          </tr>
                          <div
                            className={`fixed z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md border border-[black] max-h-[60vh] overflow-y-scroll bg-[white] ${
                              activeModalOrderId.orderID === order._id &&
                              activeModalOrderId.type === "completed-order"
                                ? "block"
                                : "hidden"
                            }`}
                          >
                            <button
                              className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                              onClick={() => setActiveModalOrderId({})}
                            >
                              <i className="fas fa-times"></i>
                            </button>
                            <h4>Order: {order.uniqueId}</h4>
                            {hasRoles(
                              ["Measurer", "Cutter", "Tailor"],
                              user
                            ) && (
                              <h4 className="text-center font-bold text-[1.5rem]">
                                {`${user.name} ${user.surname}` ===
                                  order.tailor &&
                                order.overall_state === "Tailoring"
                                  ? `${t("Send Order for QA/QC")}`
                                  : `${
                                      hasRoles(["Measurer"], user) &&
                                      order.overall_state === "Measuring"
                                        ? "Assign Cutter"
                                        : hasRoles(["Cutter"], user) &&
                                          order.overall_state === "Cutting"
                                        ? "Assign Tailor"
                                        : hasRoles(["Measurer"], user) &&
                                          order.overall_state === "QA/QC"
                                        ? "Assign Driver"
                                        : ""
                                    }`}
                              </h4>
                            )}
                            {hasRoles(["Measurer", "Cutter"], user) &&
                              order.overall_state !== "Tailoring" &&
                              order.overall_state !== "Driving" && (
                                <div className="w-full flex flex-col mx-auto my-4 relative">
                                  <select
                                    className="p-2 border-2 border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                                    onChange={(e) => {
                                      determineUserValue(e.target.value, order);
                                    }}
                                    ref={userRef}
                                  >
                                    <option value="">
                                      -- Select from the list --
                                    </option>
                                    {filteredUsers &&
                                      filteredUsers.map((user, index) => (
                                        <option
                                          key={index}
                                          value={`${user.name} ${user.surname}`}
                                        >
                                          {`${user.name} ${user.surname}`} (
                                          {user.role.map((r, i) => (
                                            <span key={i}>
                                              {r.type}
                                              {i < user.role.length - 1 && ", "}
                                            </span>
                                          ))}
                                          )
                                        </option>
                                      ))}
                                  </select>
                                </div>
                              )}
                            {userType.some((r) => r.type === "Driver") &&
                              order.driver && (
                                <div className="w-full flex flex-col gap-[0.5rem] items-center mx-auto my-[4rem] relative">
                                  <button
                                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[orangered] text-white text-lg rounded-md cursor-pointer hover:opacity-90 w-full"
                                    onClick={() =>
                                      assignOrder(order._id, false, false, true)
                                    }
                                    onMouseOver={() =>
                                      setRequiresAlteration(true)
                                    }
                                    onMouseOut={() =>
                                      setRequiresAlteration(false)
                                    }
                                  >
                                    {t("Requires Alteration")}
                                  </button>
                                  <p
                                    className={`text-sm ${
                                      requiresAlteration ? "block" : "hidden"
                                    }`}
                                  >
                                    {t(
                                      "This will send the order back for measuring"
                                    )}
                                  </p>
                                </div>
                              )}
                            {hasRoles(["Measurer", "Cutter", "Tailor"], user) &&
                              order.overall_state !== "Driving" && (
                                <div className="w-full flex flex-col mx-auto my-4 relative">
                                  <button
                                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[black] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                                    onClick={() => {
                                      if (
                                        hasRoles(["Tailor"], user) &&
                                        order.overall_state === "Tailoring"
                                      ) {
                                        assignOrder(
                                          order._id,
                                          false,
                                          true,
                                          false
                                        );
                                      } else {
                                        assignOrder(
                                          order._id,
                                          false,
                                          false,
                                          false
                                        );
                                      }
                                    }}
                                  >
                                    {hasRoles(["Tailor"], user) &&
                                    order.overall_state === "Tailoring"
                                      ? `${t("Send")}`
                                      : `${t("Assign")}`}
                                    {hasRoles(
                                      ["Measurer", "Cutter", "Tailor"],
                                      user
                                    ) &&
                                      order.overall_state !== "Driving" && (
                                        <>
                                          {" "}
                                          <i className="fas fa-user-plus"></i>
                                        </>
                                      )}
                                  </button>
                                </div>
                              )}
                            {hasRoles(["Driver"], user) &&
                              order.overall_state === "Driving" && (
                                <div className="w-full flex gap-[0.5rem] flex-col mx-auto my-[2rem] relative">
                                  <button
                                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[limegreen] text-white text-lg rounded-md cursor-pointer hover:opacity-90 w-full"
                                    onClick={() =>
                                      assignOrder(order._id, true, false, false)
                                    }
                                    onMouseOver={() => setOrderComplete(true)}
                                    onMouseOut={() => setOrderComplete(false)}
                                  >
                                    {t("Submit Order")}
                                  </button>
                                  <p
                                    className={`text-sm ${
                                      orderComplete ? "block" : "hidden"
                                    }`}
                                  >
                                    {t(
                                      "This will change the order to 'Delivered' status"
                                    )}
                                  </p>
                                </div>
                              )}
                          </div>
                        </>
                      ))}
                    </tbody>
                  </table>
                </>
              ) : (
                <div className="border-2 border-[orangered] my-[2rem]">
                  <p className="text-center text-2xl my-[2rem] animate-pulse text-[orangered]">
                    {hasRoles(["Administrator"], user)
                      ? `${t("No orders here...")}`
                      : `${t("You have no completed orders...")}`}
                  </p>
                </div>
              )}
            </section>
          </>
        )}
      </main>
      <Footer />
    </>
  );
};

export default UserProfile;
