import "./App.css";
import { Routes, Route, Navigate } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import NotFound from "./pages/404";
import Customers from "./pages/customers/Customers";
import CreateCustomer from "./pages/customers/CreateCustomer";
import CustomerDetails from "./pages/customers/CustomerDetails";
import EditCustomer from "./pages/customers/EditCustomer";
import Products from "./pages/products/Products";
import NewProduct from "./pages/products/NewProduct";
import ProductView from "./pages/products/ProductView";
import EditProduct from "./pages/products/EditProduct";
import ServicesView from "./pages/services/ServicesView";
import Services from "./pages/services/Services";
import NewService from "./pages/services/NewService";
import EditService from "./pages/services/EditService";
import Invoices from "./pages/invoices/Invoices";
import ShowInvoice from "./pages/invoices/ShowInvoice";
import NewInvoice from "./pages/invoices/NewInvoice";
import Orders from "./pages/orders/Orders";
import NewOrder from "./pages/orders/NewOrder";
import OrderDetails from "./pages/orders/OrderDetails";
import AdminDashboard from "./pages/admin/AdminDashboard";
import { Suspense, useEffect } from "react";
import CustomersData from "./pages/admin/CustomersData";
import AdminRevenue from "./pages/admin/AdminRevenue";
import AdminFabrics from "./pages/admin/AdminFabrics";
import AdminInvoiceData from "./pages/admin/AdminInvoiceData";
import AdminServiceTimes from "./pages/admin/AdminServiceTimes";
import AdminTailorDetails from "./pages/admin/AdminTailorDetails";
import AdminTailors from "./pages/admin/AdminTailors";
import AdminProducts from "./pages/admin/products/AdminProducts";
import AdminProductsView from "./pages/admin/products/AdminProductsView";
import ForgotPassword from "./pages/users/ForgotPassword";
import ResetPassword from "./pages/users/ResetPassword";
import RegisterUser from "./pages/users/RegisterUser";
import CreateMaster from "./pages/users/CreateMaster";
import EditUser from "./pages/users/EditUser";
import { useStoreActions, useStoreState } from "easy-peasy";
import DependentsPrompt from "./pages/customers/dependents/DependentsPrompt";
import CreateDependent from "./pages/customers/dependents/CreateDependent";
import { jwtDecode } from "jwt-decode";
import { appRoutes } from "./routes";

function App() {
  const authKey = localStorage.getItem("jwt");
  const user = useStoreState((state) => state.user);
  const setUser = useStoreActions((state) => state.setUser);

  useEffect(() => {
    document.title = "Khamco Shop";

    const getUser = () => {
      if (authKey) {
        try {
          const decoded = jwtDecode(authKey);
          setUser({
            id: decoded.id,
            email: decoded.email,
            role: decoded.role,
            language: decoded.language,
          });
        } catch (error) {
          console.error(error);
        }
      }
    };
    getUser();
  }, [authKey, setUser, user]);

  return (
    <>
    <Suspense
      fallback={
        <div>
          <div className="fas fa-rotate animate-spin"></div>
        </div>
      }
    >
      <Routes>
        {appRoutes.map((route) => (
          <Route
            key={route.path}
            exact
            path={route.path}
            element={
              authKey ? <route.component /> : <Navigate to="/users/login" />
            }
          />
        ))}
        {/* Misc Routes */}
        <Route exact path="/unauthorized" element={<NotFound />} />
        <Route exact path="*" element={<NotFound />} />
      </Routes>
    </Suspense>
    </>
  );
}

export default App;
