import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";

const AdminTailors = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/tailors"
    : "https://khamco-shop-backend.onrender.com/admin/tailors";
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = "Admin Tailor Data - Khamco Shop";
    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setUsers(response.data.users);
          setDisplay(true);
          setIsLoading(false);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.message);
          }
          return;
        });
    };

    return () => {
      fetchData();
    };
  }, []);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section
          id="users-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          <h1 className="title">All Tailors</h1>
          <div className="utilities">
            <Link to="/admin/register-user">
              Add New Tailor <i className="fas fa-user-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          {isLoading &&
            new Array(3).fill(
              <div className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] animate-pulse"></div>
            )}
          {display && (
            <>
              {users &&
                users.map((user, index) => (
                  <div
                    className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem]"
                    key={index}
                  >
                    <p>
                      <strong>Name</strong>: {user.name}
                    </p>
                    <p>
                      <strong>Role</strong>: {user.role}
                    </p>
                    <p>
                      <strong>Email</strong>:{" "}
                      <a href={`mailto:${user.email}`}>{user.email}</a>
                    </p>
                    <Link to={`/admin/users/${user.id}`} className="edit-btn">
                      View
                    </Link>
                  </div>
                ))}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailors;
