import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Services = () => {
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalServices, setTotalServices] = useState(0);
  const location = useLocation();
  const navigate = useNavigate();

  const fetchServices = async (queryParams = "") => {
    setLoading(true);
    try {
      const response = await axios.get(`${apiUrl}/services${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      const sortedServices = response.data.services.sort(
        (a, b) => new Date(a.date) - new Date(b.date)
      );
      setServices(sortedServices);
      setTotalPages(response.data.totalPages);
      setTotalServices(response.data.totalServices);
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    document.title = "Services - Khamco Shop";
    const searchParams = new URLSearchParams(location.search);
    searchParams.set("page", currentPage); // Add page param to the query
    fetchServices(`?${searchParams.toString()}`);
  }, [currentPage, location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  // Pagination controls
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prev) => prev + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="py-0 px-[2rem] justify-center items-center grid-cols-1 gap-[1rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/services/new"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              New Service <i className="fas fa-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="number"
                step={0.01}
                placeholder="Search service price"
                name="price"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="date"
                name="date"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          {loading && (
            <div className="overflow-auto">
            <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem]">
              <thead>
                <tr className="bg-[#aaa] text-[blue] text-center h-12 border-[0.2rem] border-khamco-secondary border-t-0">
                  <td>Loading...</td>
                </tr>
              </thead>
              <tbody>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12 border-[0.2rem] border-khamco-secondary border-t-0">
                  <td>k</td>
                </tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12 border-[0.2rem] border-khamco-secondary border-t-0">
                  <td>k</td>
                </tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12 border-[0.2rem] border-khamco-secondary border-t-0">
                  <td>k</td>
                </tr>
              </tbody>
            </table>
            </div>
          )}
          <h1 className="text-center text-[2rem] font-minimoBold">
            Services ({totalServices})
          </h1>
          <p className="my-[2rem]">(Showing {services.length} results)</p>
          {services.length > 0 ? (
            <>
            <div className="overflow-auto">
              <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem]">
                <thead className="text-khamco-primary font-bold">
                  <tr className="border-[0.2rem] border-khamco-secondary border-t-0 relative">
                    <td className="bg-[#ddd] text-center">Service</td>
                    <td className="bg-[#ddd] text-center">Price</td>
                    <td className="bg-[#ddd] text-center">Date Created</td>
                    <td className="bg-[#ddd] text-center"></td>
                  </tr>
                </thead>
                <tbody>
                  {services.map((service, index) => (
                    <tr
                      key={index}
                      className="border-[0.2rem] border-khamco-secondary border-t-0 relative"
                    >
                      <td className="text-center p-[0.5rem]">
                        {service.service}
                      </td>
                      <td className="text-center p-[0.5rem]">
                        {new Intl.NumberFormat("kw-KW", {
                        style: "currency",
                        currency: "KWD",
                        minimumFractionDigits: 3,
                        maximumFractionDigits: 3,
                      }).format(service.price)}
                      </td>
                      <td className="text-center p-[0.5rem]">
                        {new Date(service.date).toLocaleDateString("en-us", {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        })}
                      </td>
                      <td className="text-center p-[0.5rem]">
                        <Link
                          to={`/services/${service._id}`}
                          className="text-[blue] no-underline hover:underline cursor-pointer"
                        >
                          View
                        </Link>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
              <div className="flex gap-[0.5rem] items-center justify-center">
                <button
                  disabled={currentPage === 1}
                  onClick={handlePreviousPage}
                  className={`${
                    currentPage === 1
                      ? "bg-[#999] opacity-90"
                      : "bg-khamco-primary"
                  }  text-[white] p-[0.5rem] rounded-md hover:opacity-80`}
                >
                  Previous
                </button>
                <span className="text-[black] p-[0.5rem] font-bold">
                  {currentPage} of {totalPages}
                </span>
                <button
                  disabled={currentPage === totalPages}
                  onClick={handleNextPage}
                  className={`${
                    currentPage === totalPages
                      ? "bg-[#999] opacity-90"
                      : "bg-khamco-primary"
                  }  text-[white] p-[0.5rem] rounded-md hover:opacity-80`}
                >
                  Next
                </button>
              </div>
            </>
          ) : (
            <p className="text-center">No services available.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Services;
