import { Link, useNavigate, useParams } from "react-router-dom";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { applyAnimation } from "../../utilities";

const UserProfile = () => {
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [filteredUsers, setFilteredUsers] = useState(null);
  const [customers, setCustomers] = useState([]);
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [activeModalOrderId, setActiveModalOrderId] = useState(null);
  const [newOrders, setNewOrders] = useState(null);
  const [currentOrders, setCurrentOrders] = useState(null);
  const [completedOrders, setCompletedOrders] = useState(null);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [tailor, setTailor] = useState("");
  const [driver, setDriver] = useState("");
  const [userType, setUserType] = useState("");

  useEffect(() => {
    const controller = new AbortController();
    const signal = controller.signal;

    const fetchData = async () => {
      try {
        const response = await axios.get(
          `${serverUrl}/admin/users/profile/${id}`,
          {
            headers: { Authorization: `Bearer ${authKey}` },
            signal,
          }
        );
        if (!signal.aborted) {
          setIsLoading(false);
          setUser(response.data.user);
          setUserType(response.data.user.role);
          updateUsers(response.data.user, response.data.all_users);
          setCustomers(response.data.customers);
          updateOrders(response.data.user_orders, response.data.user);
          document.title = `Tailor Dashboard - ${response.data.user.name}`;
        }
      } catch (error) {
        if (!signal.aborted) {
          setIsLoading(false);
          if (
            error.response?.status === 403 ||
            error.response?.status === 404
          ) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response?.data?.message || error.message);
          }
        }
      }
    };

    fetchData();

    return () => {
      controller.abort(); // Cancel the Axios request if the component unmounts
    };
  }, [authKey, id, navigate, serverUrl]);

  const updateUsers = (user, users) => {
    if (user.role.some((r) => r.type === "Administrator")) {
      // If measurer update users list to cutters
      setFilteredUsers(
        users.filter((user) => user.role.some((r) => r.type === "Measurer"))
      );
    } else if (user.role.some((r) => r.type === "Measurer")) {
      // If measurer update users list to cutters
      setFilteredUsers(
        users.filter((user) => user.role.some((r) => r.type === "Cutter"))
      );
    } else if (user.role.some((r) => r.type === "Cutter")) {
      // If cutter update users list to tailors
      setFilteredUsers(
        users.filter((user) => user.role.some((r) => r.type === "Tailor"))
      );
    } else if (user.role.some((r) => r.type === "Tailor")) {
      // If tailor update users list to measurers
      setFilteredUsers(
        users.filter((user) => user.role.some((r) => r.type === "Measurer"))
      );
    }
  };

  const updateOrders = (orders, user) => {
    if (user.role.some((r) => r.type === "Administrator")) {
      // Update administrator details
      setCompletedOrders(orders);
    } else if (user.role.some((r) => r.type === "Measurer")) {
      // Update measurer details
      setNewOrders(
        orders.filter(
          (order) =>
            (order.measurer_accepted === "Not Accepted" &&
              order.order_status === "Measurer Assigned") ||
            (order.qa_qc_accepted === "Not Accepted" &&
              order.order_status === "QA/QC Assigned")
        )
      );
      setCurrentOrders(
        orders.filter(
          (order) =>
            order.measurer_accepted === "Accepted" && !order.measuring_complete
        )
      );
      setCompletedOrders(
        orders.filter(
          (order) =>
            order.measurer_accepted === "Accepted" && order.measuring_complete
        )
      );
    } else if (user.role.some((r) => r.type === "Cutter")) {
      // Update cutter details
      setNewOrders(
        orders.filter(
          (order) =>
            order.cutter_accepted === "Not Accepted" &&
            order.order_status === "Cutting Assigned"
        )
      );
      setCurrentOrders(
        orders.filter(
          (order) =>
            order.cutter_accepted === "Accepted" && !order.cutting_complete
        )
      );
      setCompletedOrders(
        orders.filter(
          (order) =>
            order.cutter_accepted === "Accepted" && order.cutting_complete
        )
      );
    } else if (user.role.some((r) => r.type === "Tailor")) {
      // Update tailor details
      setNewOrders(
        orders.filter(
          (order) =>
            order.tailor_accepted === "Not Accepted" &&
            order.order_status === "Tailoring Assigned"
        )
      );
      setCurrentOrders(
        orders.filter(
          (order) =>
            order.tailor_accepted === "Accepted" && !order.tailoring_complete
        )
      );
      setCompletedOrders(
        orders.filter(
          (order) =>
            order.tailor_accepted === "Accepted" && order.tailoring_complete
        )
      );
    } else if (user.role.some((r) => r.type === "Driver")) {
      // Update driver details
      setNewOrders(
        orders.filter((order) => order.driver_accepted === "Not Accepted")
      );
      setCurrentOrders(
        orders.filter(
          (order) =>
            order.driver_accepted === "Accepted" && !order.driving_completed
        )
      );
      setCompletedOrders(
        orders.filter(
          (order) =>
            order.driver_accepted === "Accepted" && order.driving_completed
        )
      );
    }
  };

  const renderImage = () => {
    if (user.picture && user.picture.data) {
      // Convert the binary data to base64
      const binary = new Uint8Array(user.picture.data).reduce(
        (acc, byte) => acc + String.fromCharCode(byte),
        ""
      );
      const base64String = btoa(binary);

      return (
        <img
          src={`data:${user.photoType};base64,${base64String}`}
          alt={user.name}
          loading="lazy"
          className="w-full md:w-2/4 h-[40vh] object-contain order-1 md:order-2"
        />
      );
    } else {
      return (
        <img
          src="https://user-images.githubusercontent.com/11250/39013954-f5091c3a-43e6-11e8-9cac-37cf8e8c8e4e.jpg"
          alt="Generic user"
          className="w-full md:w-2/4 h-[40vh] object-contain order-1 md:order-2"
        />
      );
    }
  };

  const newOrderResponse = async (choice, orderId) => {
    try {
      const formData = new FormData();

      if (user.role.some((r) => r.type === "Measurer")) {
        formData.append("measurer_accepted", choice);
      } else if (user.role.some((r) => r.type === "Cutter")) {
        formData.append("cutter_accepted", choice);
      } else if (user.role.some((r) => r.type === "Tailor")) {
        formData.append("tailor_accepted", choice);
      } else if (user.role.some((r) => r.type === "Driver")) {
        formData.append("driver_accepted", choice);
      }
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        formData,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(
        `Order has been ${choice === "Accepted" ? "Accepted" : "Rejected"}`
      );
      setActiveModalOrderId(null); // Close the modal
      updateOrders(response.data.orders, response.data.user);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 404) {
        toast.warn("You are not authorized to access that page");
        navigate("/");
      } else {
        toast.error(error.response?.data?.message || error.message);
      }
    }
  };

  const updateCurrentOrder = async (choice, orderId) => {
    try {
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        {
          order_started: choice,
          finished_by: `${user.name} ${user.surname}`,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(
        `Order ${choice === "Started" ? "has started" : "is finished"}`
      );
      setActiveModalOrderId(null); // Close the modal
      updateOrders(response.data.orders, response.data.user);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 404) {
        toast.warn("You are not authorized to access that page");
        navigate("/");
      } else {
        toast.error(error.response?.data?.message || error.message);
      }
    }
  };

  const assignOrder = async (orderId) => {
    try {
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        {
          assigned_by: `${user.name} ${user.surname}`,
          measurer: measurer,
          cutter: cutter,
          tailor: tailor,
          driver: driver,
          qa_qc: measurer,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      setMeasurer("");
      setCutter("");
      setTailor("");
      setDriver("");
      setActiveModalOrderId(null); // Close the modal
      updateOrders(response.data.orders, response.data.user);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 404) {
        toast.warn("You are not authorized to access that page");
        navigate("/");
      } else {
        toast.error(error.response?.data?.message || error.message);
      }
    }
  };

  const findCustomer = (order_customer_id) => {
    const customer = customers.find(
      (customer) => customer.uniqueId === order_customer_id
    );
    return customer ? customer._id : null;
  };

  // Determine the user value based on the currently authenticated user
  const determineUserValue = (e) => {
    if (userType.some((r) => r.type === "Administrator")) {
      setMeasurer(e.target.value);
    } else if (userType.some((r) => r.type === "Measurer")) {
      setCutter(e.target.value);
    } else if (userType.some((r) => r.type === "Cutter")) {
      setTailor(e.target.value);
    } else if (userType.some((r) => r.type === "Tailor")) {
      setMeasurer(e.target.value);
    } else if (userType.some((r) => r.type === "Driver")) {
      setMeasurer(e.target.value);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem] font-arial">
        <h1 className="text-center text-[2rem] font-minimoBold">Dashboard</h1>
        {isLoading && (
          <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto text-center">
            <thead className="text-khamco-primary font-bold bg-[#ddd]">
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                <td className="text-center p-2">Loading...</td>
              </tr>
            </thead>
            <tbody>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
            </tbody>
          </table>
        )}
        {user && (
          <>
            <section
              className={`w-3/4 m-auto flex justify-between items-center my-[2rem] flex-wrap p-[1rem] gap-[2rem] md:gap-0`}
            >
              <div className="flex flex-col gap-[1rem] order-2 md:order-1">
                <span className="flex gap-[0.5rem]">
                  <strong>Name:</strong> {`${user.name} ${user.surname}`}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>Email:</strong> {user.email}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>Position:</strong>
                  <ul>
                    {user.role.map((r, index) => (
                      <li key={index}>{r.type}</li>
                    ))}
                  </ul>
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>ID No:</strong> {user._id}
                </span>
                <span>
                  <strong>Date Joined:</strong>{" "}
                  {new Date(user.date).toLocaleDateString("en-us", {
                    month: "long",
                    day: "numeric",
                    year: "numeric",
                  })}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>Preferred Language:</strong>{" "}
                  {user.language === "en" && "English"}
                  {user.language === "ur" && "Urdu"}
                  {user.language === "ar" && "Arabic"}
                  {user.language === "hi" && "Hindi"}
                </span>
              </div>
              {renderImage()}
            </section>
            {userType.some((r) => r.type !== "Administrator") && (
              <section>
                <>
                  {newOrders && newOrders.length > 0 ? (
                    <>
                      <h1 className="text-center text-[2rem] font-minimoBold">
                        New Orders
                      </h1>
                      <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                        <thead className="text-khamco-primary font-bold bg-[#ddd]">
                          <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                            <th className="text-center p-2">Order No.</th>
                            <th className="text-center p-2">Date Assigned</th>
                            <th className="text-center p-2">Assigned By</th>
                            <th className="text-center p-2">Order Details</th>
                            <th className="text-center p-2">Accept/Reject</th>
                          </tr>
                        </thead>
                        <tbody>
                          {newOrders.map((order, index) => (
                            <>
                              <tr
                                key={index}
                                className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                              >
                                <td className="text-center p-2">
                                  {order.uniqueId}
                                </td>
                                <td className="text-center p-2">
                                  {new Date(
                                    order.date_assigned
                                  ).toLocaleDateString("en-us", {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  })}
                                </td>
                                <td className="text-center p-2">
                                  {order.assigned_by || "--"}
                                </td>
                                <td className="text-center p-2">
                                  <Link
                                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                    to={`/orders/${order._id}`}
                                    target="_blank"
                                  >
                                    View
                                  </Link>
                                </td>
                                <td className="text-center p-2">
                                  <button
                                    className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                    onClick={() =>
                                      setActiveModalOrderId(order._id)
                                    }
                                  >
                                    Action
                                  </button>
                                </td>
                              </tr>
                              <div
                                className={`fixed z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md border border-[black] bg-[white] ${
                                  activeModalOrderId === order._id
                                    ? "block"
                                    : "hidden"
                                }`}
                              >
                                <button
                                  className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                                  onClick={() => setActiveModalOrderId(null)}
                                >
                                  <i className="fas fa-times"></i>
                                </button>
                                <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                                  Select an Action
                                </h4>
                                <h4>Order: {order.uniqueId}</h4>
                                <div className="flex my-[2rem] justify-center gap-[2rem] flex-wrap">
                                  <button
                                    className="p-[1rem] bg-[lawngreen] rounded-lg hover:opacity-70 flex-auto"
                                    onClick={() =>
                                      newOrderResponse("Accepted", order._id)
                                    }
                                  >
                                    Accept
                                  </button>
                                  <button
                                    className="p-[1rem] bg-[crimson] text-[white] rounded-lg hover:opacity-70 flex-auto"
                                    onClick={() =>
                                      newOrderResponse(
                                        "Not Accepted",
                                        order._id
                                      )
                                    }
                                  >
                                    Reject
                                  </button>
                                </div>
                              </div>
                            </>
                          ))}
                        </tbody>
                      </table>
                    </>
                  ) : (
                    <div className="border-2 border-[orangered] my-[2rem]">
                      <p className="text-center text-2xl my-[2rem] animate-pulse text-[orangered]">
                        You have no new orders...
                      </p>
                    </div>
                  )}
                </>
              </section>
            )}
            {userType.some((r) => r.type !== "Administrator") && (
              <section>
                <>
                  {currentOrders && currentOrders.length > 0 ? (
                    <>
                      <h1 className="text-center text-[2rem] font-minimoBold">
                        Current Orders
                      </h1>
                      <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                        <thead className="text-khamco-primary font-bold bg-[#ddd]">
                          <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                            <th className="text-center p-2">Order No.</th>
                            <th className="text-center p-2">Date Assigned</th>
                            <th className="text-center p-2">Measurements</th>
                            <th className="text-center p-2">Order Details</th>
                            <th className="text-center p-2">Order Progress</th>
                          </tr>
                        </thead>
                        <tbody>
                          {currentOrders.map((order, index) => (
                            <>
                              <tr
                                key={index}
                                className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                              >
                                <td className="text-center p-2">
                                  {order.uniqueId}
                                </td>
                                <td className="text-center p-2">
                                  {new Date(
                                    order.date_assigned
                                  ).toLocaleDateString("en-us", {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  })}
                                </td>
                                <td className="text-center p-2">
                                  <Link
                                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                    to={`/customers/${findCustomer(
                                      order.customer_id
                                    )}/measurements`}
                                    target="_blank"
                                  >
                                    View
                                  </Link>
                                </td>
                                <td className="text-center p-2">
                                  <Link
                                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                    to={`/orders/${order._id}`}
                                    target="_blank"
                                  >
                                    View
                                  </Link>
                                </td>
                                <td className="text-center p-2">
                                  <button
                                    className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                    onClick={() =>
                                      setActiveModalOrderId(order._id)
                                    }
                                  >
                                    Action
                                  </button>
                                </td>
                              </tr>
                              <div
                                className={`fixed z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md border border-[black] bg-[white] ${
                                  activeModalOrderId === order._id
                                    ? "block"
                                    : "hidden"
                                }`}
                              >
                                <button
                                  className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                                  onClick={() => setActiveModalOrderId(null)}
                                >
                                  <i className="fas fa-times"></i>
                                </button>
                                <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                                  Select an Action
                                </h4>
                                <h4>Order: {order.uniqueId}</h4>
                                <div className="flex my-[2rem] justify-center gap-[2rem] flex-wrap">
                                  {order.order_started === "Not Started" && (
                                    <button
                                      className="p-[1rem] bg-[lawngreen] rounded-lg hover:opacity-70 flex-auto"
                                      onClick={() =>
                                        updateCurrentOrder("Started", order._id)
                                      }
                                    >
                                      Start Process
                                    </button>
                                  )}
                                  {order.order_started === "Started" && (
                                    <button
                                      className="p-[1rem] bg-[crimson] text-[white] rounded-lg hover:opacity-70 flex-auto"
                                      onClick={() =>
                                        updateCurrentOrder(
                                          "Finished",
                                          order._id
                                        )
                                      }
                                    >
                                      Finish Process
                                    </button>
                                  )}
                                </div>
                              </div>
                            </>
                          ))}
                        </tbody>
                      </table>
                    </>
                  ) : (
                    <div className="border-2 border-[orangered] my-[2rem]">
                      <p className="text-center text-2xl my-[2rem] animate-pulse text-[orangered]">
                        You have not accepted any orders...
                      </p>
                    </div>
                  )}
                </>
              </section>
            )}
            <section>
              {completedOrders && completedOrders.length > 0 ? (
                <>
                  <h1 className="text-center text-[2rem] font-minimoBold">
                    {userType.some((r) => r.type === "Administrator")
                      ? "Order List"
                      : "Completed Orders"}
                  </h1>
                  <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                    <thead className="text-khamco-primary font-bold bg-[#ddd]">
                      <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                        <th className="text-center p-2">Order No.</th>
                        <th className="text-center p-2">Date Completed</th>
                        <th className="text-center p-2">Order Details</th>
                        <th className="text-center p-2">Order Status</th>
                        <th className="text-center p-2">Assign User</th>
                      </tr>
                    </thead>
                    <tbody>
                      {completedOrders.map((order, index) => (
                        <>
                          <tr
                            key={index}
                            className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                          >
                            <td className="text-center p-2">
                              {order.uniqueId}
                            </td>
                            <td className="text-center p-2">
                              {order.date_completed
                                ? new Date(
                                    order.date_completed
                                  ).toLocaleDateString("en-us", {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  })
                                : "--"}
                            </td>
                            <td className="text-center p-2">
                              <Link
                                className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                to={`/orders/${order._id}`}
                                target="_blank"
                              >
                                View
                              </Link>
                            </td>
                            <td className="text-center">
                              <span
                                className={`p-3 rounded-2xl ${applyAnimation(
                                  order.order_status
                                )}`}
                              >
                                {order.order_status}
                              </span>
                            </td>
                            <td className="text-center p-2">
                              <button
                                className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                onClick={() => {
                                  if (
                                    order.assigned_by ===
                                    `${user.name} ${user.surname}`
                                  ) {
                                    setActiveModalOrderId(order._id);
                                  } else {
                                    toast.warn("Action not allowed");
                                  }
                                }}
                              >
                                Assign
                              </button>
                            </td>
                          </tr>
                          <div
                            className={`fixed z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md border border-[black] max-h-[60vh] overflow-y-scroll bg-[white] ${
                              activeModalOrderId === order._id
                                ? "block"
                                : "hidden"
                            }`}
                          >
                            <button
                              className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                              onClick={() => setActiveModalOrderId(null)}
                            >
                              <i className="fas fa-times"></i>
                            </button>
                            <h4>Order: {order.uniqueId}</h4>

                            <h4 className="text-center font-bold text-[1.5rem]">
                              {userType.some((r) => r.type === "Tailor")
                                ? "Assign QA/QC"
                                : "Assign Order To:"}
                            </h4>
                            <div className="w-full flex flex-col mx-auto my-4 relative">
                              <select
                                className="p-2 border-2 border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                                onChange={(e) => {
                                  if(userType.some((r) => r.type === "Tailor")){
                                  determineUserValue(e)
                                  }
                                  
                                  }}
                              >
                                <option value="">
                                  -- Select from the list --
                                </option>
                                {filteredUsers &&
                                  filteredUsers.map((user, index) => (
                                    <option
                                      key={index}
                                      value={`${user.name} ${user.surname}`}
                                    >{`${user.name} ${user.surname}`}</option>
                                  ))}
                              </select>
                            </div>
                            <div className="w-full flex flex-col mx-auto my-4 relative">
                              <button
                                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[black] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                                onClick={() => assignOrder(order._id)}
                              >
                                Assign <i className="fas fa-user-plus"></i>
                              </button>
                            </div>
                          </div>
                        </>
                      ))}
                    </tbody>
                  </table>
                </>
              ) : (
                <div className="border-2 border-[orangered] my-[2rem]">
                  <p className="text-center text-2xl my-[2rem] animate-pulse text-[orangered]">
                    {userType.some((r) => r.type === "Administrator")
                      ? "No orders here..."
                      : "You have no completed orders..."}
                  </p>
                </div>
              )}
            </section>
          </>
        )}
      </main>
      <Footer />
    </>
  );
};

export default UserProfile;
