import React, { useEffect, useState } from "react";
import menu_white from "../assets/menu_white_48dp.svg";
import close_white from "../assets/close_white_24dp.svg";
import lunch from "../assets/lunch_dining_white_24dp.svg";
import { Link } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Sidebar = () => {
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:8000"
    : "";
  const [sideMenu, setSideMenu] = useState(false);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchCategories = async () => {
      await axios
        .get(`${apiUrl}/categories`)
        .then((response) => {
          setCategories(response.data.categories);
        })
        .catch((error) => {
          console.error("Error: ", error);
          toast.error(error.response.data?.message || error.message);
        })
        .finally(() => {
          setIsLoading(false);
        });
    };

    // fetchCategories();
  }, [categories, apiUrl]);

  return (
    <>
      <input
        type="checkbox"
        id="check"
        className="hidden"
        onChange={() => setSideMenu(!sideMenu)}
      />
      <label htmlFor="check" title="Open/close menu">
        {!sideMenu && (
          <span className="absolute cursor-pointer bg-[green] rounded-[3px] left-[40px] top-[25px] text-[2rem] text-[white] py-[6px] px-[12px]">
            <img src={menu_white} alt="" />
          </span>
        )}
        <span
          className={`absolute cursor-pointer bg-[green] rounded-[3px] text-[2rem] text-[white] py-[6px] px-[12px] z-[1111] ${
            sideMenu ? "left-[90%] md:left-[20%]" : "-left-[195px]"
          } top-[25px] text-[2rem] transition-all duration-[.5s]`}
        >
          <img src={close_white} alt="" />
        </span>
      </label>
      <div
        className={`fixed z-[1110] ${
          sideMenu ? "left-0" : "-left-full md:-left-2/4"
        } w-full md:w-1/4 h-screen overflow-y-scroll overflow-x-hidden bg-[tomato]`}
      >
        <Link
          to="/create"
          className="mt-[5rem] flex justify-between w-full text-[1.25rem] text-[white] py-[20px] px-[40px] box-border border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:pl-[50px] mr-[60px] bg-[orange]"
        >
          Create Quiz
          <i className="fas fa-square-plus text-[white]"></i>
        </Link>
        <h2 className="text-[1.25rem] text-center text-[green] bg-[black] select-none py-[0.5rem]">
          All Quizzes ({categories.length || 0})
        </h2>
        <ul>
          {isLoading && <li>Loading...</li>}
          <li className="active">
            <Link
              to="/quiz/food"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Food
            </Link>
          </li>
          <li>
            <Link
              to="./pages/geo.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Geography
            </Link>
          </li>
          <li>
            <Link
              to="./pages/sports.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Sports
            </Link>
          </li>
          <li>
            <Link
              to="./pages/celeb.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Celebrities
            </Link>
          </li>
          <li>
            <Link
              to="./pages/politics.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Politics
            </Link>
          </li>
          <li>
            <Link
              to="./pages/television.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              TV
            </Link>
          </li>
          <li>
            <Link
              to="./pages/music.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Music
            </Link>
          </li>
          <li>
            <Link
              to="./pages/marvel.html"
              className="flex justify-center text-[1.25rem] text-[white] text-center w-full py-[1rem] border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:-translate-x-[5%]"
            >
              Marvel
            </Link>
          </li>
          <li>
            <Link
              to="./pages/driving.html"
              className="flex justify-between h-full w-full text-[1.25rem] text-[white] py-[20px] box-border border-t-[1px] border-b-[1px] border-b-[black] border-solid border-[#ffffff1a] duration-[.4s] hover:pl-[50px] mr-[60px]"
            >
              Driving
              <span>
                <img src="./assets/car.svg" alt="" />
              </span>{" "}
            </Link>
          </li>
        </ul>
      </div>
      <br />
    </>
  );
};

export default Sidebar;
