import React, { useState } from "react";

const Clothing = ({ label, imageUrl }) => {
  const [isChecked, setIsChecked] = useState(false);
  return (
    <>
      <div
        className={`relative w-28 hover:bg-[#ddd] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 ${
          isChecked ? "border-2 border-khamco-primary bg-[#ccc]" : ""
        }`}
      >
        <label htmlFor="clothing_item" onClick={(e) => e.preventDefault()} className="flex flex-col items-center cursor-pointer">
          <img src={imageUrl} alt={label} />
          <p className="text-sm">{label}</p>
        </label>
        <input
          type="checkbox"
          id="clothing_item"
          onChange={() => setIsChecked(!isChecked)}
          className="w-[50rem] accent-khamco-primary absolute top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 z-50 cursor-pointer"
        />
      </div>
    </>
  );
};

export default Clothing;
