import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import { Link } from "react-router-dom";
import axios from "axios";

const AdminFabrics = () => {
  const [message, setMessage] = useState("");
  const [aggregateData, setAggregateData] = useState([]);
  const [highestProducts, setHighestProducts] = useState([]);
  const [lowestProducts, setLowestProducts] = useState([]);
  const [productQuantities, setProductQuantities] = useState([]);
  const [products, setProducts] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState("Recent Activity");
  const [display, setDisplay] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = "http://localhost:5000/admin/fabrics";

  useEffect(() => {
    const getData = async() => {
        await axios.get(apiUrl).then(response => {
            setIsLoading(false);
            setDisplay(true);
            setAggregateData(response.data.aggregateData);
            setHighestProducts(response.data.highestProducts);
            setLowestProducts(response.data.lowestProducts);
            setProductQuantities(response.data.productQuantities);
            setProducts(response.data.products);
        }).catch(error => {
            setMessage(error);
            setDisplay(false);
            setIsLoading(true);
        });
    }
  
    return () => {
      getData()
    }
  }, [])
  

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="admin-dashboard font-arial">
          <p className="message">{message}</p>
          {isLoading && <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="metric bg-[#444]"></div>
            <div className="metric bg-[#444]"></div>
            <div className="metric bg-[#444]"></div>
          </div>}
          {display && <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            {highestProducts.map((product) => (
              <div className="metric">
                <h2 className="text-khamco-secondary text-2xl font-bold">Highest Quantity</h2>
                <p>{product.name}</p>
                <h2>
                  <Link
                    to={`/products/${product.id}`}
                    target="_blank"
                    className="text-xl font-semibold text-green-500"
                  >
                    {product.quantity} meters{" "}
                    <i className="fas fa-external-link"></i>
                  </Link>
                </h2>
              </div>
            ))}
            {lowestProducts.map((product) => (
              <div className="metric">
                <h2 className="text-khamco-secondary text-2xl font-bold">Lowest Quantity</h2>
                <p>{product.name}</p>
                <h2>
                  <Link
                    to={`/products/${product.id}`}
                    target="_blank"
                    className="text-red-500"
                  >
                    {product.quantity} meters{" "}
                    <i className="fas fa-external-link"></i>
                  </Link>
                </h2>
              </div>
            ))}
          </div>}
          {display && <div className="search-engine mt-[4rem]">
            <select className="search-input" onChange={(e)=>setSelectedIndex(e.target.value)}>
              <option>-- Select Report --</option>
              <option>Recent Activity</option>
              <option>Fabric Consumed</option>
              <option>Available Fabric</option>
            </select>
          </div>}
          {display && <>
          {selectedIndex === "Recent Activity" && <table id="recent-activity">
            <caption>Recent Activity</caption>
            <thead>
              <tr>
                <td>Fabric</td>
                <td>Date</td>
                <td>Meters</td>
              </tr>
            </thead>
            <tbody>
              {aggregateData.map((data) => (
                <tr>
                  <td>{data._id.choice}</td>
                  <td>
                    {new Date(
                      `${data._id.month}/${data._id.day}/${data._id.year}`
                    ).toLocaleDateString("en-US", {
                      month: "long",
                      day: "numeric",
                      year: "numeric",
                    })}
                  </td>
                  <td>{data.totalQuantity}</td>
                </tr>
              ))}
            </tbody>
          </table>}
          {selectedIndex === "Fabric Consumed" && <table id="fabric-consumed">
            <caption>Fabric Consumed</caption>
            <thead>
              <tr>
                <td>Fabric</td>
                <td>Total Consumption</td>
              </tr>
            </thead>
            <tbody>
              {Object.keys(productQuantities).map((product) => (
                <tr>
                  <td>{product}</td>
                  <td>{productQuantities[product]} meters</td>
                </tr>
              ))}
            </tbody>
          </table>}
          {selectedIndex === "Available Fabric" && <table id="available-fabric">
            <caption>Available Fabric</caption>
            <thead>
              <tr>
                <td>Name</td>
                <td>Meters</td>
                <td>Category</td>
                <td>Price</td>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr>
                  <td>{product.name}</td>
                  <td>{product.quantity.toFixed(2)}</td>
                  <td className="capitalize">
                    {product.category}
                  </td>
                  <td>KWD {product.price}</td>
                </tr>
              ))}
            </tbody>
          </table>}
          </>}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminFabrics;
