import React, { useState } from "react";
import Navbar from "../../../components/Navbar";
import AdminPanel from "../../../components/AdminPanel";
import Footer from "../../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";
import { Buffer } from "buffer";
import { toast } from "react-toastify";

const AdminProducts = () => {
  const [products, setProducts] = useState([]);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/products"
    : "https://khamco-shop-backend.onrender.com/products";
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();

  const fetchData = async (queryParams = "") => {
    await axios
      .get(`${apiUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        setProducts(response.data.products);
        setDisplay(true);
        setIsLoading(false);
      })
      .catch((error) => {
        setProducts([]);
        setDisplay(false);
        setIsLoading(true);
        toast.error(error.response?.data?.message || error.message);
      });
  };

  useEffect(() => {
    document.title = "Admin Products Data - Khamco Shop";

    fetchData(location.search);
  }, [location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section
          id="inventory"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          <div className="utilities">
            <Link to="/products/new">
              Add New Product <i className="fas fa-cart-plus"></i>
            </Link>
          </div>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#666] p-[6rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] animate-pulse"
                ></div>
              ))}
          {display && (
            <>
              <div className="utilities">
                <form onSubmit={handleSearch} className="search-engine">
                  <input
                    type="text"
                    placeholder="Search product name"
                    name="name"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form onSubmit={handleSearch} className="search-engine">
                  <input
                    type="date"
                    name="date"
                    id="date-search"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-check"></i>
                  </button>
                </form>
              </div>
              <h1 className="title">All Products ({products.length || 0})</h1>
              {products ? (
                products.map((product, index) => (
                  <div
                    className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem]"
                    key={index}
                  >
                    <img
                      src={`data:${product.photoType};base64, ${Buffer.from(
                        product.picture.data
                      ).toString("base64")}`}
                      alt={product.name}
                      loading="lazy"
                      className="w-[15vw] h-[30vh]"
                    />
                    <p>
                      <strong>Name</strong>: {product.name}
                    </p>
                    <p>
                      <strong>Category</strong>: {product.category}
                    </p>
                    <Link
                      to={`/admin/products/${product._id}`}
                      className="edit-btn"
                    >
                      View Product
                    </Link>
                  </div>
                ))
              ) : (
                <p className="text-center my-12">No product data.</p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminProducts;
