import { Link, useNavigate, useParams } from "react-router-dom";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";

const UserProfile = () => {
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [allUsers, setAllUsers] = useState(null);
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [newOrderModal, setNewOrderModal] = useState(false);
  const [currentOrderModal, setCurrentOrderModal] = useState(false);
  const [completedOrderModal, setCompletedOrderModal] = useState(false);
  const [newOrders, setNewOrders] = useState(null);
  const [currentOrders, setCurrentOrders] = useState(null);
  const [completedOrders, setCompletedOrders] = useState(null);

  useEffect(() => {
    let isMounted = true;

    document.title = "Profile Management";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/admin/users/profile/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setIsLoading(false);
            setUser(response.data.user);
            setAllUsers(response.data.all_users);
            setNewOrders(
              response.data.user_orders.filter(
                (order) => order.is_accepted === false
              )
            );
            setCurrentOrders(
              response.data.user_orders.filter(
                (order) => order.is_accepted === true
              )
            );
            setCompletedOrders(
              response.data.user_orders.filter(
                (order) => order.order_status === "Completed"
              )
            );
            document.title = `Tailor Dashboard - ${response.data.user.name}`;
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, id, navigate, serverUrl]);

  const renderImage = () => {
    if (user.picture && user.picture.data) {
      // Convert the binary data to base64
      const binary = new Uint8Array(user.picture.data).reduce(
        (acc, byte) => acc + String.fromCharCode(byte),
        ""
      );
      const base64String = btoa(binary);

      return (
        <img
          src={`data:${user.photoType};base64,${base64String}`}
          alt={user.name}
          loading="lazy"
          className="w-full md:w-2/4 h-[40vh] object-contain"
        />
      );
    } else {
      return (
        <img
          src=""
          alt=""
          className="w-full md:w-2/4 h-[40vh] object-contain"
        />
      );
    }
  };

  const newOrderResponse = async (choice, orderId) => {
    try {
      const response = await axios.put(
        `${serverUrl}/orders/${orderId}`,
        {
          is_accepted: choice,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(`Order has been ${choice ? "Accepted" : "Rejected"}`);
      setNewOrderModal(false);
      setNewOrders(
        response.data.user_orders.filter((order) => order.is_accepted === false)
      );
      setCurrentOrders(
        response.data.user_orders.filter((order) => order.is_accepted === true)
      );
      setCompletedOrders(
        response.data.user_orders.filter(
          (order) => order.order_status === "Completed"
        )
      );
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem] font-arial">
        <h1 className="text-center text-[2rem] font-minimoBold">Dashboard</h1>
        {isLoading && (
          <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto text-center">
            <thead className="text-khamco-primary font-bold bg-[#ddd]">
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                <td className="text-center p-2">Loading...</td>
              </tr>
            </thead>
            <tbody>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
              <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td className="text-center p-2">k</td>
              </tr>
            </tbody>
          </table>
        )}
        {user && (
          <>
            <section
              className={`w-3/4 m-auto flex justify-between items-center my-[2rem] flex-wrap p-[1rem] gap-[2rem] md:gap-0`}
            >
              <div className="flex flex-col gap-[1rem]">
                <span className="flex gap-[0.5rem]">
                  <strong>Name:</strong> {`${user.name} ${user.surname}`}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>Email:</strong> {user.email}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>Position:</strong> {user.role}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>ID No:</strong> {user._id}
                </span>
                <span>
                  <strong>Date Joined:</strong>{" "}
                  {new Date(user.date).toLocaleDateString("en-us", {
                    month: "long",
                    day: "numeric",
                    year: "numeric",
                  })}
                </span>
                <span className="flex gap-[0.5rem]">
                  <strong>Preferred Language:</strong>{" "}
                  {user.language === "en" && "English"}
                  {user.language === "ur" && "Urdu"}
                  {user.language === "ar" && "Arabic"}
                  {user.language === "hi" && "Hindi"}
                </span>
              </div>
              {renderImage()}
            </section>
            <section className="relative">
              <>
                {newOrders && newOrders.length > 0 ? (
                  <>
                    <h1 className="text-center text-[2rem] font-minimoBold">
                      New Orders
                    </h1>
                    <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                      <thead className="text-khamco-primary font-bold bg-[#ddd]">
                        <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                          <th className="text-center p-2">Order No.</th>
                          <th className="text-center p-2">Date Assigned</th>
                          <th className="text-center p-2">Assigned By</th>
                          <th className="text-center p-2">Order Details</th>
                          <th className="text-center p-2">Accept/Reject</th>
                        </tr>
                      </thead>
                      <tbody>
                        {newOrders.map((order, index) => (
                          <>
                            <tr
                              key={index}
                              className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                            >
                              <td className="text-center p-2">
                                {order.uniqueId}
                              </td>
                              <td className="text-center p-2">
                                {new Date(order.order_date).toLocaleDateString(
                                  "en-us",
                                  {
                                    month: "2-digit",
                                    day: "2-digit",
                                    year: "numeric",
                                  }
                                )}
                              </td>
                              <td className="text-center p-2">
                                {order.assigned_by}
                              </td>
                              <td className="text-center p-2">
                                <Link
                                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                  to={`/orders/${order._id}`}
                                >
                                  View
                                </Link>
                              </td>
                              <td className="text-center p-2">
                                <button
                                  className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                  onClick={() => setNewOrderModal(true)}
                                >
                                  Action
                                </button>
                              </td>
                            </tr>
                            <div
                              className={`absolute z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md bg-[white] ${
                                newOrderModal ? "block" : "hidden"
                              }`}
                            >
                              <button
                                className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                                onClick={() => setNewOrderModal(false)}
                              >
                                <i className="fas fa-times"></i>
                              </button>
                              <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                                Select an Action
                              </h4>
                              <div className="flex my-[2rem] justify-center gap-[2rem] flex-wrap">
                                <button
                                  className="p-[1rem] bg-[lawngreen] rounded-lg hover:opacity-70 flex-auto"
                                  onClick={() =>
                                    newOrderResponse(true, order._id)
                                  }
                                >
                                  Accept
                                </button>
                                <button
                                  className="p-[1rem] bg-[crimson] text-[white] rounded-lg hover:opacity-70 flex-auto"
                                  onClick={() =>
                                    newOrderResponse(false, order._id)
                                  }
                                >
                                  Reject
                                </button>
                              </div>
                            </div>
                          </>
                        ))}
                      </tbody>
                    </table>
                  </>
                ) : (
                  <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                    You have no new orders...
                  </p>
                )}
              </>
            </section>
            <section className="relative">
              <>
                {currentOrders && currentOrders.length > 0 ? (
                  <>
                    <h1 className="text-center text-[2rem] font-minimoBold">
                      Current Orders
                    </h1>
                    <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                      <thead className="text-khamco-primary font-bold bg-[#ddd]">
                        <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                          <th className="text-center p-2">Order No.</th>
                          <th className="text-center p-2">Date Assigned</th>
                          <th className="text-center p-2">Measurements</th>
                          <th className="text-center p-2">Order Details</th>
                          <th className="text-center p-2">Order Progress</th>
                        </tr>
                      </thead>
                      <tbody>
                        {currentOrders.map((order, index) => (
                          <>
                            <tr
                              key={index}
                              className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                            >
                              <td className="text-center p-2">
                                {order.uniqueId}
                              </td>
                              <td className="text-center p-2">
                                {new Date(
                                  order.in_progress_time
                                ).toLocaleDateString("en-us", {
                                  month: "2-digit",
                                  day: "2-digit",
                                  year: "numeric",
                                })}
                              </td>
                              <td className="text-center p-2">
                                <Link
                                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                  to={`/orders/${order._id}`}
                                >
                                  View
                                </Link>
                              </td>
                              <td className="text-center p-2">
                                <Link
                                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90"
                                  to={`/orders/${order._id}`}
                                >
                                  View
                                </Link>
                              </td>
                              <td className="text-center p-2">
                                <button
                                  className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                                  onClick={() => setCurrentOrderModal(true)}
                                >
                                  Action
                                </button>
                              </td>
                            </tr>
                            <div
                              className={`absolute z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md bg-[white] ${
                                currentOrderModal ? "block" : "hidden"
                              }`}
                            >
                              <button
                                className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                                onClick={() => setCurrentOrderModal(false)}
                              >
                                <i className="fas fa-times"></i>
                              </button>
                              <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                                Select an Action
                              </h4>
                              <div className="flex my-[2rem] justify-center gap-[2rem] flex-wrap">
                                <button
                                  className="p-[1rem] bg-[lawngreen] rounded-lg hover:opacity-70 flex-auto"
                                  onClick={() => setCurrentOrderModal(false)}
                                >
                                  Start Process
                                </button>
                                <button
                                  className="p-[1rem] bg-[crimson] text-[white] rounded-lg hover:opacity-70 flex-auto"
                                  onClick={() => setCurrentOrderModal(false)}
                                >
                                  Finish Process
                                </button>
                              </div>
                            </div>
                          </>
                        ))}
                      </tbody>
                    </table>
                  </>
                ) : (
                  <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                    You have not accepted any orders...
                  </p>
                )}
              </>
            </section>
            <section className="relative">
              <h1 className="text-center text-[2rem] font-minimoBold">
                Completed Orders
              </h1>
              <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                <thead className="text-khamco-primary font-bold bg-[#ddd]">
                  <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <th className="text-center p-2">Order No.</th>
                    <th className="text-center p-2">Date Completed</th>
                    <th className="text-center p-2">Order Details</th>
                    <th className="text-center p-2">Order Status</th>
                    <th className="text-center p-2">Order Progress</th>
                  </tr>
                </thead>
                <tbody>
                  <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td className="text-center p-2">AS5435SD</td>
                    <td className="text-center p-2">01/09/2024</td>
                    <td className="text-center p-2">
                      <Link className="py-[0.5rem] px-[1rem] outline-none border-none text-white rounded-lg cursor-pointer bg-[blue] hover:opacity-90">
                        View
                      </Link>
                    </td>
                    <td className="text-center p-2">Accepted</td>
                    <td className="text-center p-2">
                      <button
                        className="bg-[#444] text-white py-[0.5rem] px-[0.8rem] rounded-lg hover:opacity-80"
                        onClick={() => setCompletedOrderModal(true)}
                      >
                        Action
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
              <div
                className={`absolute z-50 left-2/4 top-1/4 -translate-x-2/4 w-full md:w-2/4 shadow-md shadow-[#aaa] py-[1rem] px-[2rem] rounded-md bg-[white] ${
                  completedOrderModal ? "block" : "hidden"
                }`}
              >
                <button
                  className="absolute top-1 right-1 bg-[#444] py-[0.5rem] px-[1rem] rounded-full text-2xl text-[white]"
                  onClick={() => setCompletedOrderModal(false)}
                >
                  <i className="fas fa-times"></i>
                </button>
                <h4 className="text-center font-bold text-[1.5rem] my-[3rem]">
                  Assign Order To:
                </h4>
                <div className="w-full flex flex-col mx-auto my-4 relative">
                  <select className="p-2 border-2 border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]">
                    <option value="">-- Select from the list --</option>
                    {allUsers &&
                      allUsers.map((user, index) => (
                        <option
                          key={index}
                          value={`${user.name} ${user.surname}`}
                        >{`${user.name} ${user.surname}`}</option>
                      ))}
                  </select>
                </div>
              </div>
            </section>
          </>
        )}
      </main>
      <Footer />
    </>
  );
};

export default UserProfile;
