import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import FormFields from "./FormFields";
import { Link } from "react-router-dom";

const NewProduct = () => {
  const [message, setMessage] = useState("");
  const [writingDirection, setWritingDirection] = useState("ltr");
  return (
    <>
      <Navbar />
      <main>
        <section className="p-[4rem] grid justify-center items-center" dir={writingDirection}>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products">
              <i className="fas fa-arrow-left"></i> All Products
            </Link>
          </div>
          <h1 className="title">Add New Product</h1>
          <p className="message">{message}</p>
          <form action="/products" method="POST" className="flex flex-col justify">
            <FormFields />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewProduct;
