import React from "react";

const MeasurementItem = ({ icon, metric, currentUnit, label }) => {
  return (
    <span className="measurement-items flex flex-col justify-start text-balance flex-wrap border-2 border-[black] p-[1rem] bg-[#ddd] items-center gap-2 rounded-3xl text-lg">
      <strong>{label}: </strong>
      {metric} {currentUnit === "Centimeters" ? "centimeters" : "inches"}
      {icon ? (
        <img
          src={icon}
          alt=""
          className="w-[12rem] h-[30vh] border-2 border-khamco-primary"
        />
      ) : (
        <img
          src=""
          alt=""
          className="bg-[#333] w-[12rem] h-[30vh] border-2 border-khamco-primary"
        />
      )}
    </span>
  );
};

export default MeasurementItem;
