import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo192.png";
import { useState } from "react";
import { useStoreActions, useStoreState } from "easy-peasy";
import axios from "axios";
import { toast } from "react-toastify";

const Navbar = () => {
  const [menu, setMenu] = useState(false);
  const userLanguage = useStoreState((state) => state.userLanguage);
  const navigate = useNavigate();
  const handleLogout = async () => {
    localStorage.clear();
    navigate("/users/login");
    toast.success();
  };
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/users/language"
    : "https://khamco-shop-backend.onrender.com/users/language";
  const authKey = localStorage.getItem("jwt");

  const updateUserLanguage = async (language) => {
    localStorage.setItem("userLanguage", language);
    await axios
      .post(
        apiUrl,
        { language: language },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
      })
      .catch((error) => {
        toast.error(error.message);
      });
  };

  return (
    <nav className="flex gap-[8rem] justify-evenly items-center py-2 px-4">
      <Link
        to="/"
        className="text-[3rem] text-khamco-primary no-underline font-minimoBold"
      >
        <img src={logo} alt="" style={{ width: "40%" }} />
      </Link>
      {!menu && (
        <button
          className="text-khamco-ribbon text-[2rem] cursor-pointer md:hidden"
          onClick={() => setMenu(!menu)}
        >
          <i className="fas fa-bars"></i>
        </button>
      )}
      {menu && (
        <button
          className="text-khamco-ribbon text-[2rem] cursor-pointer md:hidden"
          onClick={() => setMenu(!menu)}
        >
          <i className="fas fa-times"></i>
        </button>
      )}
      <ul
        className={`md:flex flex-col md:flex-row justify-between list-none gap-[4rem] absolute md:relative w-full md:w-[35%] bg-white top-[15%] z-50 py-[1rem] px-[4rem] md:p-0 ${
          menu ? "flex" : "hidden"
        }`}
      >
        <Link
          to="/"
          className="text-white bg-khamco-ribbon py-2 px-4 rounded-2xl cursor-pointer text-[1rem]"
        >
          Main Menu
        </Link>
        <form>
          <select
            onChange={(e) => {
              updateUserLanguage(e.target.value);
            }}
            className="text-white bg-khamco-ribbon py-2 px-4 rounded-2xl cursor-pointer text-[1rem] w-full"
          >
            <option
              value="en"
              className="bg-white text-khamco-secondary cursor-pointer"
              selected={userLanguage === "en"}
            >
              English
            </option>
            <option
              value="hi"
              className="bg-white text-khamco-secondary cursor-pointer"
              selected={userLanguage === "hi"}
            >
              Hindi
            </option>
            <option
              value="ur"
              className="bg-white text-khamco-secondary cursor-pointer"
              selected={userLanguage === "ur"}
            >
              Urdu
            </option>
            <option
              value="ar"
              className="bg-white text-khamco-secondary cursor-pointer"
              selected={userLanguage === "ar"}
            >
              Arabic
            </option>
          </select>
        </form>
        <button
          className="text-white bg-[crimson] py-2 px-4 rounded-2xl cursor-pointer text-[1rem]"
          onClick={handleLogout}
        >
          Logout
        </button>
      </ul>
    </nav>
  );
};

export default Navbar;
