import React, { useEffect, useState } from "react";
import Ribbon from "../../components/Ribbon";
import axios from "axios";

const ResetPassword = () => {
  const [passwordType, setPasswordType] = useState(false);
  const [password2Type, setPassword2Type] = useState(false);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";

  useEffect(() => {
    document.title = "Reset Password - Khamco Shop";
  }, []);

  const handleResetPassword = async()=>{
    try {
      const response = await axios.post(`${apiUrl}`)
    } catch (error) {
      
    }
  }

  return (
    <>
      <Ribbon />
      <main>
        <section className="p-[4rem] grid justify-center items-center">
          <h1 className="title">Change Password</h1>
          <form action="/users/reset-password/<%= token %>" method="POST" className="flex flex-col justify-center items-center w-[90vw]">
            <div className="form-row">
              <label for="password">New Password</label>
              <input type={passwordType ? "text" : "password"} name="password" id="password" required />
              <button type="button" className="show-hide" onClick={()=>setPasswordType(!passwordType)}>
                Show
              </button>
            </div>
            <div className="form-row">
              <label for="password2">Confirm Password</label>
              <input type={password2Type ? "text" : "password"} name="password2" id="password2" required />
              <button type="button" className="show-hide" onClick={()=>setPassword2Type(!password2Type)}>
                Show
              </button>
            </div>
            <div className="form-row">
              <button type="submit">Reset Password</button>
            </div>
          </form>
        </section>
      </main>
    </>
  );
};

export default ResetPassword;
