import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";

const AdminDashboard = () => {
  const [message, setMessage] = useState("Hello World");
  const [customers, setCustomers] = useState([{}]);
  const total_revenue = 1500.99;
  const available_fabric = 650;
  const average_service_time = {
    days: 7,
    hours: 24,
  };
  const invoice_data = [
    {
      total: 500,
      sum: 100,
    },
  ];
  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section class="admin-dashboard">
          <h1 class="title">Admin Dashboard</h1>
          <p class="message">{message}</p>
          <div class="khamco-wrapper">
            <div class="metric">
              <h2>Total Customers</h2>
              <h2>{customers.length}</h2>
              <Link to="/admin/customers_data" class="edit-btn">
                Details
              </Link>
            </div>
            <div class="metric">
              <h2>Revenue Generated</h2>
              <h2 className="text-green-500">
                KWD {new Intl.NumberFormat("en-US").format(total_revenue)}
              </h2>
              <Link to="/admin/revenue" class="edit-btn">
                Details
              </Link>
            </div>
            <div class="metric">
              <h2>Total Fabric</h2>
              <h2 className="text-green;">{available_fabric} meters</h2>
              <a href="/admin/fabrics" class="edit-btn">
                Details
              </a>
            </div>
            <div class="metric">
              <h2>Average Service Time</h2>
              <h2 style="color: green;">{`${average_service_time.days.toFixed(
                2
              )} "day(s)," average_service_time.hours.toFixed(2) "hours"}`}</h2>
              <Link to="/admin/service-times" class="edit-btn">
                Details
              </Link>
            </div>
            <div class="metric">
              <h2>Invoice Data</h2>
              <h2 style="color: green;">{`${
                invoice_data.length ? invoice_data[0].total : 0
              } "created"}`}</h2>
              <h2 style="color: green;">{`${
                invoice_data.length ? invoice_data[0].sum : 0
              } "deleted"}`}</h2>
              <Link to="/admin/invoice_data" class="edit-btn">
                Details
              </Link>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminDashboard;
