import React, { useState } from "react";

const ProductsForm = ({
  setProductName,
  setProductQuantity,
  setProductCategory,
  setProductPhoto,
  setProductPrice,
  setProductDate,
  date,
}) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  return (
    <>
      <div className="form-row">
        <label for="name">Product Name</label>
        <input
          type="text"
          placeholder="Enter name of product"
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label for="quantity">Quantity</label>
        <input type="number" onChange={(e) => setProductQuantity(e.target.value)} />
      </div>
      <div className="form-row">
        <label for="category">Category</label>
        <select defaultValue={"cotton"} onChange={(e) => setProductCategory(e.target.value)}>
          <option value="cotton">Cotton</option>
          <option value="linen">Linen</option>
          <option value="wool">Wool</option>
          <option value="polyester">Polyester</option>
          <option value="other">Other</option>
        </select>
      </div>
      <div className="form-row">
        <label for="picture">Product Photo</label>
        <input
          type="file"
          name="cover"
          id="image"
          onChange={(e) => setPRod(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label for="price">Price</label>
        <input type="number" step="0.01" onChange={(e) => setProductPrice(e.target.value)} />
      </div>
      <div className="form-row">
        <label for="date">Date</label>
        <input
          type="date"
          name="date"
          id="date"
          onChange={(e) =>
            setProductDate(e.target.value.toISOString().split("T")[0])
          }
          value={date ? formatDate(date) : null}
        />
      </div>
      <div className="form-row">
        <button type="submit">Submit</button>
      </div>
    </>
  );
};

export default ProductsForm;
