import { useEffect, useState } from "react";

const AssignedOrders = ({ isOpen, setIsOpen, users, orders }) => {
  const [assignedOrders, setAssignedOrders] = useState({});

  useEffect(() => {
    if (users && orders) {
      // Initialize the assigned orders for each user
      const userOrders = users.reduce((acc, user) => {
        acc[`${user.name} ${user.surname}`] = 0; // Start with 0 orders for each user
        return acc;
      }, {});

      orders.forEach((order) => {
        // Increment the count for each role if the user matches
        users.forEach((user) => {
          const userName = `${user.name} ${user.surname}`;
          if (order.measurer === userName) {
            userOrders[userName] += 1;
          }
          if (order.cutter === userName) {
            userOrders[userName] += 1;
          }
          if (order.tailor === userName) {
            userOrders[userName] += 1;
          }
          if (order.driver === userName) {
            userOrders[userName] += 1;
          }
        });
      });

      setAssignedOrders(userOrders);
      console.log(userOrders);
    }
  }, [orders, users]);

  return (
    <dialog
      className="fixed z-[60] top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-[90%] md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] p-[2rem]"
      open={isOpen}
    >
      <button
        className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
        type="button"
        onClick={() => setIsOpen(false)}
      >
        <i className="fas fa-times"></i>
      </button>
      <h2 className="text-center text-[2rem] my-[2rem] underline font-minimoBold">
        User Orders
      </h2>
      <ul className="list-disc m-auto w-full md:w-1/2 flex flex-col mx-auto relative">
        {users &&
          users.map((user, index) => (
            <li key={index}>
              {`${user.name} ${user.surname}`} ({assignedOrders[`${user.name} ${user.surname}`] || 0} Orders)
            </li>
          ))}
      </ul>
    </dialog>
  );
};

export default AssignedOrders;
