import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";

const CustomerInvoices = () => {
  const { id } = useParams();
  const [invoices, setInvoices] = useState(null);
  const [customer, setCustomer] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/invoices`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/invoices`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const {t} = useTranslation();

  const fetchData = async (queryParams = "") => {
    await axios
      .get(`${apiUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        setCustomer(response.data.customer);
        setInvoices(response.data.invoices);
        setLoading(false);
        setDisplay(true);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        setCustomer(null);
        setLoading(true);
        setLoading(false);
        setInvoices(null);
      });
  };

  useEffect(() => {
    
    fetchData(location.search);
  }, [apiUrl, authKey, location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section
          id="customer-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          {loading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                ></div>
              ))}
          {display && invoices && customer && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link to={`/customers/${customer._id}`} className="text-[blue] no-underline hover:underline cursor-pointer">
                  <i className="fas fa-arrow-left"></i>
                  {` Customer ${customer.name} ${customer.surname}`}
                </Link>
              </div>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
                  <input type="date" name="date" className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary" />
                  <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                    <i className="fas fa-check"></i>
                  </button>
                </form>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">{`${t("All Invoices for")} ${customer.name} ${customer.surname} (${invoices.length})`}</h1>
              {invoices && invoices.length > 0 ? (
                invoices.map((invoice, index) => (
                  <div
                    className="w-full bg-[#bebebe80] p-[2rem] shadow-lg shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem] my-[1rem]"
                    key={index}
                  >
                    <p className="capitalize">
                      <strong className="text-khamco-secondary text-lg">
                        {Date}
                      </strong>
                      :{" "}
                      {new Date(invoice.date).toLocaleDateString("en-US", {
                        month: "long",
                        day: "numeric",
                        year: "numeric",
                      })}
                    </p>
                    <p className="capitalize">
                      <strong className="text-khamco-secondary text-lg">
                        Payment Status:
                      </strong>{" "}
                      {invoice.payment_status}
                    </p>
                    <p className="capitalize">
                      <strong className="text-khamco-secondary text-lg">
                        Membership:
                      </strong>{" "}
                      {customer.membership_status === "VIP" ? (
                        <i className="fas fa-star text-[gold] animate-pulse">VIP</i>
                      ) : (
                        "Basic"
                      )}
                    </p>
                    <Link to={`/invoices/${invoice._id}`} className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90">
                      View
                    </Link>
                  </div>
                ))
              ) : (
                <p className="text-center">This customer has no invoices.</p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CustomerInvoices;
