import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";

const AdminInvoiceData = () => {
  const [message, setMessage] = useState("");
  const [invoiceData, setInvoiceData] = useState([]);
  const apiUrl = "http://localhost:5000/admin/invoice_data";
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = "Admin Invoice Data - Khamco Shop";
    const fetchInvoiceData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setInvoiceData(response.data.invoice_data);
          setIsLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          setMessage(error.message);
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.message);
          }
          return;
        });
    };

    return () => {
      fetchInvoiceData();
    };
  }, [authKey]);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section class="admin-dashboard">
          <p class="message">{message}</p>
          {isLoading && (
            <table className="text-center">
              <thead>
                <tr>
                  <td>Loading...</td>
                </tr>
              </thead>
              <tbody>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">k</tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">k</tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">k</tr>
              </tbody>
            </table>
          )}
          {display && (
            <table>
              <caption>Recent Invoice Data</caption>
              <thead>
                <tr>
                  <td>Customer</td>
                  <td>Date</td>
                  <td>Invoices Created</td>
                  <td>Invoices Deleted</td>
                </tr>
              </thead>
              <tbody>
                {invoiceData &&
                  invoiceData.map((entry, index) => (
                    <tr key={index}>
                      <td>
                        {entry.customer_name} {entry.customer_surname}
                      </td>
                      <td>
                        {new Date(entry.date).toLocaleDateString("en-US", {
                          month: "long",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                      <td>{entry.total_invoices_created}</td>
                      <td>{entry.total_invoices_deleted}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminInvoiceData;
