import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import AdminPanel from "../../components/AdminPanel";

const AdminCustomers = () => {
  const [customers, setCustomers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCustomers, setTotalCustomers] = useState(0);

  const fetchCustomers = async (queryParams = "") => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${apiUrl}/admin/customers${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      setCustomers(response.data.customers);
      setTotalPages(response.data.totalPages);
      setTotalCustomers(response.data.totalCustomers);
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        toast.error("An error occurred: " + error.message);
      }
      setCustomers([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = `Khamco Shop ${t("Customers")}`;
    const searchParams = new URLSearchParams(location.search);
    searchParams.set("page", currentPage); // Add page param to the query
    fetchCustomers(`?${searchParams.toString()}`);
  }, [authKey, location.search, t, currentPage]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form);
    searchParams.set("page", currentPage); // Ensure current page is included in search
    navigate(`?${searchParams.toString()}`);
  };

  const handleDelete = async (customerID) => {
    setIsLoading(true);
    try {
      const response = await axios.delete(`${apiUrl}/customers/${customerID}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      setCustomers(response.data.customers);
      setTotalCustomers(response.data.customers.length);
      setIsLoading(false);
    } catch (error) {
        setIsLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  }

  // Pagination controls
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prev) => prev + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
      <AdminPanel />
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[4rem]">
          <div className="flex justify-end gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/customers/new-customer"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              {t("New Customer")} <i className="fas fa-user-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                placeholder={`${t("Search Customer Name")}`}
                name="name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="date"
                name="date"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                name="phone"
                placeholder={`${t("Search Customer Phone Number")}`}
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                name="uniqueId"
                placeholder={`${t("Search Customer ID")}`}
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">{`${t(
            "Customer List"
          )} (${totalCustomers})`}</h1>
          
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[3rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                ></div>
              ))}
          <p>(Showing {customers.length} results)</p>
          {customers.length > 0 ? (
            customers.map((customer, index) => (
              <div
                className="w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-start md:justify-evenly flex-wrap items-center gap-[2rem]"
                dir="auto"
                key={index}
              >
                <p className="capitalize">
                  <strong>{t("Name")}</strong>:{" "}
                  {`${customer.name} ${customer.surname}`}{" "}
                  {customer.membership_status === "VIP" ? (
                    <i className="fas fa-star text-[#d2b929] animate-pulse"></i>
                  ) : (
                    ""
                  )}
                </p>
                <Link
                  to={`/customers/${customer._id}`}
                  className="py-[0.5rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-[4rem] text-center"
                >
                  {t("View")}
                </Link>
                <Link
                  to={`/customers/edit/${customer._id}`}
                  className="py-[0.5rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-[4rem] text-center"
                >
                  {t("EditButton")}
                </Link>
                <button
                  className="py-[0.5rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[red] hover:opacity-90 w-full md:w-[4rem] text-center"
                  onClick={() => handleDelete(customer._id)}
                >
                  {!isLoading ? t("Delete") : ""}
                  {isLoading ? <i className="fas fa-spinner animate-spin"></i> : ""}
                </button>
              </div>
            ))
          ) : (
            <p className="text-center">No customer data.</p>
          )}
