import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const AdminTailorDetails = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/admin`
    : `https://khamco-shop-backend.onrender.com/admin`;
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    let isMounted = true;

    const getUserDetails = async () => {
      await axios
        .get(`${apiUrl}/users/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setUser(response.data.user);
            document.title = `Admin Tailor Details - ${`${response.data.user.name} -`} Khamco Shop`;
            setIsLoading(false);
            setDisplay(true);
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(true);
            setDisplay(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
          }
          return;
        });
    };

    getUserDetails();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, navigate, user]);

  const renderImage = () => {
    if (user.picture && user.picture.data) {
      // Convert the binary data to base64
      const binary = new Uint8Array(user.picture.data).reduce(
        (acc, byte) => acc + String.fromCharCode(byte),
        ""
      );
      const base64String = btoa(binary);

      return (
        <img
          src={`data:${user.photoType};base64,${base64String}`}
          alt={user.name}
          loading="lazy"
          className="w-full md:w-2/4 h-[40vh] m-auto"
        />
      );
    } else {
      return null;
    }
  };

  return (
    <>
      <Navbar />
      <main>
        <section>
          {isLoading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(1).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-full bg-[#666] animate-pulse m-auto h-[100vh] my"></div>
                </div>
              </div>
            </>
          )}
          {display && user && (
            <div className="grid md:grid-cols-1 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-full">
                <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                  <Link
                    to={`/users/edit-user/${user._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  >
                    Edit
                  </Link>
                </div>
                {renderImage()}
                <h1 className="text-center text-[2rem] font-minimoBold">
                  User Details {user.name}
                </h1>
                <div className="flex flex-col gap-[1rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">ID</strong>
                    <p>{user._id}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Name</strong>
                    <p className="capitalize">{user.name}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Email</strong>
                    <p>{user.email}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Surname</strong>
                    <p className="capitalize">{user.surname}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Date Joined</strong>
                    <p className="capitalize">
                      {new Date(user.date).toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Role:</strong>
                    <ul className="capitalize self-center">
                      {user.role.map((r, index) => (
                        <li className="list-disc" key={index}>
                          {r.type}
                        </li>
                      ))}
                    </ul>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Language</strong>
                    <p className="capitalize">
                      {user.language === "en" && "English"}
                      {user.language === "ur" && "Urdu"}
                      {user.language === "ar" && "Arabic"}
                      {user.language === "hi" && "Hindi"}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Salary</strong>
                    <p className="capitalize">{user.salary || "--"}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Measurements Taken</strong>
                    <p className="capitalize">{user.measurements || 0}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Orders Cut</strong>
                    <p className="capitalize">{user.cutting || 0}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">QA/QC Orders</strong>
                    <p className="capitalize">{user.qa_qc || 0}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Orders Tailored</strong>
                    <p className="capitalize">{user.tailoring || 0}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Driving Orders</strong>
                    <p className="capitalize">{user.driving || 0}</p>
                  </span>
                </div>
                <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">
                    Measurement Averages
                  </caption>
                  <thead className="text-khamco-primary font-bold">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2 bg-[#ddd]">Seconds</td>
                      <td className="text-center p-2 bg-[#ddd]">Minutes</td>
                      <td className="text-center p-2 bg-[#ddd]">Hours</td>
                      <td className="text-center p-2 bg-[#ddd]">Days</td>
                      <td className="text-center p-2 bg-[#ddd]">Weeks</td>
                      <td className="text-center p-2 bg-[#ddd]">Months</td>
                      <td className="text-center p-2 bg-[#ddd]">Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2">
                        {user.measuring_duration?.seconds ??
                          0 / (user.measurements || 0)}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration?.minutes ??
                          0 / (user.measurements || 0)}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration?.hours ??
                          0 / (user.measurements || 0)}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration?.days ??
                          0 / (user.measurements || 0)}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration?.weeks ??
                          0 / (user.measurements || 0)}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration?.months ??
                          0 / (user.measurements || 0)}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration?.years ??
                          0 / (user.measurements || 0)}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailorDetails;
