import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import MeasurementsForm from "./MeasurementsForm";
import Footer from "../../../components/Footer";
import { toast } from "react-toastify";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const EditMeasurement = () => {
  const { id } = useParams();
  const { measurementId } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const [measurement, setMeasurement] = useState(null);
  const userLanguage = useStoreState((state) => state.userLanguage);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamco-shop-backend.onrender.com/customers";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [dishdashaLength, setDishdashaLength] = useState(0);
  const [shirtType, setShirtType] = useState("");
  const [garmentLength, setGarmentLength] = useState(0);
  const [pantsLength, setPantsLength] = useState(0);
  const [shortsLength, setShortsLength] = useState(0);
  const [dressLength, setDressLength] = useState(0);
  const [collar, setCollar] = useState(0);
  const [shoulders, setShoulders] = useState(0);
  const [sleeve, setSleeve] = useState(0);
  const [shirtSleeve, setShirtSleeve] = useState(0);
  const [dressSleeve, setDressSleeve] = useState(0);
  const [sleepwearSleeve, setSleepwearSleeve] = useState(0);
  const [chest, setChest] = useState(0);
  const [belly, setBelly] = useState(0);
  const [waist, setWaist] = useState(0);
  const [hips, setHips] = useState(0);
  const [armHole, setArmHole] = useState(0);
  const [bicep_tricep, setBicepTricep] = useState(0);
  const [elbow, setElbow] = useState(0);
  const [wrist, setWrist] = useState(0);
  const [inseam, setInseam] = useState(0);
  const [thigh, setThigh] = useState(0);
  const [calf, setCalf] = useState(0);
  const [leg_foot, setLegFoot] = useState(0);
  const [backWidth, setBackWidth] = useState(0);
  const [backHeight, setBackHeight] = useState(0);
  const [specialOrder, setSpecialOrder] = useState("");
  const [alterations, setAlterations] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/edit/${id}/measurements/${measurementId}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setMeasurement(response.data.measurement);
          document.title = `Editing Measurement - ${response.data.measurement._id}`;
        })
        .catch((error) => {
          toast.error(error.response?.data?.message || error.message);
          navigate(
            `/customers/${customer._id}/measurements/${measurement._id}`
          );
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, userLanguage]);

  const handleSubmit = async (event) => {
    setLoading(true);
    event.preventDefault();
    try {
      const response = await axios.put(
        `${apiUrl}/${id}/measurements/${measurementId}`,
        {
          date_created: date,
          description: description,
          clothing_options: clothingOptions,
          waist: waist,
          natural_waist: naturalWaist,
          garment_length: garmentLength,
          half_across: halfAcross,
          chest: chest,
          trouser_waist_position: trouserWaistPosition,
          seat: seat,
          neck: neck,
          shoulder: shoulder,
          sleeve_length: sleeveLength,
          side_seam_length: sideSeamLength,
          body_rise: bodyRise,
          inside_leg: insideLeg,
          wrist: wrist,
          top_arm: topArm,
          scye_depth: scyeDepth,
          arm_sleeve_hole: armSleeveHole,
          frontal_collar: frontalCollar,
          hip: hip,
          front_rise: frontRise,
          back_rise: backRise,
          thigh: thigh,
          knee: knee,
          hem: hem,
          outseam: outseam,
          foot_size: footSize,
          head_size: headSize,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );

      toast.success(response.data.message);
      setCustomer(response.data.customer);
      navigate(
        `/customers/${response.data.customer._id}/measurements/${response.data.measurement._id}`
      );
      setLoading(false);
    } catch (error) {
      setLoading(false);
      if (error.response) {
        const { status, data } = error.response;

        if (status === 404) {
          if (data.message === "Customer not found") {
            toast.error("Customer not found");
          } else if (data.message === "Dependent not found") {
            toast.error("Dependent not found");
          } else if (data.message === "Date is required") {
            toast.error("The date field is required");
          } else {
            toast.error("Resource not found");
          }
        } else if (status === 400) {
          toast.error("Invalid ObjectId format");
        } else {
          toast.error("An error occurred while updating dependent details");
        }
      } else {
        toast.error("An error occurred while updating dependent details");
      }
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section className="khamco-wrapper" dir={writingDirection}>
          {customer && measurement && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer && customer._id}/measurements`} className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i> Measurements
                </Link>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">{`Edit Measurements for ${new Date(
                measurement.date_created
              ).toLocaleDateString("en-US", {
                month: "long",
                day: "numeric",
                year: "numeric",
              })}`}</h1>
            </>
          )}
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">Enter New Date</label>
              <input className="p-2 border-2 border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="date"
                onChange={(e) => {
                  setDate(e.target.value);
                }}
              />
            </div>
            {measurement && measurement.clothing_options.length > 0 && (
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">Previously Selected Clothing</h2>
                <div className="flex gap-4 w-full justify-center flex-wrap my-[2rem]">
                  {measurement.clothing_options.map((item) => (
                    <span className="bg-white text-khamco-primary border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
                      {item.clothing_item}
                    </span>
                  ))}
                </div>
              </div>
            )}
            <MeasurementsForm
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              setDishdashaLength={setDishdashaLength}
              shirtType={shirtType}
              setShirtType={setShirtType}
              setGarmentLength={setGarmentLength}
              setPantsLength={setPantsLength}
              setShortsLength={setShortsLength}
              setDressLength={setDressLength}
              setCollar={setCollar}
              setShoulders={setShoulders}
              setSleeve={setSleeve}
              setShirtSleeve={setShirtSleeve}
              setDressSleeve={setDressSleeve}
              setDescription={setDescription}
              setSleepwearSleeve={setSleepwearSleeve}
              setChest={setChest}
              setBelly={setBelly}
              setWaist={setWaist}
              setHips={setHips}
              setArmHole={setArmHole}
              setBicepTricep={setBicepTricep}
              loading={loading}
              areas={areaNames}
              setElbow={setElbow}
              setWrist={setWrist}
              setInseam={setInseam}
              setThigh={setThigh}
              setCalf={setCalf}
              setLegFoot={setLegFoot}
              setBackWidth={setBackWidth}
              setBackHeight={setBackHeight}
              setSpecialOrder={setSpecialOrder}
              setAlterations={setAlterations}
              setNotes={setNotes}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditMeasurement;
