import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";

const AdminDashboard = () => {
  const serverUrl = "http://localhost:5000/admin/dashboard";
  const [availableFabric, setAvailableFabric] = useState(0);
  const [averageServiceTime, setAverageServiceTime] = useState({
    days: 0.00,
    hours: 0
  });
  const [customers, setCustomers] = useState([{}]);
  const [invoiceData, setInvoiceData] = useState([]);
  const [message, setMessage] = useState("Hello World");
  const [totalRevenue, setTotalRevenue] = useState(null);
  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(serverUrl)
        .then((response) => {
          setAvailableFabric(response.data.available_fabric);
          setAverageServiceTime(response.data.average_service_time);
          setCustomers(response.data.customers);
          setInvoiceData(response.data.invoice_data);
          setMessage(response.data.message);
          setTotalRevenue(response.data.total_revenue);
        })
        .catch((error) => {
          console.error(error);
        });
    };

    fetchData();
  }, []);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <AdminPanel />
        <section className="admin-dashboard font-arial">
          <h1 className="title">Admin Dashboard</h1>
          <p className="message">{message}</p>
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            {customers && (
              <div className="metric">
                <h2 className="text-khamco-secondary text-2xl font-bold">
                  Total Customers
                </h2>
                <h2 className="text-xl font-semibold text-green-500">
                  {customers.length}
                </h2>
                <Link to="/admin/customers_data" className="edit-btn">
                  Details
                </Link>
              </div>
            )}
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Revenue Generated
              </h2>
              <h2 className="text-xl font-semibold text-green-500">
                KWD {new Intl.NumberFormat("en-US").format(totalRevenue)}
              </h2>
              <Link to="/admin/revenue" className="edit-btn">
                Details
              </Link>
            </div>
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Total Fabric
              </h2>
              <h2 className="text-xl font-semibold text-green-500">
                {availableFabric} meters
              </h2>
              <Link to="/admin/fabrics" className="edit-btn">
                Details
              </Link>
            </div>
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Average Service Time
              </h2>
              <h2 className="text-xl font-semibold text-green-500">{`${
                averageServiceTime.days && averageServiceTime.days.toFixed(2)
              } day(s), ${
                averageServiceTime.hours && averageServiceTime.hours.toFixed(2)
              } hours`}</h2>
              <Link to="/admin/service-times" className="edit-btn">
                Details
              </Link>
            </div>
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Invoice Data
              </h2>
              <h2 className="text-xl font-semibold text-green-500">{`${
                invoiceData.length ? invoiceData[0].total : 0
              } created`}</h2>
              <h2 className="text-xl font-semibold text-red-500">{`${
                invoiceData.length ? invoiceData[0].sum : 0
              } deleted`}</h2>
              <Link to="/admin/invoice_data" className="edit-btn">
                Details
              </Link>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminDashboard;
