import { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";
import MeasurementList from "../measurements/MeasurementList";

const DependentsDetails = () => {
  const { id } = useParams();
  const { dependentId } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [customer, setCustomer] = useState();
  const [dependent, setDependent] = useState();
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/dependents/${dependentId}`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/dependents/${dependentId}`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    document.title = "Dependent Details";

    const fetchData = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        if(isMounted){
          setCustomer(response.data.customer);
          setDependent(response.data.dependent);
          setLoading(false);
        }
      } catch (error) {
        
      }
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setDependent(response.data.dependent);
          setLoading(false);
        })
        .catch((error) => {
          setLoading(true);
          toast.error(error.message);
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, userLanguage]);

  const handleDelete = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.delete(apiUrl, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate(`/customers/${response.data.customer._id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };
  return (
    <>
      <Navbar />
      <main>
        <h1 className="title">Customer Details</h1>
        <section className="customer-details" dir={writingDirection}>
          {loading &&
            new Array(3).fill(
              <div
                className="w-full bg-[#ccc] animate-pulse py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                dir={writingDirection}
              ></div>
            )}
          {customer && dependent && (
            <>
              <div className="utilities">
                <Link to={`/customers/${customer._id}`}>
                  <i className="fas fa-arrow-left"></i> Customer {customer.name}{" "}
                  {customer.surname}
                </Link>
              </div>
              <div className="khamco-card">
                <div className="utilities">
                  <Link
                    to={`/customers/edit/${customer._id}/dependents/${dependent._id}`}
                    className="edit-btn"
                  >
                    Edit
                  </Link>
                  <form onSubmit={handleDelete}>
                    <button type="submit" className="delete-btn">
                      Delete
                    </button>
                  </form>
                  <button className="print-btn" onClick={() => window.print()}>
                    Print <i className="fas fa-print"></i>
                  </button>
                </div>
                <div className="flex flex-col gap-[1rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Dependent of</strong>
                    <p>
                      {customer.name} {customer.surname}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Customer ID</strong>
                    <p>{dependent._id}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Name</strong>
                    <p>
                      {dependent.name} {dependent.surname}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Mobile</strong>
                    <a href={`tel:${dependent.phone}`}>{dependent.phone}</a>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Last Measurement</strong>
                    <p>
                      {new Date(dependent.date_created).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Address</strong>
                    <p>{customer.address}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Email</strong>{" "}
                    <a href={`mailto:${dependent.email}`}>{dependent.email}</a>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Status</strong>
                    {dependent.military_status}
                  </span>
                </div>
                {dependent.military_idnumber && (
                  <>
                    <div className="flex flex-col gap-[1rem]">
                      <h2 className="title">Military Details</h2>
                    </div>
                    <div className="flex flex-col gap-[1rem]">
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>ID Number</strong>
                        <p>{dependent.military_idnumber}</p>
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>Rank</strong>
                        <p>{dependent.military_rank}</p>
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>Department</strong>
                        <p>{dependent.military_department}</p>
                      </span>
                    </div>
                  </>
                )}
                <div className="flex flex-col gap-[1rem]">
                  <h2 className="title">Measurements</h2>
                </div>
                {dependent.measurements && dependent.measurements.length > 0 ? (
                  dependent.measurements.map((measurement, index) => (
                    <MeasurementList key={index} measurement={measurement} />
                  ))
                ) : (
                  <p className="text-center">
                    Dependent does not have any measurements.
                  </p>
                )}
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default DependentsDetails;
