import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { useStoreState } from "easy-peasy";
import { toast } from "react-toastify";
import { applyAnimation } from "../../utilities";

const Orders = () => {
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();
  const user = useStoreState((state) => state.user);

  const fetchOrders = async (queryParams = "") => {
    try {
      const response = await axios.get(`${apiUrl}/orders${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });

      setOrders(response.data.orders);
      setIsLoading(false);
      setDisplay(true);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
      setIsLoading(false);
      setDisplay(true);
      setOrders([]);
    }
  };

  useEffect(() => {
    document.title = "Orders - Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchOrders(location.search);
  }, [location.search, userLanguage]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  const handleOrderCreation = async (e) => {
    e.preventDefault();
    await axios
      .post(
        `${apiUrl}/orders/new-order`,
        { date: new Date() },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        navigate(`/orders/new-order/${response.data.order._id}`);
      })
      .catch(() => {
        toast.error("There was an error creating your order");
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              className="text-[blue] no-underline hover:underline cursor-pointer"
              onClick={handleOrderCreation}
            >
              New Order <i className="fas fa-plus"></i>
            </Link>
            <Link
              className="text-[blue] no-underline hover:underline cursor-pointer"
              to={`/my-orders/${user.id}`}
            >
              My Orders <i className="fas fa-user"></i>
            </Link>
          </div>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="flex justify-evenly gap-[2rem] items-center my-[2rem] m-auto flex-wrap w-full bg-[#888] p-[3rem] shadow-md shadow-[#333] rounded-[0.5rem] animate-pulse"
                  dir={writingDirection}
                ></div>
              ))}
          {display && (
            <>
              <h1 className="text-center text-[2rem] font-minimoBold">
                All Orders ({orders.length || 0})
              </h1>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <input
                    type="text"
                    placeholder="Search Customer Name"
                    name="customer_name"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  />
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <input
                    type="text"
                    placeholder="Search Customer Phone Number"
                    name="customer_phone"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  />
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <input
                    type="date"
                    name="order_date"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  />
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-check"></i>
                  </button>
                </form>
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <select
                    name="order_status"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  >
                    <option value="">-- Search Order Status --</option>
                    <option value="Accepted">Accepted</option>
                    <option value="In Progress">In Progress</option>
                    <option value="Complete">Complete</option>
                    <option value="Awaiting Delivery">Awaiting Delivery</option>
                    <option value="Delivered">Delivered</option>
                  </select>
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-search"></i>
                  </button>
                </form>
              </div>
              {orders && orders.length > 0 ? (
                orders.map((order, index) => (
                  <div
                    key={index}
                    className="flex justify-evenly gap-[2rem] items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem]"
                    dir={writingDirection}
                  >
                    <p className="capitalize">
                      <strong>Customer Name</strong>:{" "}
                      {`${order.customer_name} ${order.customer_surname}`}
                    </p>
                    {order.order_start_time && (
                      <p className="capitalize">
                        <strong>Order Start Date</strong>:{" "}
                        {new Date(order.order_start_time).toLocaleDateString(
                          "en-us",
                          {
                            month: "long",
                            year: "numeric",
                            day: "numeric",
                          }
                        )}
                      </p>
                    )}
                    <p className="capitalize">
                      <strong>Order Status</strong>:{" "}
                      <span
                        className={`py-[0.2rem] px-[0.5rem] rounded-[0.7rem] ${applyAnimation(
                          order.order_status
                        )}`}
                      >
                        {order.order_status}
                      </span>
                    </p>
                    <Link
                      to={`/orders/${order._id}`}
                      className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                    >
                      View
                    </Link>
                  </div>
                ))
              ) : (
                <p className="text-center">No orders available.</p>
              )}
            </>
          )}
        </section>
