import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import UserForm from "./UserForm";
import Footer from "../../components/Footer";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const EditUser = () => {
  const { id } = useParams();
  const [checkMaster, setCheckMaster] = useState(null);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/admin/users/${id}`
    : `https://khamco-shop-backend.onrender.com/admin/users/${id}`;
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [language, setLanguage] = useState("");
  const [userPhoto, setUserPhoto] = useState("");
  const [email, setEmail] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "Edit User Details - Khamco Shop";
    
    const fetchUserData = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setFirstName(response.data.user.name);
        setLastName(response.data.user.surname);
        setRole(response.data.user.role);
        setLanguage(response.data.user.language);
        setEmail(response.data.user.email);
        document.title = `Edit User Details - ${response.data.user.name}`;
      } catch (error) {
        if (error.response) {
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        }
      }
    };

    fetchUserData();
  }, [apiUrl, authKey, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (oldPassword && !password) {
      setLoading(false);
      toast.info("Please enter a new password");
      return;
    }

    if (password !== confirmPassword) {
      setLoading(false);
      toast.info("New passwords do not match");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("name", firstName);
      formData.append("surname", lastName);
      formData.append("email", email);
      formData.append("role", role);
      formData.append("language", language);
      formData.append("old_password", oldPassword);
      formData.append("password", password);
      formData.append("password2", confirmPassword);

      // Append the photo file if it exists
      if (userPhoto) {
        formData.append("picture`", userPhoto);
      }

      const response = await axios.put(apiUrl, formData, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate(`/admin/users/${response.data.user._id}`);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      if (error.response) {
        if (error.response.status === 403) {
          toast.warn("You are not authorized");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      }
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section>
          <h1 className="text-center text-[2rem] font-minimoBold">
            Edit Tailor Details
          </h1>
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <UserForm
              page="edit"
              checkMaster={checkMaster}
              authorizedUser={authorizedUser}
              loading={loading}
              setFirstName={setFirstName}
              setLastName={setLastName}
              setRole={setRole}
              setLanguage={setLanguage}
              setUserPhoto={setUserPhoto}
              setEmail={setEmail}
              setOldPassword={setOldPassword}
              setPassword={setPassword}
              setConfirmPassword={setConfirmPassword}
              firstName={firstName}
              lastName={lastName}
              role={role}
              language={language}
              email={email}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditUser;
