import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import ServiceForm from "./ServiceForm";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";

const EditService = () => {
  const { id } = useParams();
  const [service, setService] = useState({});
  const [price, setPrice] = useState("");
  const [date, setDate] = useState(null);
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/services/${id}`
    : `https://khamco-shop-backend.onrender.com/services/${id}`;

  useEffect(() => {
    document.title = "Edit Service Details - Khamco Shop";

    const fetchService = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setService(response.data.service.service);
        setPrice(response.data.service.price);
        setDate(response.data.service.date);
      } catch (error) {
        setService({});
        toast.error(error.message);
      }
    };

    fetchService();
  }, [apiUrl, authKey]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    if (!service || !price || !date) {
      toast.warn("Please enter all fields.");
      setLoading(false);
      return;
    }
    await axios
      .put(
        apiUrl,
        {
          service: service,
          price: price,
          date: date,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        navigate("/services");
        setLoading(false);
      })
      .catch((error) => {
        toast.error(error.message);
        setLoading(false);
      });
  };
  
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section className="p-[4rem] grid justify-center items-center">
          <div className="row">
            <Link to="/services">
              <i className="fas fa-arrow-left"></i> Services
            </Link>
          </div>
          <h1 className="title">Edit Service</h1>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <ServiceForm
              setService={setService}
              setPrice={setPrice}
              setDate={setDate}
              service={service}
              price={price}
              date={date}
              loading={loading}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditService;
