import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import FormFields from "./FormFields";
import { Link } from "react-router-dom";

const Edit = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const product = {
    id: "asd453asd",
    name: "Vans",
  };
  const [message, setMessage] = useState("Hello World");
  return (
    <>
      <Navbar />
      <main>
        <section className="p-[4rem] grid justify-center items-center" dir={writingDirection}>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to={`/products/${product.id}`}>
              <i className="fas fa-arrow-left"></i> {product.name}
            </Link>
          </div>
          <h1 className="title">Edit Product Details</h1>
          <p className="message">{message}</p>
          <form action="/products/<%= product.id %>?_method=PUT" method="POST">
            <FormFields />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Edit;
