import { Link, useParams } from "react-router-dom";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";

const UserProfile = () => {
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState({});
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin"
    : "https://khamco-shop-backend.onrender.com/admin";
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    let isMounted = true;

    document.title = "Profile Management";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/users/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setIsLoading(false);
            setUser(response.data.user);
            document.title = `Profile Management - ${response.data.user.name}`;
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            toast.error(error.response?.data?.message || error.message);
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, id, serverUrl]);

  return (
    <>
      <Navbar />
      <main>
        <section className="admin-dashboard font-arial">
          <h1 className="title">My Dashboard</h1>
          {isLoading && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}
          {user && (
            <>
              <table>
                <thead>
                  <tr>
                    <td>Name</td>
                    <td>Surname</td>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{`${user.name} ${user.surname}` }</td>
                    <td></td>
                  </tr>
                </tbody>
              </table>
              <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] my-[2rem] m-auto">
                <div className="metric">
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    User Details
                  </h2>
                  <Link to={`/admin/users/${id}`} className="edit-btn">
                    Details
                  </Link>
                </div>
                <div className="metric">
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    My Orders
                  </h2>
                  <Link to={`/my-orders/${id}`} className="edit-btn">
                    Details
                  </Link>
                </div>
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default UserProfile;
