import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";
import { applyAnimation } from "../../utilities";
import { formatDistanceToNow } from "date-fns";

const OrderDetails = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [openDialog, setOpenDialog] = useState(false);
  const [openComment, setOpenComment] = useState(false);
  const [order, setOrder] = useState({});
  const [invoice, setInvoice] = useState({});
  const [customer, setCustomer] = useState({});
  const [users, setUsers] = useState([]);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [tailor, setTailor] = useState("");
  const [driver, setDriver] = useState("");
  const [orderStatus, setOrderStatus] = useState("");
  const [loading, setLoading] = useState(true);
  const [comments, setComments] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamco-shop-backend.onrender.com`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Details - Khamco Shop";

    const fetchOrder = async () => {
      try {
        const response = await axios.get(`${apiUrl}/orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setLoading(false);
          setOrder(response.data.order);
          setInvoice(response.data.invoice);
          setCustomer(response.data.customer);
          setUsers(response.data.users);
          setAuthorizedUser(
            `${response.data.authorizedUser.name} ${response.data.authorizedUser.surname}`
          );
          setUserRole(response.data.authorizedUser.role);
        }
      } catch (error) {
        if (isMounted) {
          navigate("/orders");
          toast.error(error.response?.data?.message || error.message);
        }
      }
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchOrder();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, navigate, userLanguage]);

  const deleteEntireOrder = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/orders");
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to do that");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    }
  };

  const changeOrderStatus = async (e) => {
    e.preventDefault();
    let orderStatus;
    switch (userRole) {
      case "Administrator":
        orderStatus = "in_progress";
        break;
      case "Measurer":
        orderStatus = "complete";
        break;
      case "Cutter":
        orderStatus = "awaiting";
        break;
      case "Tailor":
        orderStatus = "assign-driver";
        break;
      case "Driver":
        orderStatus = "delivered";
        break;
      default:
        break;
    }

    if (userRole === "Administrator" && !measurer) {
      toast.warn("Please select a Measurer from the list");
      return;
    }

    if (userRole === "Measurer" && !cutter) {
      toast.warn("Please select a Cutter from the list");
      return;
    }

    if (userRole === "Cutter" && !tailor) {
      toast.warn("Please select a Tailor from the list");
      return;
    }

    if (userRole === "Tailor" && !driver) {
      toast.warn("Please select a Driver from the list");
      return;
    }

    try {
      const response = await axios.put(
        `${apiUrl}/orders/${order._id}/${orderStatus}`,
        {
          id: order._id,
          order_status: orderStatus,
          measurer,
          cutter,
          tailor,
          driver,
          comments,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      setOrder(response.data.order);
      setOpenDialog(false);
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to do that");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        toast.error("An error occurred: " + error.message);
      }
    }
  };

  const submitComment = async (e) => {
    e.preventDefault();
    setLoading(true);

    await axios
      .put(
        `${apiUrl}/orders/comments/${order._id}`,
        {
          comments: comments,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setLoading(false);
        setOrder(response.data.order);
        setOpenComment(false);
      })
      .catch((error) => {
        console.error(error);
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section className="customer-details" dir={writingDirection}>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/orders"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> All Orders
            </Link>
          </div>
          {loading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(6).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[1.8rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-2/4 bg-[#666] animate-pulse m-auto h-[100vh] my-[2rem]"></div>
                </div>
              </div>
            </>
          )}
          {order && customer && users && invoice && (
            <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => window.print()}
                >
                  Print <i className="fas fa-print"></i>
                </button>
                <form onSubmit={deleteEntireOrder}>
                  <button
                    type="submit"
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  >
                    Delete <i className="fas fa-times"></i>
                  </button>
                </form>
                <button
                  type="submit"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[black] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => setOpenDialog(true)}
                >
                  Assign <i className="fas fa-user-plus"></i>
                </button>
                <button
                  type="submit"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[green] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => setOpenComment(true)}
                >
                  Comment <i className="fas fa-message"></i>
                </button>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold flex items-center justify-center gap-4">
                Order Details{" "}
                <span
                  className={`p-1 text-lg rounded-xl ${applyAnimation(
                    order.order_status
                  )}`}
                >
                  {order.order_status}
                </span>
              </h1>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Date:</strong>
                  <p>
                    {new Date(order.order_start_time).toLocaleDateString(
                      "en-us",
                      { month: "long", year: "numeric", day: "numeric" }
                    )}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Customer ID:</strong>
                  <Link
                    to={`/customers/${customer._id}`}
                    target="_blank"
                    className="text-[blue] no-underline hover:underline cursor-pointer"
                  >
                    {order.customer_id}
                  </Link>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <Link
                    className="text-[blue] no-underline hover:underline cursor-pointer"
                    to={`/customers/${customer._id}/measurements`}
                    target="_blank"
                  >
                    View Customer Measurements{" "}
                    <i className="fas fa-external-link"></i>
                  </Link>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Invoice/Order ID:</strong>
                  <p>{order.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <Link
                    to={`/invoices/${invoice._id}`}
                    target="_blank"
                    className="text-[blue] no-underline hover:underline cursor-pointer"
                  >
                    View Invoice {order.uniqueId}{" "}
                    <i className="fas fa-external-link"></i>
                  </Link>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Details:</strong>
                  <ol className="list-disc">
                    {order.details &&
                      order.details.map((item, index) => (
                        <li key={index}>
                          {item.name} <strong>{`X${item.quantity}`}</strong>
                        </li>
                      ))}
                  </ol>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Dependents:</strong>
                  <ul className="list-disc">
                    {order.dependents &&
                      order.dependents.map((customer, index) => (
                        <li key={index}>{customer.name || "--"}</li>
                      ))}
                  </ul>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Measurer:</strong>
                  <p>{order.measurer || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Cutter:</strong>
                  <p>{order.cutter || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Tailor:</strong>
                  <p>{order.tailor || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Driver:</strong>
                  <p>{order.driver || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Comments:</strong>
                  <div className="flex flex-col gap-[1rem]">
                    {order.comments &&
                      order.comments.map((comment) => (
                        <div className="bg-[#ddd] text-[1rem] p-[0.5rem] rounded-lg flex flex-col gap-2">
                          <span className="self-start text-khamco-secondary italic">
                            {comment.user}
                          </span>
                          <span>{comment.text}</span>
                          <span className="self-end">
                            <strong>
                              {formatDistanceToNow(
                                new Date(comment.timestamp),
                                { addSuffix: true }
                              )}
                            </strong>
                          </span>
                        </div>
                      ))}
                  </div>
                </span>

                {order.in_progress_duration &&
                  order.order_status === "In Progress" && (
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong>In Progress:</strong>
                      <p>
                        <strong>on</strong>{" "}
                        {new Date(order.in_progress_time).toLocaleDateString(
                          "en-us",
                          { month: "long", year: "numeric", day: "numeric" }
                        )}
                        <strong> after </strong>
                        {order.in_progress_duration.years} years,{" "}
                        {order.in_progress_duration.months} months,{" "}
                        {order.in_progress_duration.weeks} weeks,{" "}
                        {order.in_progress_duration.days} days,{" "}
                        {order.in_progress_duration.hours} hours,{" "}
                        {order.in_progress_duration.minutes} minutes,{" "}
                        {order.in_progress_duration.seconds} seconds.
                      </p>
                    </span>
                  )}
                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-green-500">
                      Order has been delivered.{" "}
                      <i className="fas fa-thumbs-up"></i>
                    </p>
                  </span>
                )}
                {order.order_status === "Accepted" && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform-none">
                      Order is not yet in progress.
                    </p>
                  </span>
                )}
                {order.completed_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Complete:</strong>{" "}
                    <p>
                      <strong>on </strong>
                      {new Date(order.completed_time).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                      <strong> after </strong> {order.completed_duration.years}{" "}
                      years, {order.completed_duration.months} months,{" "}
                      {order.completed_duration.weeks} weeks,{" "}
                      {order.completed_duration.days} days,{" "}
                      {order.completed_duration.hours} hours,{" "}
                      {order.completed_duration.minutes} minutes,{" "}
                      {order.completed_duration.seconds} seconds.
                    </p>
                  </span>
                )}

                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-green-500">
                      Order has been delivered.{" "}
                      <i className="fas fa-thumbs-up"></i>
                    </p>
                  </span>
                )}

                {!order.delivered_time && !order.completed_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform:none">
                      Order is not yet complete.
                    </p>
                  </span>
                )}
                {order.awaiting_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Awaiting Delivery:</strong>{" "}
                    <p>
                      <strong>on </strong>
                      {new Date(order.awaiting_time).toLocaleDateString(
                        "en-us",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                      <strong> after </strong> {order.awaiting_duration.years}{" "}
                      years, {order.awaiting_duration.months} months,{" "}
                      {order.awaiting_duration.weeks} weeks,{" "}
                      {order.awaiting_duration.days} days,{" "}
                      {order.awaiting_duration.hours} hours,{" "}
                      {order.awaiting_duration.minutes} minutes,{" "}
                      {order.awaiting_duration.second} seconds.
                    </p>
                  </span>
                )}

                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-green-500">
                      Order has been delivered.{" "}
                      <i className="fas fa-thumbs-up"></i>
                    </p>
                  </span>
                )}

                {!order.awaiting_time && !order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform:none">
                      Order is not yet ready for delivery.
                    </p>
                  </span>
                )}

                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Delivered:</strong>{" "}
                    <p>
                      <strong>on </strong>
                      {new Date(order.delivered_time).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                      <strong> after </strong> {order.delivered_duration.years}{" "}
                      years, {order.delivered_duration.months} months,{" "}
                      {order.awaiting_duration.weeks} weeks,{" "}
                      {order.delivered_duration.days} days,{" "}
                      {order.delivered_duration.hours} hours,{" "}
                      {order.delivered_duration.minutes} minutes,{" "}
                      {order.delivered_duration.seconds} seconds.
                    </p>
                  </span>
                )}
                {!order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform-none">
                      Order has not been delivered.
                    </p>
                  </span>
                )}
                {order.order_end_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Order End Date:</strong>
                    <p>
                      {new Date(order.order_end_time).toLocaleDateString(
                        "en-us",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                    </p>
                  </span>
                )}
                {order.order_end_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Order Duration:</strong>
                    <p>
                      {order.order_duration.years} years,{" "}
                      {order.order_duration.months} months,{" "}
                      {order.order_duration.weeks} weeks,{" "}
                      {order.order_duration.days} days,{" "}
                      {order.order_duration.hours} hours,{" "}
                      {order.order_duration.minutes} minutes,{" "}
                      {order.order_duration.seconds} seconds.
                    </p>
                  </span>
                )}
                {!order.order_end_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform:none">
                      Order is still under <strong>{order.order_status}</strong>{" "}
                      status. The duration will be calculated after delivery.
                    </p>
                  </span>
                )}
              </div>
            </div>
          )}
        </section>
        <dialog
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openDialog}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={changeOrderStatus}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenDialog(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  Update Order Details
                </h2>
              </div>
              {userRole === "Driver" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Order Status{" "}
                    {order.order_status ? (
                      <span
                        className={`p-1 ${applyAnimation(order.order_status)}`}
                      >
                        {order.order_status}
                      </span>
                    ) : (
                      ""
                    )}
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setOrderStatus(e.target.value)}
                  >
                    <option label="-- Select order status --"></option>
                    <option value="Accepted" label="Accepted"></option>
                    <option value="In Progress" label="In Progress"></option>
                    <option value="Complete" label="Complete"></option>
                    <option
                      value="Awaiting Delivery"
                      label="Awaiting Delivery"
                    ></option>
                    <option value="Delivered" label="Delivered"></option>
                  </select>
                </div>
              )}
              {userRole === "Administrator" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label
                    className="text-lg my-2 select-none self-start font-medium"
                    htmlFor="measurer"
                  >
                    Measurer
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setMeasurer(e.target.value)}
                  >
                    <option label="-- Select the Measurer--" value=""></option>
                    {users &&
                      users.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              {userRole === "Measurer" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Cutter
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setCutter(e.target.value)}
                  >
                    <option label="-- Select the Cutter --" value=""></option>
                    {users &&
                      users.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              {userRole === "Cutter" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    {" "}
                    Tailor
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setTailor(e.target.value)}
                  >
                    <option label="-- Select the Tailor--" value=""></option>
                    {users &&
                      users.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              {userRole === "Tailor" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    {" "}
                    Driver
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setDriver(e.target.value)}
                  >
                    <option label="-- Select the Driver--" value=""></option>
                    {users &&
                      users.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Assign
                </button>
              </div>
            </form>
          </div>
        </dialog>
        <dialog
          className="fixed z-50 top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-screen md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openComment}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={submitComment}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenComment(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  Add a Comment
                </h2>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Comments
                </label>
                <textarea
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  placeholder="Write a comment"
                  onChange={(e) =>
                    setComments({
                      user: authorizedUser,
                      text: e.target.value,
                      timestamp: new Date(),
                    })
                  }
                ></textarea>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Update
                </button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default OrderDetails;
