import React, { useEffect } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Invoices = () => {
  const [message, setMessage] = useState("Hello World");
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [invoices, setInvoices] = useState([
    {
      _id: "asdjnsa57",
      customer_name: "John",
      customer_surname: "Doe",
      date: new Date(),
    },
    {
      _id: "asdjnsa57",
      customer_name: "John",
      customer_surname: "Doe",
      date: new Date(),
    },
    {
      _id: "asdjnsa57",
      customer_name: "John",
      customer_surname: "Doe",
      date: new Date(),
    },
  ]);
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/products"
    : "https://khamcotailor.com/products";
  const authKey = localStorage.getItem("jwt");
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  const getInvoices = async (queryParams = "") => {
    try {
      const response = await axios.get(`${serverUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });

      setIsLoading(false);
      if (response.data.products) {
        setInvoices(response.data.products);
      } else {
        setInvoices([]);
      }
    } catch (error) {
      toast.error(error.message);
      setInvoices([]);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = "Invoices - Khamco Shop";
    getInvoices(location.search);
  }, [location.search]);

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          id="invoice-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="utilities">
            <Link to="/invoices/new">
              New Invoice <i className="fas fa-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          <div className="utilities">
            <form action="/invoices" method="GET" className="search-engine">
              <input
                type="text"
                placeholder="Search Customer Name"
                name="customer_name"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form action="/invoices" method="GET" className="search-engine">
              <input
                type="text"
                placeholder="Search Invoice ID"
                name="uniqueId"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              action="/invoices"
              method="GET"
              className="search-engine"
              id="date-form"
            >
              <input
                type="date"
                name="date"
                id="date-search"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form action="/invoices" method="GET" className="search-engine">
              <select name="payment_status" className="search-input">
                <option selected disabled>
                  -- Search Payment Status --
                </option>
                <option value="Unpaid">Unpaid</option>
                <option value="Partially Paid">Partially Paid</option>
                <option value="Paid">Paid</option>
                <option value="Other">Other</option>
              </select>
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form action="/invoices" method="GET" className="search-engine">
              <select
                name="payment_type"
                id="payment_type"
                className="search-input"
              >
                <option value="knet">KNet</option>
                <option value="cash">Cash</option>
                <option value="other">Other</option>
              </select>
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="title">Invoices</h1>
          {isLoading &&
            new Array(3).fill(
              <div
                className="w-full bg-[#ccc] animate-pulse py-[6rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                dir={writingDirection}
              ></div>
            )}
          {invoices.length > 0 ? (
            invoices.map((invoice, index) => (
              <div
                className="flex w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem]"
                dir="auto"
                key={index}
              >
                <p className="capitalize">
                  <strong>Name</strong>: {invoice.customer_name}
                  {invoice.customer_surname}
                </p>
                <p className="capitalize">
                  <strong>Last Measurement</strong>:{" "}
                  {new Date(invoice.date).toLocaleDateString("en-us", {
                    month: "long",
                    year: "numeric",
                    day: "numeric",
                  })}
                </p>
                <Link
                  to={`/invoices/${invoice._id}`}
                  className="edit-btn no-underline"
                >
                  View
                </Link>
              </div>
            ))
          ) : (
            <p className="empty">No invoice data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Invoices;
