import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const OrderForm = () => {
  const [users, setUsers] = useState([]);
  const [customers, setCustomers] = useState([]);
  const order = {
    order_notes:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam dolores ad quam minus vitae cum dolorem reprehenderit impedit nostrum praesentium?",
  };
  const devBaseUrl = "http://localhost:5000";
  const prodBaseUrl = "https://www.khamcotailor.com";

  const currentBaseUrl =
    window.location.hostname === "localhost" ? devBaseUrl : prodBaseUrl;

  const apiURL = `${currentBaseUrl}/api/customer-array`;

  useEffect(() => {
    const getCustomerArray = async () => {
      await axios
        .get(apiURL)
        .then((response) => {
          setCustomers(response.data.customers);
        })
        .catch((error) => {
          toast.error(error.message);
        });
    };

    const getAllUsers = async() => {
      await axios.get(`${apiURL}/admin/tailors`, )
    }

    getCustomerArray();
  }, [apiURL]);

  return (
    <>
      <input
        type="text"
        name="order_name"
        id="order_name_hidden"
        placeholder="Order Name"
        className="hidden"
      />
      <input
        type="text"
        name="customer_name"
        id="customer_name_hidden"
        placeholder="Customer Name"
        className="hidden"
      />
      <input
        type="text"
        name="dependent_name"
        id="dependent_hidden"
        placeholder="Dependent"
        className="hidden"
      />
      <div className="form-row">
        <label for="customer_surname">Customer Surname</label>
        <input
          type="text"
          name="customer_surname"
          id="customer_surname"
          placeholder="Customer surname"
          readonly
        />
      </div>
      <div className="form-row">
        <label for="customer_email">Email</label>
        <input
          type="email"
          name="customer_email"
          id="customer_email"
          readonly
          placeholder="Customer email"
        />
      </div>
      <div className="form-row">
        <label for="customer_phone">Phone Number</label>
        <input
          type="tel"
          name="customer_phone"
          id="customer_phone"
          readonly
          placeholder="Customer phone number"
        />
      </div>
      <div className="form-row">
        <label for="customer_address">Address</label>
        <input
          type="text"
          name="customer_address"
          id="customer_address"
          placeholder="Customer address"
          readonly
        />
      </div>
      <div className="form-row">
        <label for="measurer">Measurer</label>
        <select name="measurer" id="measurer">
          {users.map((user) => (
            <option
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label for="cutter">Cutter</label>
        <select name="cutter" id="cutter">
          {users.map((user) => (
            <option
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label for="sewer">Sewer</label>
        <select name="sewer" id="sewer">
          {users.map((user) => (
            <option
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label for="order_status">Order Status</label>
        <select name="order_status" id="order_status">
          <option value="Accepted" label="Accepted"></option>
          <option value="In Progress" label="In Progress"></option>
          <option value="Complete" label="Complete"></option>
          <option value="Awaiting Delivery" label="Awaiting Delivery"></option>
          <option value="Delivered" label="Delivered"></option>
        </select>
      </div>
      <div className="form-row">
        <label for="order_start_time">Order Start Date</label>
        <input
          type="datetime-local"
          name="order_start_time"
          id="order_start_time"
          value="<%= order.start_time == null ? "
        />
      </div>
      <div className="form-row">
        <label for="order_notes">Other Details</label>
        <textarea
          name="order_notes"
          id="order_notes"
          placeholder="Optional notes"
        >
          {order.order_notes}
        </textarea>
      </div>
      <div className="form-row">
        <button type="submit">Submit</button>
      </div>
    </>
  );
};

export default OrderForm;
