import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import AdminPanel from "../../../components/AdminPanel";
import Footer from "../../../components/Footer";
import { Link, useParams } from "react-router-dom";
import { Buffer } from "buffer";
import axios from "axios";

const AdminProductsView = () => {
  const { id } = useParams();
  // const id = "65cb6045375f981ed80b0f7d";
  const [message, setMessage] = useState("");
  const [product, setProduct] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/admin/products/${id}`
    : `https://khamco-shop-backend.onrender.com/admin/products/${id}`;
  const [openDialog, setOpenDialog] = useState(false);
  const [display, setDisplay] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    document.title = "Admin Product Details - Khamco Shop";
    const fetchData = async () => {
      await axios
        .get(apiUrl)
        .then((response) => {
          console.log(response.data);
          setProduct(response.data.product);
          setIsLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          setDisplay(false);
            setIsLoading(true);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
        });
    };

    return () => {
      fetchData();
    };
  }, [apiUrl]);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="customer-details">
          <div className="utilities">
            <p className="message">{message}</p>
            <Link href="/admin/products">
              <i className="fas fa-arrow-left"></i> All Products
            </Link>
          </div>
          {isLoading && <h1 className="title">Loading...</h1>}
          {display && (
            <div className="khamco-card">
              <div className="utilities">
                <button
                  className="edit-btn"
                  onClick={() => setOpenDialog(true)}
                >
                  EditButton
                </button>
                <form
                  action="/products/<%= product.id %>?_method=DELETE"
                  method="POST"
                  className="d-inline"
                >
                  <button type="submit" className="delete-btn">
                    Delete
                  </button>
                </form>
              </div>
              {product && (
                <>
                  <h1 className="title">{product.name}</h1>
                  <img
                    src={`data:${product.photoType};base64, ${Buffer.from(
                      product.picture.data
                    ).toString("base64")}`}
                    alt={product.name}
                    loading="lazy"
                    className="w-[20vw] h-[40vh] m-auto"
                  />
                  <div className="flex flex-col gap-[1rem]">
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">ID</strong>
                      <p className="text-balance">{product._id}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">
                        Quantity
                      </strong>
                      <p className="text-balance">{product.quantity} meters</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">Price</strong>
                      <p className="text-balance">
                        KWD{" "}
                        {new Intl.NumberFormat("en-US").format(product.price)}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">Cost</strong>
                      <p className="text-balance">
                        KWD{" "}
                        {new Intl.NumberFormat("en-US").format(product.cost)}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">
                        Category
                      </strong>
                      <p className="text-balance">{product.category}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">Date</strong>
                      <p className="text-balance">
                        {new Date(product.date).toLocaleDateString("en-us", {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        })}
                      </p>
                    </span>
                  </div>
                </>
              )}
            </div>
          )}
        </section>
        <dialog
          id="editDialog"
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto; overflow-x-hidden max-h-[80vh] p-[2rem] px-0 shadow-md shadow-[#333] fadeIn"
          open={openDialog}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              action="/admin/products/<%= product.id %>?_method=PUT"
              method="POST"
            >
              <button
                className="delete-btn absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenDialog(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="form-row">
                <label htmlFor="name">Product Name</label>
                <input
                  type="text"
                  name="name"
                  id="name"
                  placeholder="Enter name of product"
                  value="<%= product.name %>"
                />
              </div>
              <div className="form-row">
                <label htmlFor="quantity">Quantity</label>
                <input
                  type="number"
                  step="0.01"
                  name="quantity"
                  id="quantity"
                  value="<%= product.quantity %>"
                />
              </div>
              <div className="form-row">
                <label htmlFor="category">Category</label>
                <select name="category" id="category">
                  <option value="cotton">Cotton</option>
                  <option value="linen">Linen</option>
                  <option value="wool">Wool</option>
                  <option value="polyester">Polyester</option>
                  <option value="other">Other</option>
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="picture">Product Photo</label>
                <input
                  type="file"
                  name="cover"
                  id="image"
                  className="article-cover filepond"
                />
              </div>
              <div className="form-row">
                <label htmlFor="price">Price</label>
                <input
                  type="number"
                  step="0.01"
                  name="price"
                  id="price"
                  value="<%= product.price %>"
                />
              </div>
              <div className="form-row">
                <label htmlFor="cost">Cost</label>
                <input
                  type="number"
                  step="0.01"
                  placeholder="Cost of the product"
                  name="cost"
                  id="cost"
                  value="<%= product.cost %>"
                />
              </div>
              <div className="form-row">
                <label htmlFor="date">Date</label>
                <input
                  type="date"
                  name="date"
                  id="date"
                  value="<%= product.date == null ? '' : product.date.toISOString().split('T')[0] %>"
                />
              </div>
              <div className="form-row">
                <button type="submit">Submit</button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default AdminProductsView;
