import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import Footer from "../../../components/Footer";
import { Link, useParams } from "react-router-dom";
import { useStoreState } from "easy-peasy";
import { toast } from "react-toastify";
import axios from "axios";

const CreateMeasurement = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/measurements/${measurementId}`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/measurements/${measurementId}`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    const fetchData = async () => {
        await axios
          .get(apiUrl, {
            headers: { Authorization: `Bearer ${authKey}` },
          })
          .then((response) => {
            setCustomer(response.data.customer);
          })
          .catch((error) => {
            toast.error(error.message);
            setCustomer(null);
          });
      };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
    return () => {
        fetchData();
      };
  }, [userLanguage]);

  return (
    <>
      <Navbar />
      <main>
        <section className="khamco-wrapper" dir={writingDirection}>
          <div className="utilities">
            <Link to={`/customers/${customer._id}/measurements`}>
              <i className="fas fa-arrow-left"></i> Measurements
            </Link>
          </div>
          <h1 className="title">
            `New Measurement for ${customer.name} ${customer.surname}`
          </h1>
          <form
            action="/customers/create-measurement/<%= customer.id %>"
            method="POST"
          >
            <div className="row">
              <label for="date_created">Date</label>
              <input type="date" name="date_created" id="date_created" />
            </div>
            <div className="form-row">
              <button type="submit">Submit</button>
            </div>
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CreateMeasurement;
