import { useState } from "react";
import { useStoreState } from "easy-peasy";
import Clothing from "./Clothing";
import tshirt from "../../assets/tshirt.svg";
import girlTshirt from "../../assets/girl-tshirt.png";
import poloShirt from "../../assets/polo-shirt.png";
import shirt from "../../assets/shirt.png";
import longShirt from "../../assets/long-shirt.png";
import jacket from "../../assets/jacket.png";
import hoodie from "../../assets/hoodie.png";
import suit from "../../assets/suit.png";
import pants from "../../assets/pants.png";
import shorts from "../../assets/shorts.png";
import raggedShorts from "../../assets/ragged-shorts.png";
import dress from "../../assets/dress.png";
import vans from "../../assets/vans.png";
import flipFlops from "../../assets/flip-flops.png";
import shoes from "../../assets/shoes.png";
import heels from "../../assets/heels.png";
import socks from "../../assets/socks.png";
import tanktop from "../../assets/tanktop.png";
import briefs from "../../assets/briefs.png";
import bra from "../../assets/bra.png";
import backpack from "../../assets/backpack.png";
import handbag from "../../assets/handbag.png";
import beanie from "../../assets/beanie.png";
import snapback from "../../assets/snapback.png";

const FormFields = () => {
  const [selectedGovernorate, setSelecteGovernorate] = useState("Al Aasima");
  const [defaultMilitary, setDefaultStatus] = useState("civilian");
  const [defaultMembership, setDefaultMembership] = useState("Basic");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("");
  const [area, setArea] = useState("");
  const [country, setCountry] = useState("");
  const [militaryStatus, setMilitaryStatus] = useState(false);
  const [membershipStatus, setMembershipStatus] = useState(false);
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const clothingList = [
    { label: "T-Shirt", imageUrl: tshirt },
    { label: "Girl T-Shirt", imageUrl: girlTshirt },
    { label: "Polo Shirt", imageUrl: poloShirt },
    { label: "Shirt", imageUrl: shirt },
    { label: "Long Shirt", imageUrl: longShirt },
    { label: "Jacket", imageUrl: jacket },
    { label: "Hoodie", imageUrl: hoodie },
    { label: "Suit", imageUrl: suit },
    { label: "Pants", imageUrl: pants },
    { label: "Shorts", imageUrl: shorts },
    { label: "Ragged Shorts", imageUrl: raggedShorts },
    { label: "Dress", imageUrl: dress },
    { label: "Vans", imageUrl: vans },
    { label: "Flip Flops", imageUrl: flipFlops },
    { label: "Shoes", imageUrl: shoes },
    { label: "Heels", imageUrl: heels },
    { label: "Socks", imageUrl: socks },
    { label: "Tank Top", imageUrl: tanktop },
    { label: "Briefs", imageUrl: briefs },
    { label: "Bra", imageUrl: bra },
    { label: "Backpack", imageUrl: backpack },
    { label: "Handbag", imageUrl: handbag },
    { label: "Beanie", imageUrl: beanie },
    { label: "Snapback", imageUrl: snapback },
  ];
  const itemCount = useStoreState((state) => state.itemCount);
  let [areas, setAreas] = useState([]);

  function selectArea(mainElement) {
    switch (mainElement.value) {
        case "Al Aasima":
            setAreas(["Abdulla Al-Salem", "Adailiya", "Bnaid Al-Qar", "Daʿiya", "Dasma", "Doha", "Doha Port", "Faiha'", "Failaka", "Granada", "Jaber Al-Ahmad City", "Jibla", "Kaifan", "Khaldiya", "Mansūriya", "Mirgab", "Nahdha", "North West Sulaibikhat", "Nuzha", "Qadsiya", "Qurtuba", "Rawda", "Shamiya", "Sharq", "Shuwaikh", "Shuwaikh Industrial Area", "Shuwaikh Port", "Sulaibikhat", "Surra", "Umm an Namil Island", "Yarmouk"]);
            break;
        case "Hawalli":
            setAreas(["Anjafa", "Bayān", "Bi'da", "Hawally", "Hittin", "Jabriya", "Maidan Hawalli", "Mishrif", "Mubarak Al-Jabir", "Nigra", "Rumaithiya", "Salam", "Salmiya", "Salwa", "Sha'ab", "Shuhada", "Siddiq", "South Surra", "Zahra"]);
            break;
        case "Mubarak Al-Kabeer":
            setAreas(["Abu Al Hasaniya", "Abu Futaira", "Adān", "Al Qurain", "Al-Qusour", "Fintās", "Funaitīs", "Misīla", "Mubarak Al-Kabeer", "Sabah Al-Salem", "Sabhān", "South Wista", "Wista"]);
            break;
        case "Ahmadi":
            setAreas(["Abu Halifa", "Abdullah Port", "Ahmadi", "Ali As-Salim", "Aqila", "Bar Al Ahmadi", "Bneidar", "Dhaher", "Fahaheel", "Fahad Al-Ahmad", "Hadiya", "Jaber Al-Ali", "Jawaher Al Wafra", "Jilei'a", "Khairan", "Mahbula", "Mangaf", "Miqwa'", "New Khairan City", "New Wafra", "Nuwaiseeb", "Riqqa", "Sabah Al-Ahmad City", "Sabah Al Ahmad Sea City", "Sabahiya", "Shu'aiba (North & South)", "South Sabahiya", "Wafra", "Zoor", "Zuhar"]);
            break;
        case "Farwaniya":
            setAreas(["Abdullah Al-Mubarak", "Airport District", "Andalous", "Ardiya", "Ardiya Herafiya", "Ardiya Industrial Area", "Ashbelya", "Dhajeej", "Farwaniya", "Fordous", "Jleeb Al-Shuyoukh", "Khaitan", "Omariya", "Rabiya", "Rai", "Al-Riggae", "Rihab", "Sabah Al-Nasser", "Sabaq Al Hajan"]);
            break;
        case "Jahra":
            setAreas(["Abdali", "Al Nahda / East Sulaibikhat", "Amghara", "Bar Jahra", "Jahra", "Jahra Industrial Area", "Kabad", "Naeem", "Nasseem", "Oyoun", "Qasr", "Saad Al Abdullah City", "Salmi", "Sikrab", "South Doha / Qairawān", "Subiya", "Sulaibiya", "Sulaibiya Agricultural Area", "Taima", "Waha"]);
            break;
        default: setAreas([]);
    }
}

  return (
    <>
      <div className="form-row">
        <label htmlFor="customer_name">Customer Name</label>
        <input
          type="text"
          name="name"
          id="name"
          placeholder="Customer name"
          required
        />
      </div>
      <div className="form-row">
        <label htmlFor="surname">Customer Surname</label>
        <input
          type="text"
          name="surname"
          id="surname"
          placeholder="Customer surname"
          required
        />
      </div>
      <div className="form-row">
        <label htmlFor="email">Email</label>
        <input type="email" name="email" id="email" placeholder="(Optional)" />
      </div>
      <div className="form-row">
        <label htmlFor="phone">Phone Number</label>
        <input
          type="text"
          name="phone"
          id="phone"
          placeholder="Customer phone number e.g. +965000000000"
        />
      </div>
      <div className="form-row">
        <label htmlFor="house_unit">House Unit</label>
        <input
          type="text"
          name="house_unit"
          id="house_unit"
          placeholder="Enter street address"
        />
      </div>
      <div className="form-row">
        <label htmlFor="governorate">Governorate</label>
        <select
          name="governorate"
          // defaultValue={selectedGovernorate}
          value={selectedGovernorate}
          onChange={(e) => {
            selectArea(e.target);
          }}
        >
          <option value="Al Aasima">Al Aasima</option>
          <option value="Hawalli">Hawalli</option>
          <option value="Mubarak Al-Kabeer">Mubarak Al-Kabeer</option>
          <option value="Ahmadi">Ahmadi</option>
          <option value="Farwaniya">Farwaniya</option>
          <option value="Jahra">Jahra</option>
        </select>
      </div>
      <div className="form-row">
        <label htmlFor="area">Area</label>
        <select name="area" id="area">
          {areas.map((area, index) => (
            <option key={index}>{area}</option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label htmlFor="country">Country</label>
        <select name="country" id="country">
          <option value="Kuwait">Kuwait</option>
        </select>
      </div>
      <div className="form-row">
        <label>Status</label>
        <div className="inline">
          <span>
            <label htmlFor="civilian" className="cursor-pointer">
              Civilian
            </label>
            <input
              type="checkbox"
              value="civilian"
              id="civilian"
              name="status"
              checked={defaultMilitary === "civilian"}
              onChange={(e) => {
                setMilitaryStatus(e.target.value);
                setDefaultStatus(e.target.value);
              }}
            />
          </span>
          <span>
            <label htmlFor="military" className="cursor-pointer">
              Military
            </label>
            <input
              type="checkbox"
              value="military"
              id="military"
              name="status"
              checked={defaultMilitary === "military"}
              onChange={(e) => {
                setMilitaryStatus(e.target.value);
                setDefaultStatus(e.target.value);
              }}
            />
          </span>
        </div>
      </div>
      {militaryStatus === "military" && (
        <div className="w-full bg-khamco-ribbon text-white transition-all fadeIn">
          <h4 className="title">Military Details</h4>
          <div className="form-row">
            <label htmlFor="military_idnumber">ID Number</label>
            <input type="text" name="military_idnumber" id="idnumber" />
          </div>
          <div className="form-row">
            <label htmlFor="military_rank">Position/Rank</label>
            <input type="text" name="military_rank" id="military_rank" />
          </div>
          <div className="form-row">
            <label htmlFor="department">Division</label>
            <select
              name="military_department"
              id="military_department"
              defaultValue={"airforce"}
            >
              <option disabled selected>
                -- Select military division --
              </option>
              <option value="airforce">Air Force</option>
              <option value="ground">Ground Base</option>
              <option value="navy">Navy</option>
              <option value="specialops">Special Operations</option>
            </select>
          </div>
        </div>
      )}
      <div className="form-row">
        <label>Membership Status</label>
        <div className="inline">
          <span>
            <label htmlFor="basic" className="cursor-pointer">
              Basic
            </label>
            <input
              type="checkbox"
              value="Basic"
              id="basic"
              name="membership_status"
              checked={defaultMembership === "Basic"}
              onChange={(e) => {
                setDefaultMembership(e.target.value);
                setMembershipStatus(e.target.value);
              }}
            />
          </span>
          <span>
            <label htmlFor="VIP" className="cursor-pointer">
              VIP <i className="fas fa-star blink"></i>
            </label>
            <input
              type="checkbox"
              value="VIP"
              id="VIP"
              name="membership_status"
              checked={defaultMembership === "VIP"}
              onChange={(e) => {
                setDefaultMembership(e.target.value);
                setMembershipStatus(e.target.value);
              }}
            />
          </span>
        </div>
      </div>
      <div className="form-row">
        <label htmlFor="date_created">Date</label>
        <input
          type="date"
          name="date_created"
          id="date_created"
          onChange={(e) => {
            setDate(e.target.value);
          }}
        />
      </div>
      <div className="form-row">
        <h1 className="title mb-8">Click to select an item</h1>
        <div className="relative flex justify-center md:justify-between flex-wrap h-[50vh] overflow-y-scroll">
          {clothingList &&
            clothingList.map((item, index) => (
              <Clothing
                label={item.label}
                imageUrl={item.imageUrl}
                key={index}
              />
            ))}
        </div>
      </div>
      {itemCount.length > 0 && (
        <p className="bg-khamco-primary text-white text-[1.2rem] rounded-full py-1 px-2 w-10 text-center fadeIn">
          {itemCount.length / 2}
        </p>
      )}
      <div className="form-row">
        <label htmlFor="other">Other Details</label>
        <textarea name="other" id="other" placeholder="(Optional)"></textarea>
      </div>
      <div className="form-row">
        <button type="submit">Submit</button>
      </div>
    </>
  );
};

export default FormFields;
