import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import { useStoreState } from "easy-peasy";
import axios from "axios";
import { toast } from "react-toastify";

const NewInvoice = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  // API Data
  const [services, setServices] = useState([]);
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [invoice, setInvoice] = useState({});
  // Product form fields
  const [productChoice, setProductChoice] = useState("");
  const [productQuantity, setProductQuantity] = useState(0);
  const [productPrice, setProductPrice] = useState(0);
  const [productAvailable, setProductAvailable] = useState(0);
  const productQuantityRef = useRef(null);
  const productChoiceRef = useRef(null);

  // Service form fields
  const [serviceChoice, setServiceChoice] = useState("");
  const [serviceQuantity, setServiceQuantity] = useState(0);
  const [servicePrice, setServicePrice] = useState(0);
  const serviceQuantityRef = useRef(null);
  const serviceChoiceRef = useRef(null);

  // Dependent form fields
  const [dependentName, setDependentName] = useState("");
  const [dependents, setDependents] = useState([]);
  const dependentNameRef = useRef(null);
  
  // Other form fields
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState(null);
  const [totalPrice, setTotalPrice] = useState(0);
  const [date, setDate] = useState(null);
  // Dynamic values
  const [loading, setLoading] = useState(false);
  const [loadingData, setLoadingData] = useState(true);

  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Invoice - Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/new-invoice/data/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        setProducts(response.data.products);
        setServices(response.data.services);
        setCustomers(response.data.customers);
        setInvoice(response.data.invoice);
        setLoadingData(false);
      } catch (error) {
        toast.error(error.response?.data?.message || error.message);
        setLoadingData(false);
      }
    };

    fetchData();
  }, [apiUrl, authKey, id, userLanguage]);

  const clearForm = async () => {
    // Product form fields
    setProductChoice("");
    productChoiceRef.current.value = "-- Select a product --";
    setProductQuantity(0);
    setProductAvailable(0);
    setProductPrice(0);
    productQuantityRef.current.value = null;
    // Service form fields
    setServiceChoice("");
    serviceChoiceRef.current.value = "-- Select a service --";
    setServiceQuantity(0);
    setServicePrice(0);
    serviceQuantityRef.current.value = null;
    // Dependent form fields
    setDependentName("");
    dependentNameRef.current.value = "-- Select a Dependent --";
    dependentNameRef.current.value = "-- Select a Dependent --";
    setTotalPrice(0);
  };

  const removeProduct = async (productName, productId) => {
    clearForm();
    // setLoading(true);
    // try {
    //   const response = await axios.delete(
    //     `${apiUrl}/${id}/products/${productId}`,
    //     {
    //       data: { product_choice: productName },
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   setInvoice(response.data.invoice);
    //   setProducts(response.data.products);
    //   setServices(response.data.services);
    //   setLoading(false);
    //   clearForm();
    // } catch (error) {
    //   setLoading(false);
    //   toast.error(error.response?.data?.message || error.message);
    // }
  };

  const addProduct = async () => {
    clearForm();
    // setLoading(true);

    // if (!productQuantity || productQuantity <= 0) {
    //   setLoading(false);
    //   toast.info("Please enter a product quantity");
    //   return;
    // }

    // try {
    //   const response = await axios.put(
    //     `${apiUrl}/${id}/products`,
    //     {
    //       product_choice: productChoice,
    //       product_quantity: productQuantity,
    //       product_price: productPrice,
    //     },
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   setProducts(response.data.products);
    //   setServices(response.data.services);
    //   setInvoice(response.data.invoice);
    //   setLoading(false);
    //   clearForm();
    // } catch (error) {
    //   setLoading(false);
    //   toast.error(error.response?.data?.message || error.message);
    // }
  };

  const handleProductChange = (e) => {
    const selectedProductName = e.target.value;

    if (selectedProductName === "-- Select a product --") {
      // Reset relevant states when the default option is selected
      setProductChoice("");
      setTotalPrice(0 + servicePrice * serviceQuantity);
      setProductAvailable(0);
      setProductPrice(0);
      return;
    }

    const selectedProduct = products.find(
      (product) => product.name === selectedProductName
    );

    if (selectedProduct) {
      setProductChoice(selectedProductName);
      setTotalPrice(
        0 +
          selectedProduct.price * productQuantity +
          servicePrice * serviceQuantity
      );
      setProductAvailable(selectedProduct.quantity);
      setProductPrice(selectedProduct.price);
    }
  };

  const handleServiceChange = (e) => {
    const selectedService = e.target.value;

    if (selectedService === "-- Select a service --") {
      // Reset relevant states when the default option is selected
      setServiceChoice("");
      setTotalPrice(0 + productPrice * productQuantity);
      setServicePrice(0);
      return;
    }

    const chosenService = services.find(
      (service) => service.service === selectedService
    );

    if (selectedService) {
      setServiceChoice(selectedService);
      setTotalPrice(
        0 +
          chosenService.price * serviceQuantity +
          productPrice * productQuantity
      );
      setServicePrice(chosenService.price);
    }
  };

  const handleCustomerChange = (e) => {
    const selectedCustomerName = e.target.value;
    setCustomerName(selectedCustomerName);

    const selectedCustomer = customers.find(
      (customer) => customer.name === selectedCustomerName
    );

    if (selectedCustomer) {
      setCustomerSurname(selectedCustomer.surname);
      setCustomerPhone(selectedCustomer.phone);
      setDependents(selectedCustomer.dependents);
    }
  };

  const removeService = async (serviceId) => {
    clearForm();
    // setLoading(true);
    // try {
    //   const response = await axios.delete(
    //     `${apiUrl}/${id}/services/${serviceId}`,
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   setInvoice(response.data.invoice);
    //   setProducts(response.data.products);
    //   setServices(response.data.services);
    //   clearForm();
    //   setLoading(false);
    // } catch (error) {
    //   setLoading(false);
    //   toast.error(error.response?.data?.message || error.message);
    // }
  };

  const addService = async () => {
    clearForm();
    // setLoading(true);

    // if (!serviceQuantity || serviceQuantity <= 0) {
    //   setLoading(false);
    //   toast.info("Service quantity is required");
    //   return;
    // }

    // try {
    //   const response = await axios.put(
    //     `${apiUrl}/${id}/services`,
    //     {
    //       service_choice: serviceChoice,
    //       service_quantity: serviceQuantity,
    //       service_price: servicePrice,
    //     },
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   setProducts(response.data.products);
    //   setServices(response.data.services);
    //   setInvoice(response.data.invoice);
    //   clearForm();
    // } catch (error) {
    //   setLoading(false);
    //   toast.error(error.response?.data?.message || error.message);
    // }
  };

  const removeDependent = async (dependentId) => {
    clearForm();
    // setLoading(true);
    // try {
    //   const response = await axios.delete(
    //     `${apiUrl}/${id}/dependents/${dependentId}`,
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   setInvoice(response.data.invoice);
    //   setProducts(response.data.products);
    //   setServices(response.data.services);
    //   setLoading(false);
    //   clearForm();
    // } catch (error) {
    //   setLoading(false);
    //   toast.error(error.response?.data?.message || error.message);
    // }
  };

  const addDependent = async () => {
    clearForm();
    // setLoading(true);

    // if (!dependentName) {
    //   setLoading(false);
    //   toast.info("Please select a dependent");
    //   return;
    // }

    // if (!customerName) {
    //   setLoading(false);
    //   toast.info("Please select a customer");
    //   return;
    // }

    // try {
    //   const response = await axios.put(
    //     `${apiUrl}/${id}/dependents`,
    //     {
    //       dependent_name: dependentName,
    //     },
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   setProducts(response.data.products);
    //   setServices(response.data.services);
    //   setInvoice(response.data.invoice);
    //   setLoading(false);
    //   clearForm();
    // } catch (error) {
    //   setLoading(false);
    //   toast.error(error.response?.data?.message || error.message);
    // }
  };

  const deleteInvoice = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.delete(`${apiUrl}/${id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/invoices");
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async () => {
    setLoading(true);

    if (!amountPaid) {
      setLoading(false);
      toast.info("Please enter the amount paid");
      return;
    }

    if (!date) {
      setLoading(false);
      toast.info("Invoice date is required");
      return;
    }

    if (!customerName) {
      setLoading(false);
      toast.info("Please select a customer");
      return;
    }

    if (!paymentStatus) {
      setLoading(false);
      toast.info("Please select a payment status");
      return;
    }

    try {
      const response = axios.put(
        `${apiUrl}/${id}`,
        {
          service_choice: serviceChoice,
          service_quantity: serviceQuantity,
          service_price: servicePrice,
          product_choice: productChoice,
          product_quantity: productQuantity,
          product_price: productPrice,
          dependent_name: dependentName,
          customer_name: customerName,
          customer_surname: customerSurname,
          customer_phone: customerPhone,
          payment_type: paymentType,
          payment_status: paymentStatus,
          amount_paid: amountPaid,
          total_price: totalPrice,
          date: date,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setLoading(false);
      toast.success("Invoice details saved");
      navigate("/invoices");
    } catch (error) {
      setLoading(false);
      console.error("Error:", error.message);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="khamco-wrapper font-minimoBold"
          dir={writingDirection}
        >
          <div className="utilities">
            <Link to="/invoices">
              <i className="fas fa-arrow-left"></i> Invoices
            </Link>
          </div>
          <h1 className="title">New Invoice</h1>
          <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem] overflow-auto">
            <thead className="text-khamco-primary font-bold">
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="bg-[#ddd] text-center p-[0.5rem]">Item</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">Quantity</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">
                  Available{" "}
                  <i
                    className="fas fa-info-circle text-khamco-ribbon cursor-pointer hover:text-khamco-primary"
                    title="Total products available in the database"
                  ></i>
                </td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">Price/Unit</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">
                  Total Price
                </td>
              </tr>
            </thead>
            <tbody>
              {loadingData &&
                new Array(3).fill(
                  <tr className="border-khamco-secondary bg-[#999] animate-pulse h-[2.5rem]">
                    <td className="text-khamco-secondary">Loading...</td>
                    <td></td>
                    <td></td>
                    <td></td>
                    <td></td>
                  </tr>
                )}
              {invoice.products && invoice.products.length > 0 ? (
                invoice.products.map((product, index) => (
                  <tr
                    className="border-t-0 border-[0.2rem] border-khamco-secondary relative"
                    key={index}
                  >
                    <td className="text-center p-[0.5rem]">
                      <button
                        className="remove-data"
                        onClick={() =>
                          removeProduct(product.choice, product._id)
                        }
                        title="Remove this product"
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <>
                            <i className="fas fa-minus"></i>
                          </>
                        )}
                      </button>
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {product.choice}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {product.quantity}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">--</td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {new Intl.NumberFormat("en-US").format(product.price)}
                      </span>
                    </td>
                    <td className="text-center p[0.5rem]">
                      {new Intl.NumberFormat("en-US").format(
                        product.total_price
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                    No products yet...
                  </td>
                </tr>
              )}

              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary">
                <td className="text-center p-[0.5rem]">
                  <button
                    className="add-data"
                    onClick={addProduct}
                    title="Add a product"
                  >
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>
                        <i className="fas fa-plus"></i>
                      </>
                    )}
                  </button>
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem] border border-black rounded-md"
                    onChange={handleProductChange}
                    ref={productChoiceRef}
                  >
                    <option
                      label="-- Select a product --"
                      value="-- Select a product --"
                    ></option>
                    {products &&
                      products.map((product, index) => (
                        <option
                          key={index}
                          value={product.name}
                          label={product.name}
                        ></option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    placeholder="Product quantity"
                    ref={productQuantityRef}
                    onChange={(e) => {
                      setProductQuantity(e.target.value);
                      setTotalPrice(
                        0 +
                          productPrice * e.target.value +
                          serviceQuantity * servicePrice
                      );
                    }}
                  />
                </td>
                <td
                  className={`text-center p-[0.5rem] ${
                    productAvailable <= 0 && "text-[red] animate-pulse"
                  }`}
                >
                  {productAvailable.toFixed(2)}
                </td>
                <td className="text-center p-[0.5rem]">
                  {new Intl.NumberFormat("en-US").format(productPrice)}
                </td>
                <td className="text-center p-[0.5rem]">
                  {new Intl.NumberFormat("en-US").format(
                    productPrice * productQuantity
                  )}
                </td>
              </tr>
              {loadingData &&
                new Array(3).fill(
                  <tr className="border-khamco-secondary bg-[#999] animate-pulse h-[2.5rem]">
                    <td className="text-khamco-secondary">Loading...</td>
                    <td></td>
                    <td></td>
                    <td></td>
                    <td></td>
                  </tr>
                )}
              {invoice.services && invoice.services.length > 0 ? (
                invoice.services.map((service, index) => (
                  <tr
                    className="border-t-0 border-[0.2rem] border-khamco-secondary relative"
                    key={index}
                  >
                    <td className="text-center p-[0.5rem]">
                      <button
                        className="remove-data"
                        onClick={() => removeService(service._id)}
                        title="Remove this service"
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <>
                            <i className="fas fa-minus"></i>
                          </>
                        )}
                      </button>
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {service.choice}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {service.quantity}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {service.price}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      {new Intl.NumberFormat("en-US").format(
                        service.total_price
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                    No services yet...
                  </td>
                </tr>
              )}
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button
                    className="add-data"
                    onClick={addService}
                    title="Add a service"
                  >
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>
                        <i className="fas fa-plus"></i>
                      </>
                    )}
                  </button>
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem] border border-black rounded-md"
                    onChange={handleServiceChange}
                    ref={serviceChoiceRef}
                  >
                    <option
                      label="-- Select a service --"
                      value="-- Select a service --"
                    ></option>
                    {services &&
                      services.map((service, index) => (
                        <option
                          key={index}
                          value={service.service}
                          label={service.service}
                        ></option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    placeholder="Service quantity"
                    ref={serviceQuantityRef}
                    onChange={(e) => {
                      setServiceQuantity(e.target.value);
                      setTotalPrice(
                        0 +
                          servicePrice * e.target.value +
                          productPrice * productQuantity
                      );
                    }}
                  />
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    {servicePrice}
                  </span>
                </td>
                <td className="text-center p-[0.5rem]">
                  {new Intl.NumberFormat("en-US").format(
                    servicePrice * serviceQuantity
                  )}
                </td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">
                  <strong className="text-khamco-primary">Total Invoice</strong>
                </td>
                <td>
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    {new Intl.NumberFormat("en-US").format(
                      invoice.total_price + totalPrice
                    )}
                  </span>
                </td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative text-khamco-primary font-bold">
                <td className="text-center p-[0.5rem]">Customer Details</td>
              </tr>
              {invoice.dependents && invoice.dependents.length > 0 ? (
                invoice.dependents.map((dependent, index) => (
                  <tr
                    className="border-t-0 border-[0.2rem] border-khamco-secondary relative"
                    key={index}
                  >
                    <td className="text-center p-[0.5rem]">
                      <button
                        className="remove-data"
                        onClick={() => removeDependent(dependent._id)}
                        title="Remove this dependent"
                      >
                        <i className="fas fa-minus"></i>
                      </button>
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {dependent.customer_name}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]"></td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                    No dependents selected...
                  </td>
                </tr>
              )}
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem] border border-black rounded-md"
                    onChange={handleCustomerChange}
                  >
                    <option label="-- Select a Customer --"></option>
                    {customers &&
                      customers.map((customer, index) => (
                        <option key={index} value={customer.name}>
                          {`${customer.name} ${customer.surname}`}
                        </option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button
                    className="add-data"
                    onClick={addDependent}
                    title="Add a dependent"
                  >
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>
                        <i className="fas fa-plus"></i>
                      </>
                    )}
                  </button>
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem] border border-black rounded-md"
                    onChange={(e) => setDependentName(e.target.value)}
                    ref={dependentNameRef}
                  >
                    <option label="-- Select a Dependent --" value=""></option>
                    {dependents &&
                      dependents.length > 0 &&
                      dependents.map((dependent, index) => (
                        <option
                          key={index}
                          value={`${dependent.name} ${dependent.surname}`}
                          label={`${dependent.name} ${dependent.surname}`}
                        ></option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="p-[0.5rem] text-[1.2rem] text-center">
                  Surname: {customerSurname || "--"}
                </td>
                <td className="p-[0.5rem] text-[1.2rem] text-center">
                  Phone: {customerPhone || "--"}
                </td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary">
                <td className="text-center p-[0.5rem] text-khamco-primary font-bold">
                  Payment Details
                </td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    step="0.01"
                    placeholder="Amount Paid"
                    className="w-full p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setAmountPaid(e.target.value)}
                  />
                </td>
                <td className="text-center p-[0.5rem]">
                  <select
                    name="payment_type"
                    onChange={(e) => setPaymentType(e.target.value)}
                    className="w-full p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                  >
                    <option label="-- Payment Type --" value={null}></option>
                    <option value="K-Net">K-Net</option>
                    <option value="Cash">Cash</option>
                    <option value="Other">Other</option>
                  </select>
                </td>
                <td className="text-center p-[0.5rem]">
                  <select
                    className="w-full p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setPaymentStatus(e.target.value)}
                  >
                    <option label="-- Payment status --" value={null}></option>
                    <option value="Unpaid">Unpaid</option>
                    <option value="Partially Paid">Partially Paid</option>
                    <option value="Paid">Paid</option>
                    <option value="Other">Other</option>
                  </select>
                </td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="date"
                    className="uppercase w-full p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setDate(e.target.value)}
                  />
                </td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary">
                <td className="text-center p-[0.5rem] text-khamco-primary font-bold">
                  Company
                </td>
                <td className="text-center p-[0.5rem]">Khamco</td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button
                    className="edit-btn w-full py-[1rem] px-[2rem]"
                    onClick={handleSubmit}
                  >
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>Submit</>
                    )}
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
          <div className="row">
            <form onSubmit={deleteInvoice}>
              <button
                type="submit"
                className="bg-[red] py-[0.5rem] px-[1rem] fixed top-[50%] right-2 rounded-full text-[1.5rem] text-white hover:rotate-12 transition-all"
                title="Delete this invoice"
              >
                {loading ? (
                  <i className="fas fa-spinner animate-spin"></i>
                ) : (
                  <>
                    <i className="fas fa-trash-can"></i>
                  </>
                )}
              </button>
            </form>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewInvoice;
