import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const AdminTailorDetails = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const apiUrl = `http://localhost:5000/admin/users/${id}`;
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const getUserDetails = async () => {
      await axios
        .get(apiUrl)
        .then((response) => {
          setUser(response.data.user);
          console.log(response.data.user);
          setIsLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          setIsLoading(true);
          setDisplay(false);
          console.error(error);
        });
    };

    return () => {
      getUserDetails();
    };
  }, [apiUrl]);
  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="customer-details">
          {isLoading && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}
          {display && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="utilities">
                <Link to={`/admin/edit-user/${user.id}`} className="edit-btn">
                  Edit
                </Link>
                <p>{message}</p>
                <form
                  action="/admin/<%= user.id %>?_method=DELETE"
                  method="POST"
                  className="d-inline"
                >
                  <button type="submit" className="delete-btn">
                    Delete
                  </button>
                </form>
              </div>
              <h1 className="title">User Details {user.name}</h1>
              <div className="khamco-card bg-yellow-400">
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong className="capitalize">ID</strong>
                  <p className="capitalize">{user.id}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong className="capitalize">Name</strong>
                  <p className="capitalize">{user.name}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong className="capitalize">Surname</strong>
                  <p className="capitalize">{user.surname}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong className="capitalize">Role</strong>
                  <p className="capitalize">{user.role}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Language</strong>
                  <p className="capitalize">
                    {user.language === "en" && "English"}
                    {user.language === "ur" && "Urdu"}
                    {user.language === "ar" && "Arabic"}
                    {user.language === "hi" && "Hindi"}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong className="capitalize">Date Created</strong>
                  <p className="capitalize">
                    {new Date(user.date).toLocaleDateString("en-us", {
                      month: "long",
                      year: "numeric",
                      day: "numeric",
                    })}
                  </p>
                </span>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailorDetails;
