import { useEffect, useState } from "react";
import Clothing from "../../../components/Clothing";
import {
  clothingList,
  dishdashaLength,
  garmentLength,
  hasShirt,
  hasSleepwearSleeve,
  hasSpecialOrder,
  upperMeasurements,
  needsSleeve,
  shortsLength,
  lowerMeasurements,
  isBottom,
  hasBackWidth,
  hasAlterations,
  isCoverall,
  measurement_icons,
  isDress,
  isSuit,
} from "../../../utilities";
import { useTranslation } from "react-i18next";
import UnitInput from "../../../components/UnitInput";

const MeasurementsForm = ({
  clothingOptions,
  setClothingOptions,
  shirtType,
  setShirtType,
  setDescription,
  loading,
  setSpecialOrder,
  setAlterations,
  setNotes,
  setValues,
  values,
}) => {
  const { t } = useTranslation();
  const [unitType, setUnitType] = useState("Inches");

  useEffect(()=>{
    if(values.unit_type === "Inches"){
      setUnitType("Centimeters");
    } else {
      
    }
  },[]);

  const handleInputChange = (name, value) => {
    setValues((prevValues) => ({
      ...prevValues,
      [name]: value,
      unit_type: unitType,
    }));
  };

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <h1 className="text-center text-[2rem] font-minimoBold mb-8">
          Click to select/deselect an item
        </h1>
        <div className="relative flex justify-center md:justify-evenly gap-[1rem] md:gap-[0.5rem] flex-wrap h-[70vh] overflow-y-scroll">
          {clothingList &&
            clothingList.map((item, index) => (
              <Clothing
                key={index}
                label={item.label}
                imageUrl={item.imageUrl}
                setOptions={setClothingOptions}
                options={clothingOptions}
              />
            ))}
        </div>
      </div>
      {clothingOptions.length > 0 && (
        <>
          <p className="bg-khamco-primary text-white text-[1.2rem] rounded-full py-1 px-2 w-10 text-center">
            {clothingOptions.length}
          </p>
          <p className="my-4 text-[#444] text-lg">Enter measurements below</p>
          <p className="my-4 text-[#444] text-lg">(1 inch = 2.54 cm)</p>
          <button
            type="button"
            className="p-2 my-4 bg-khamco-secondary text-white rounded-md fixed z-50 top-0 md:top-2/4 md:right-[8rem] md:-translate-y-2/4"
            onClick={() => {
              const newUnitType =
                unitType === "Inches" ? "Centimeters" : "Inches";
              setUnitType(newUnitType); // Update the state to the new unit type
              setValues((prevValues) => ({
                ...prevValues,
                unit_type: newUnitType, // Ensure the unit type is updated across all values
              }));
            }}
          >
            Change to {unitType === "Inches" ? "Centimeters" : "Inches"}
          </button>
        </>
      )}
      {clothingOptions.length > 0 && hasShirt(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <h1 className="text-center text-[1.5rem] font-minimoBold mb-8">
            Select Shirt Type
          </h1>
          <div className="flex gap-4 w-full justify-center flex-wrap">
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="long-sleeve"
              >
                Long Sleeve{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                id="long-sleeve"
                name="shirt-type"
                value="Long Sleeve"
                checked={shirtType === "Long Sleeve"}
                onChange={() => {
                  setShirtType(() => "Long Sleeve");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="short-sleeve"
              >
                Short Sleeve{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                id="short-sleeve"
                name="shirt-type"
                checked={shirtType === "Short Sleeve"}
                onChange={() => {
                  setShirtType(() => "Short Sleeve");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="polo-shirt"
              >
                Polo{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                id="polo-shirt"
                name="shirt-type"
                type="radio"
                checked={shirtType === "Polo"}
                onChange={() => {
                  setShirtType(() => "Polo");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="t-shirt"
              >
                T-Shirt{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                value="T-Shirt"
                id="t-shirt"
                name="shirt-type"
                checked={shirtType === "T-Shirt"}
                onChange={() => {
                  setShirtType(() => "T-Shirt");
                }}
              />
            </span>
          </div>
        </div>
      )}
      <div className="w-full md:w-3/4 flex flex-col mx-auto my-4 relative">
        {isSuit(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">Suit</h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Suit Length"
                name="suit_length"
                icon={measurement_icons.coatLength}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.suit_length : 0}
              />
            </div>
          </>
        )}
        {upperMeasurements(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Upper Body Measurements
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Collar"
                name="collar"
                icon={measurement_icons.collar}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.collar : 0}
              />
              <UnitInput
                label="Shoulders"
                name="shoulders"
                icon={measurement_icons.shoulders}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.shoulders : 0}
              />
              <UnitInput
                label="Chest"
                name="chest"
                icon={measurement_icons.chest}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.chest : 0}
              />
              <UnitInput
                label="Belly"
                name="belly"
                icon={measurement_icons.belly}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.belly : 0}
              />
              <UnitInput
                label="Arm hole"
                name="arm_hole"
                icon={measurement_icons.armHole}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.arm_hole : 0}
              />
              <UnitInput
                label="Bicep/Tricep"
                name="bicep_tricep"
                icon={measurement_icons.bicepTricep}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.bicep_tricep : 0}
              />
              <UnitInput
                label="Elbow"
                name="elbow"
                icon={measurement_icons.elbow}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.elbow : 0}
              />
              <UnitInput
                label="Wrist"
                name="wrist"
                icon={measurement_icons.wrist}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.wrist : 0}
              />
              <UnitInput
                label="Chest Height"
                name="chest_height"
                icon={measurement_icons.chestHeight}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.chest_height : 0}
              />
              <UnitInput
                label="Coat Length"
                name="coat_length"
                icon={measurement_icons.coatLength}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.coat_length : 0}
              />
              <UnitInput
                label="Vest Length"
                name="vest_length"
                icon={measurement_icons.vestLength}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.vest_length : 0}
              />
            </div>
          </>
        )}
        {hasShirt(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">Shirt</h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Shirt sleeve"
                name="shirt_sleeve"
                icon={measurement_icons.sleevesLong}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.shirt_sleeve : 0}
              />
            </div>
          </>
        )}
        {dishdashaLength(clothingOptions) && (
          <>
            <h1 className="text-center text-[1.5rem] font-minimoBold">
              Dishdasha
            </h1>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Dishdasha Length"
                name="dishdasha_garment_length"
                icon={measurement_icons.dishdashaLengthIcon}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.dishdasha_garment_length : 0}
              />
              <UnitInput
                label="Dishdasha Front Height"
                name="dishdasha_front_height"
                icon={measurement_icons.dishdashaLengthIcon}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.dishdasha_front_height : 0}
              />
              <UnitInput
                label="Dishdasha Back Height"
                name="dishdasha_back_height"
                icon={measurement_icons.backHeight}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.dishdasha_back_height : 0}
              />
            </div>
          </>
        )}
        {garmentLength(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Garment Length
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Garment Length"
                name="garment_length"
                icon={measurement_icons.shirtLength}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.garment_length : 0}
              />
            </div>
          </>
        )}
        {isCoverall(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Coverall
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Coverall length"
                name="coverall_length"
                icon={measurement_icons.shirtLength}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.coverall_length : 0}
              />
            </div>
          </>
        )}
        {isBottom(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">Pants</h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Pants length"
                name="pants_garment_length"
                icon={measurement_icons.pantsLength}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.pants_garment_length : 0}
              />
              <UnitInput
                label="Inseam"
                name="inseam"
                icon={measurement_icons.inseam}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.inseam : 0}
              />
              <UnitInput
                label="Thigh"
                name="thigh"
                icon={measurement_icons.thighs}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.thighs : 0}
              />
              <UnitInput
                label="Calf"
                name="calf"
                icon={measurement_icons.calf}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.calf : 0}
              />
              <UnitInput
                label="Leg-Foot"
                name="leg_foot"
                icon={measurement_icons.leg}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.leg_foot : 0}
              />
            </div>
          </>
        )}

        {shortsLength(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Shorts
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Shorts length"
                name="shorts_garment_length"
                icon={measurement_icons.shortsLengthIcon}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.shorts_garment_length : 0}
              />
            </div>
          </>
        )}
        {isDress(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">Dress</h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Dress Length"
                name="dress_garment_length"
                icon={measurement_icons.dressLengthIcon}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.dress_garment_length : 0}
              />
              <UnitInput
                label="Dress sleeve"
                name="dress_sleeve"
                icon={measurement_icons.sleevesShort}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.dress_sleeve : 0}
              />
            </div>
          </>
        )}
        {needsSleeve(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Sleeve
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Sleeve"
                name="sleeve"
                icon={measurement_icons.sleevesLong}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.sleeve : 0}
              />
            </div>
          </>
        )}
        {hasSleepwearSleeve(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Sleepwear Sleeve
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Sleepwear sleeve"
                name="sleepwear_sleeve"
                icon={measurement_icons.sleevesLong}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.sleepwear_sleeve : 0}
              />
            </div>
          </>
        )}
        {lowerMeasurements(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Lower Body Measurements
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Waist"
                name="waist"
                icon={measurement_icons.waist}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.waist : 0}
              />
              <UnitInput
                label="Hips"
                name="hips"
                icon={measurement_icons.hips}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.hips : 0}
              />
              <UnitInput
                label="Knees"
                name="knees"
                icon={measurement_icons.knees}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.knees : 0}
              />
            </div>
          </>
        )}
        {hasBackWidth(clothingOptions) && (
          <>
            <h2 className="text-center text-[1.5rem] font-minimoBold">
              Back Width/Height
            </h2>
            <div className="flex w-full justify-evenly flex-wrap">
              <UnitInput
                label="Back width"
                name="back_width"
                icon={measurement_icons.backWidth}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.back_width : 0}
              />
              <UnitInput
                label="Back height"
                name="back_height"
                icon={measurement_icons.backHeight}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.back_height : 0}
              />
              <UnitInput
                label="Front-Chest-Width"
                name="front_chest_width"
                icon={measurement_icons.chestWidth}
                unitType={unitType}
                onChange={handleInputChange}
                measurement={values ? values.front_chest_width : 0}
              />
            </div>
          </>
        )}
      </div>
      {hasSpecialOrder(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Special order details</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Special order details"
            onChange={(e) => setSpecialOrder(e.target.value)}
            value={values ? values.special_order : undefined}
          ></textarea>
        </div>
      )}
      {hasAlterations(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Alterations</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Alterations"
            onChange={(e) => setAlterations(e.target.value)}
            value={values ? values.alterations : ""}
          ></textarea>
        </div>
      )}
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Other</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Enter a description"
            onChange={(e) => setDescription(e.target.value)}
            value={values ? values.description : undefined}
          ></textarea>
        </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label>Extra notes</label>
        <textarea
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          placeholder="Add extra notes"
          onChange={(e) => setNotes(e.target.value)}
        ></textarea>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
          disabled={loading}
        >
          {loading ? (
            <i className="fas fa-spinner animate-spin"></i>
          ) : (
            `${t("Submit")}`
          )}
        </button>
      </div>
    </>
  );
};

export default MeasurementsForm;
