import { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import logo from "../../assets/logo.jpg";
import { useStoreState } from "easy-peasy";

const ShowInvoice = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [dialogState, setDialogState] = useState(false);
  const [order, setOrder] = useState([]);
  const [invoice, setInvoice] = useState();
  const [customer, setCustomer] = useState(null);
  const [customers, setCustomers] = useState("");
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [customerName, setCustomerName] = useState("");
  const invoiceRef = useRef(null);

  useEffect(() => {
    let isMounted = true;

    document.title = "Invoice Details - Khamco Shop";

    const fetchInvoice = async () => {
      try {
        const response = await axios.get(`${apiUrl}/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setInvoice(response.data.invoice);
          setLoading(false);
          setOrder(response.data.order);
          setCustomer(response.data.customer);
          setCustomers(response.data.customers);
        }
      } catch (error) {
        if (isMounted) {
          setLoading(true);
          setInvoice({});
          toast.error(error.response?.data?.message || error.message);
        }
      }
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchInvoice();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, userLanguage]);

  const deleteInvoice = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/${invoice._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/invoices");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/${id}`,
        {
          payment_type: paymentStatus,
          amount_paid: amountPaid,
          payment_status: paymentType,
          customer_name: customerName,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setCustomer(response.data.customer);
      setOrder(response.data.order);
      toast.success(response.data.message);
      setDialogState(false);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handlePrint = () => {
    const content = invoiceRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
          <title>Invoice</title>
          <style>
            /* Add necessary styles for printing */
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
            }
            table {
              width: 100%;
              border-collapse: collapse;
              margin-bottom: 2.5rem;
            }
            table, th, td {
              border: 1px solid black;
            }
            th, td {
              padding: 10px;
              text-align: left;
            }
            .text-center {
              text-align: center;
            }
            .text-khamco-primary {
              color: #007BFF;
            }
            .text-khamco-secondary {
              color: #6c757d;
            }
            .invoice-data{
              display: flex;
              justify-content: flex-end;
              flex-wrap;
              width: 25%;
              gap: 2rem;
              align-items: center;
              margin: 1rem 0;
              padding: 0 0.2rem; 
            }
            .invoice-data strong {
              color: blue;
              font-size: 1.5rem;
            }
            .invoice-data input {
              border: 0.2rem solid black;
              padding: 0.2rem;
              font-size: 1.25rem;
            }
          </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="utilities">
            <Link to="/invoices">
              <i className="fas fa-arrow-left"> </i> Invoices
            </Link>
          </div>
          {loading && (
            <>
              <table className="text-center">
                <thead>
                  <tr>
                    <td>Loading...</td>
                  </tr>
                </thead>
                <tbody>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}
          {invoice && (
            <>
              <div className="utilities">
                <form onSubmit={deleteInvoice}>
                  <button
                    type="submit"
                    className="delete-btn"
                    title="Delete this invoice"
                  >
                    Delete
                  </button>
                </form>
                <button className="whatsapp-btn">
                  WhatsApp <i className="fas fa-share"></i>
                </button>
                <button className="print-btn" onClick={handlePrint}>
                  Print <i className="fas fa-print"></i>
                </button>
                {order ? (
                  <Link to={`/orders/${order._id}`} className="edit-btn">
                    View Order
                  </Link>
                ) : (
                  <p className="message">No Orders</p>
                )}
                <button
                  className="order-btn"
                  onClick={() => setDialogState(true)}
                >
                  Change Payment Details: {invoice.payment_status}{" "}
                  <i className="fas fa-money-check"></i>
                </button>
              </div>
              <div
                className="w-full m-auto shadow-md shadow-[#666]"
                ref={invoiceRef}
              >
                <div className="bg-white m-auto px-0">
                  <div>
                    <span className="w-full flex justify-evenly items-center m-auto">
                      <img src={logo} alt="Logo" />
                    </span>
                  </div>
                  <div className="details">
                    <table
                      className="border-collapse w-full my-[1rem] m-auto text-[1.2rem]"
                      id="receipt-table"
                    >
                      <thead className="text-khamco-primary font-bold">
                        <tr className="border-[0.2rem] border-khamco-secondary border-t-0">
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            Item
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            Price
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            Quantity
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            Total
                          </td>
                        </tr>
                      </thead>
                      <tbody>
                        {invoice.products &&
                          invoice.products.map((product, index) => (
                            <tr
                              className="border-[0.2rem] border-khamco-secondary border-t-0"
                              key={index}
                            >
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {product.choice || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {product.price || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {product.quantity || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {product.quantity * product.price}
                              </td>
                            </tr>
                          ))}
                        {invoice.services &&
                          invoice.services.map((service, index) => (
                            <tr
                              className="border-[0.2rem] border-khamco-secondary border-t-0"
                              key={index}
                            >
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {service.choice || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {service.price || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {service.quantity || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {service.quantity * service.price}
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </table>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Total Price
                      </strong>
                      <input
                        type="text"
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                        value={`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.total_price
                        )}`}
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Amount Paid
                      </strong>
                      <input
                        type="text"
                        value={`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.amount_paid
                        )}`}
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Amount Remaining
                      </strong>
                      <input
                        type="text"
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                        value={`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.amount_remaining
                        )}`}
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className=" text-khamco-secondary text-2xl">
                        Date
                      </strong>
                      <input
                        type="text"
                        value={`${new Date(invoice.date).toLocaleDateString(
                          "en-us",
                          {
                            month: "long",
                            day: "2-digit",
                            year: "numeric",
                          }
                        )}`}
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className=" text-khamco-secondary text-2xl">
                        Invoice No.
                      </strong>
                      <input
                        type="text"
                        value={`${invoice.uniqueId}`}
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Payment Type
                      </strong>
                      <input
                        type="text"
                        value={`${invoice.payment_type}`}
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Customer ID
                      </strong>
                      <input
                        type="text"
                        value={`${customer && customer.uniqueId}`}
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                      />
                    </div>
                    <div className="flex justify-end gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Customer Phone
                      </strong>
                      <input
                        value={`${
                          invoice.customer_phone &&
                          !invoice.customer_phone.startsWith("+965")
                            ? "+965" + invoice.customer_phone
                            : invoice.customer_phone
                        }`}
                        readOnly
                        className="border-[0.2rem] border-black p-[0.2rem] text-lg"
                      />
                    </div>
                    <div className="flex flex-col items-end py-[2rem]">
                      <span className="w-full flex justify-end gap-[2rem] px-[2rem] items-center m-auto my-[2rem] text-lg">
                        <i className="fas fa-phone-volume text-4xl"></i>
                        <p className="dull-text">2400650 / 55627017</p>
                      </span>
                      <address className="text-[#666] mx-[0.5rem]">
                        Shop 89, First Floor, Block at 2 Qibla, Safat 13074, PO
                        Box 21353 Kuwait City, Kuwait
                      </address>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </section>
        <dialog
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto; overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={dialogState}
        >
          <div
            className="p-[4rem] grid justify-center items-center"
            dir={writingDirection}
          >
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={handleSubmit}
            >
              <button
                className="delete-btn absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setDialogState(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="form-row">
                <h2 className="title">Change Payment Details</h2>
              </div>
              <div className="form-row">
                <label htmlFor="payment_status">{`Payment Status: ${
                  invoice && invoice.payment_status
                }`}</label>
                <select onChange={(e) => setPaymentStatus(e.target.value)}>
                  <option value="Unpaid">Unpaid</option>
                  <option value="Partially Paid">Partially Paid</option>
                  <option value="Paid">Paid</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="amount_paid">Amount Paid</label>
                <input
                  type="number"
                  step="0.1"
                  placeholder={invoice ? invoice.amount_paid : "Amount Paid"}
                  onChange={(e) => setAmountPaid(e.target.value)}
                />
              </div>
              <div className="form-row">
                <label htmlFor="payment_type">Payment Type</label>
                <select
                  name="payment_type"
                  defaultValue={invoice && invoice.payment_type}
                  onChange={(e) => setPaymentType(e.target.value)}
                >
                  <option value="K-Net">K-Net</option>
                  <option value="Cash">Cash</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="customer">
                  Customer (
                  {invoice &&
                    `${invoice.customer_name} ${invoice.customer_surname}`}
                  )
                </label>
                <select
                  id="customer"
                  onChange={(e) => setCustomerName(e.target.value)}
                >
                  <option label="-- Select a customer --"></option>
                  {customers &&
                    customers.map((customer, index) => (
                      <option
                        key={index}
                        label={`${customer.name} ${customer.surname}`}
                        value={customer.name}
                      ></option>
                    ))}
                </select>
              </div>
              <div className="form-row">
                <button type="submit">Submit</button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default ShowInvoice;
