import { useState, useEffect } from "react";
import dishdashaLengthIcon from "../../../assets/images/icons/dishdasha-length.png";
import armHole from "../../../assets/images/icons/arm-hole.png";
import backHeight from "../../../assets/images/icons/back-height.png";
import backWidth from "../../../assets/images/icons/back-width.png";
import belly from "../../../assets/images/icons/belly.png";
import bicepTricep from "../../../assets/images/icons/bicep-tricep.png";
import calf from "../../../assets/images/icons/calf.png";
import chest from "../../../assets/images/icons/chest.png";
import chestHeight from "../../../assets/images/icons/chest-height.png";
import chestWidth from "../../../assets/images/icons/chest-width.png";
import coatLength from "../../../assets/images/icons/coat-length.png";
import collar from "../../../assets/images/icons/collar.png";
import dressLength from "../../../assets/images/icons/dress-length.png";
import elbow from "../../../assets/images/icons/elbow.png";
import extras from "../../../assets/images/icons/extras.png";
import hips from "../../../assets/images/icons/hips.png";
import inseam from "../../../assets/images/icons/inseam.png";
import knees from "../../../assets/images/icons/knees.png";
import leg from "../../../assets/images/icons/leg.png";
import other from "../../../assets/images/icons/other.png";
import pantsLength from "../../../assets/images/icons/pants-length.png";
import shirtLength from "../../../assets/images/icons/shirt-length.png";
import shortsLength from "../../../assets/images/icons/shorts-length.png";
import shoulders from "../../../assets/images/icons/shoulders.png";
import sleevesLong from "../../../assets/images/icons/sleeve-long-sleeve.png";
import sleevesShort from "../../../assets/images/icons/sleeve-short-sleeve.png";
import thighs from "../../../assets/images/icons/thighs.png";
import unknown from "../../../assets/images/icons/unknown.png";
import userDefined from "../../../assets/images/icons/user-defined.png";
import vestLength from "../../../assets/images/icons/vest-length.png";
import waist from "../../../assets/images/icons/waist.png";
import wrist from "../../../assets/images/icons/wrist.png";
import MeasurementItem from "../../../components/MeasurementItem";

const MeasurementList = ({ measurement }) => {
  const [convertedMeasurement, setConvertedMeasurement] = useState({
    ...measurement,
  });
  const [currentUnit, setCurrentUnit] = useState(measurement.unit_type);
  const path = window.location.pathname;
  const pathSegments = path.split("/");

  // Assuming the path is structured like /customers/:customerId/measurements/:measurementId
  const isMeasurementPage = pathSegments.includes("measurements");

  // Conversion factor: 1 inch = 2.54 cm
  const convertUnits = (value, fromUnit, toUnit) => {
    if (fromUnit === toUnit) return value; // No conversion needed
    if (fromUnit === "Inches" && toUnit === "Centimeters") {
      return (value * 2.54).toFixed(2); // Inches to cm
    } else if (fromUnit === "Centimeters" && toUnit === "Inches") {
      return (value / 2.54).toFixed(2); // cm to inches
    }
    return value; // If no valid conversion is found, return the original value
  };

  const convertAllValues = (newUnit) => {
    const converted = { ...measurement };

    const keysToConvert = [
      "dishdasha_garment_length",
      "dishdasha_front_height",
      "dishdasha_back_height",
      "shirt_sleeve",
      "garment_length",
      "pants_garment_length",
      "shorts_garment_length",
      "dress_garment_length",
      "collar",
      "shoulders",
      "sleeve",
      "dress_sleeve",
      "sleepwear_sleeve",
      "chest",
      "belly",
      "waist",
      "hips",
      "arm_hole",
      "bicep_tricep",
      "elbow",
      "wrist",
      "inseam",
      "thighs",
      "calf",
      "leg_foot",
      "back_width",
      "back_height",
      "front_chest_width",
    ];

    // Convert all values based on the unit type
    keysToConvert.forEach((key) => {
      if (converted[key] > 0) {
        // Convert using the correct fromUnit and toUnit
        converted[key] = convertUnits(
          converted[key],
          measurement.unit_type,
          newUnit
        );
      }
    });

    setConvertedMeasurement(converted);
  };

  // Automatically convert when the unit changes
  useEffect(() => {
    convertAllValues(currentUnit);
  }, [currentUnit]);

  return (
    <>
      <span className="gap-[1rem] flex">
        <strong className="w-1/4">Clothing Options: </strong>
        <div>
          {measurement.clothing_options &&
          measurement.clothing_options.length > 0 ? (
            measurement.clothing_options.map((item, index) => (
              <span key={index}> {item.clothing_item},</span>
            ))
          ) : (
            <p className="text-[red]">No clothing items selected.</p>
          )}
        </div>
      </span>
      {measurement.unit_type && (
        <span className="inline-flex justify-end items-center gap-[2rem] text-balance flex-wrap">
          <strong>Units: </strong>
          {currentUnit}
          {isMeasurementPage && (
            <button
              className="p-2 my-4 bg-khamco-secondary text-white rounded-md"
              onClick={() =>
                setCurrentUnit((prevUnit) =>
                  prevUnit === "Inches" ? "Centimeters" : "Inches"
                )
              }
            >
              Change to {currentUnit === "Inches" ? "Centimeters" : "Inches"}
            </button>
          )}
        </span>
      )}
      {/* Garment data - convertedMeasurement contains converted values */}
      {convertedMeasurement.dishdasha_garment_length > 0 && (
        <MeasurementItem
          icon={dishdashaLengthIcon}
          metric={convertedMeasurement.dishdasha_garment_length}
          currentUnit={currentUnit}
          label="Dishdasha Garment Length"
        />
      )}
      {convertedMeasurement.dishdasha_front_height > 0 && (
        <MeasurementItem
          metric={convertedMeasurement.dishdasha_front_height}
          currentUnit={currentUnit}
          label="Dishdasha Front Height"
        />
      )}
      {convertedMeasurement.dishdasha_back_height > 0 && (
        <MeasurementItem
          icon={backHeight}
          metric={convertedMeasurement.dishdasha_back_height}
          currentUnit={currentUnit}
          label="Dishdasha Back Height"
        />
      )}
