import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import UserForm from "./UserForm";
import Footer from "../../components/Footer";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const EditUser = () => {
  const { id } = useParams();
  const [checkMaster, setCheckMaster] = useState(null);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/admin/users/${id}`
    : `https://khamco-shop-backend.onrender.com/admin/users/${id}`;
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [language, setLanguage] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  useEffect(() => {
    document.title = "Edit User Details - Khamco Shop";
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios
      .post(
        apiUrl,
        {
          name: firstName,
          surname: lastName,
          email: email,
          role: role,
          language: language,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setMessage(response.data.message);
        setAuthorizedUser(response.data.authorizedUser);
        setCheckMaster(response.data.checkMaster);
        navigate("/");
      })
      .catch((error) => {
        setMessage(error.message);
        console.error(error);
      });
  };

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <p className="message">{message}</p>
        <section>
          <h1 className="title">Edit Tailor Details</h1>
          <form
            action="/admin/<%= user.id %>?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <UserForm
              checkMaster={checkMaster}
              authorizedUser={authorizedUser}
              setFirstName={setFirstName}
              setLastName={setLastName}
              setRole={setRole}
              setLanguage={setLanguage}
              setEmail={setEmail}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditUser;
