import React, { useEffect, useState } from "react";
import ServiceForm from "./ServiceForm";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";

const NewService = () => {
  const [service, setService] = useState("");
  const [price, setPrice] = useState("");
  const [date, setDate] = useState(null);
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Service - Khamco Shop";
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    await axios.post(
      {
        service: service,
        price: price,
        date: date,
      },
      {
        headers: { Authorization: `Bearer ${authKey}` },
      }
    ).then(response => {
      toast.success(response.data.message);
      navigate("/services");
    }).catch(error => {
      toast.error(error.message);
    });
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section className="p-[4rem] grid justify-center items-center">
          <div className="row">
            <Link to="/services">
              <i className="fas fa-arrow-left"></i> Services
            </Link>
          </div>
          <h1 className="title">New Service</h1>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <ServiceForm
              setService={setService}
              setPrice={setPrice}
              setDate={setDate}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewService;
