import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useParams } from "react-router-dom";
import FormFields from "./Form_Fields";
import { useStoreState } from "easy-peasy";
import axios from "axios";

const EditCustomer = () => {
  const { id } = useParams();
  const serverUrl = "http://localhost:5000";
  const [message, setMessage] = useState("Hello World");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/invoices`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/invoices`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = `Edit Customer Details - ${customer.id} Khamco Shop`;

    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setInvoices(response.data.invoices);
          setLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          toast.error(error.message);
          setCustomer(null);
          setLoading(true);
          setLoading(false);
          setInvoices(null);
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
  }, [customer.id]);
  return (
    <>
      <Navbar />
      <main>
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to={`${serverUrl}/customers/${customer.id}`}>
              <i className="fas fa-arrow-left"></i> Customer {customer.name}{" "}
              {customer.surname}
            </Link>
          </div>
          <h1 className="title">Edit Customer Details</h1>
          <p className="message">{message}</p>
          <form
            action={`/customers/${customer.id}?_method=PUT`}
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <FormFields />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditCustomer;
