import React from "react";

const MeasurementItem = ({ icon, metric, currentUnit, label }) => {
  return (
    <span className="flex flex-col justify-start gap-[2rem] text-balance flex-wrap border-b-2 border-[black] p-[1rem] bg-khamco-ribbon">
      <div className="flex gap-[1rem]">
        <strong>{label}: </strong>
        {metric} {currentUnit === "Centimeters" ? "cm" : "in."}
      </div>
      {icon ? (
        <img
          src={icon}
          alt=""
          className="w-[12rem] h-[30vh] border-2 border-khamco-primary"
        />
      ) : (
        <img
          src=""
          alt=""
          className="bg-[#333] w-[12rem] h-[30vh] border-2 border-khamco-primary"
        />
      )}
    </span>
  );
};

export default MeasurementItem;
