  setCustomerSurname,
  setCustomerEmail,
  setCustomerPhone,
  setHouseUnit,
  setGovernorate,
  setArea,
  setCountry,
  militaryStatus,
  setMilitaryStatus,
  setMilitaryNumber,
  setMilitaryRank,
  setMilitaryDivision,
  setMembershipStatus,
  setDate,
  areas,
  setAreas,
  clothingOptions,
  setClothingOptions,
  setWaist,
  setNaturalWaist,
  setGarmentLength,
  setHalfAcross,
  setChest,
  setTrouserWaistPosition,
  setSeat,
  setNeck,
  setShoulder,
  setSleeveLength,
  setSideSeamLength,
  setBodyRise,
  setInsideLeg,
  setWrist,
  setTopArm,
  setScyeDepth,
  setArmSleeveHole,
  setFrontalCollar,
  setHip,
  setFrontRise,
  setBackRise,
  setThigh,
  setKnee,
  setHem,
  setOutseam,
  setHeadSize,
  setFootSize,
  setDescription,
  loading,
  customer,
}) => {
  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Customer Name
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder={customer ? customer.name : "Customer name"}
          onChange={(e) => setCustomerName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Customer Surname
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder={customer ? customer.surname : "Customer surname"}
          onChange={(e) => setCustomerSurname(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Email
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="email"
          placeholder={customer ? customer.email : "(Optional)"}
          onChange={(e) => setCustomerEmail(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Phone Number
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder={
            customer
              ? customer.phone
              : "Customer phone number e.g. +965000000000"
          }
          onChange={(e) => setCustomerPhone(e.target.value)}
        />
      </div>
      {setHouseUnit && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            House Unit
          </label>
          <input
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            type="text"
            placeholder={
              customer ? customer.address.split(",")[0] : "Enter street address"
            }
            onChange={(e) => setHouseUnit(e.target.value)}
          />
        </div>
      )}
      {setGovernorate && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            Governorate
          </label>
          <select
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            onChange={(e) => {
              selectArea(e.target, setGovernorate, setAreas);
            }}
          >
            <option value="Al Aasima">Al Aasima</option>
            <option value="Hawalli">Hawalli</option>
            <option value="Mubarak Al-Kabeer">Mubarak Al-Kabeer</option>
            <option value="Ahmadi">Ahmadi</option>
            <option value="Farwaniya">Farwaniya</option>
            <option value="Jahra">Jahra</option>
          </select>
        </div>
      )}
      {setArea && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            Area
          </label>
          <select
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            onChange={(e) => setArea(e.target.value)}
          >
            {areas &&
              areas.map((area, index) => <option key={index}>{area}</option>)}
          </select>
        </div>
      )}
      {setCountry && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            Country
          </label>
          <select
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            onChange={(e) => {
              setCountry(e.target.value);
            }}
            value={"Kuwait"}
          >
            <option disabled>-- Select country --</option>
            <option value="Kuwait">Kuwait</option>
          </select>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Military Status
        </label>
        <div className="flex gap-4 w-full justify-center flex-wrap">
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="civilian"
            >
              Civilian{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              id="civilian"
              type="checkbox"
              value="Civilian"
              checked={militaryStatus === "Civilian"}
              onChange={() => {
                setMilitaryStatus("Civilian");
              }}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem] ">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="military"
            >
              Military{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              type="checkbox"
              value="Military"
              id="military"
              checked={militaryStatus === "Military"}
              onChange={() => {
                setMilitaryStatus("Military");
              }}
            />
          </span>
        </div>
      </div>
      {militaryStatus === "Military" && (
        <div className="w-full bg-khamco-ribbon text-white transition-all fadeIn">
          <h4 className="title">Military Details</h4>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              ID Number {customer && `(${customer.military_idnumber})`}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] text-black"
              type="text"
              onChange={(e) => setMilitaryNumber(e.target.value)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              Position/Rank {customer && `(${customer.military_rank})`}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] text-black"
              type="text"
              onChange={(e) => setMilitaryRank(e.target.value)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              Division {customer && `(${customer.military_department})`}
            </label>
            <select
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] text-black"
              onChange={(e) => setMilitaryDivision(e.target.value)}
            >
              <option disabled>-- Select military division --</option>
              <option value="Air Force">Air Force</option>
              <option value="Ground Base">Ground Base</option>
              <option value="Navy">Navy</option>
              <option value="Special Operations">Special Operations</option>
            </select>
          </div>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Membership Status
        </label>
        <div className="flex gap-4 w-full justify-center flex-wrap">
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem] checked:border-[blue]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="basic"
            >
              Basic{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer checked:accent-[blue] checked:border-[blue]"
              type="checkbox"
              value="Basic"
              id="basic"
              checked={membershipStatus === "Basic"}
              onChange={() => {
                setMembershipStatus("Basic");
              }}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem] checked:border-[blue]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="VIP"
            >
              VIP <i className="fas fa-star text-[#d2b929] animate-pulse"></i>{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              type="checkbox"
              value="VIP"
              id="VIP"
              checked={membershipStatus === "VIP"}
              onChange={(e) => {
                setMembershipStatus("VIP");
              }}
            />
          </span>
        </div>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Date
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="date"
          placeholder={
            customer
              ? new Date(customer.date_created).toISOString().split("T")[0]
              : null
          }
          onChange={(e) => {
            setDate(e.target.value);
          }}
        />
      </div>
      <MeasurementsForm
        clothingOptions={clothingOptions}
        setClothingOptions={setClothingOptions}
        setWaist={setWaist}
        setNaturalWaist={setNaturalWaist}
        setGarmentLength={setGarmentLength}
        setHalfAcross={setHalfAcross}
        setChest={setChest}
        setTrouserWaistPosition={setTrouserWaistPosition}
        setSeat={setSeat}
        setNeck={setNeck}
        setShoulder={setShoulder}
        setSleeveLength={setSleeveLength}
        setSideSeamLength={setSideSeamLength}
        setBodyRise={setBodyRise}
        setInsideLeg={setInsideLeg}
        setWrist={setWrist}
        setTopArm={setTopArm}
        setScyeDepth={setScyeDepth}
        setArmSleeveHole={setArmSleeveHole}
        setFrontalCollar={setFrontalCollar}
        setHip={setHip}
        setFrontRise={setFrontRise}
        setBackRise={setBackRise}
        setThigh={setThigh}
        setKnee={setKnee}
        setHem={setHem}
        setOutseam={setOutseam}
        setHeadSize={setHeadSize}
        setFootSize={setFootSize}
        setDescription={setDescription}
        loading={loading}
      />
    </>
  );
};

export default CustomerForm;
