const ProductsForm = ({
  setProductName,
  setProductQuantity,
  setProductCategory,
  setProductPhoto,
  setProductPrice,
  setProductDate,
  productName,
  productQuantity,
  productCategory,
  productPhoto,
  productPrice,
  productDate,
  loading
}) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  return (
    <>
      <div className="form-row">
        <label>Product Name</label>
        <input
          type="text"
          placeholder="Enter name of product"
          value={productName || undefined}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label>Quantity</label>
        <input
          type="number"
          onChange={(e) => setProductQuantity(e.target.value)}
          value={productQuantity || undefined}
          placeholder="Product quantity"
        />
      </div>
      <div className="form-row">
        <label>Category</label>
        <select
          onChange={(e) => setProductCategory(e.target.value)}
          value={productCategory || undefined}
        >
          <option value="cotton">Cotton</option>
          <option value="linen">Linen</option>
          <option value="wool">Wool</option>
          <option value="polyester">Polyester</option>
          <option value="other">Other</option>
        </select>
      </div>
      <div className="form-row">
        <label>Product Photo</label>
        <input
          type="file"
          name="cover"
          onChange={(e) => setProductPhoto(e.target.files[0])}
        />
      </div>
      <div className="form-row">
        <label>Price</label>
        <input
          type="number"
          step="0.01"
          placeholder="Product price"
          onChange={(e) => setProductPrice(e.target.value)}
          value={productPrice || undefined}
        />
      </div>
      <div className="form-row">
        <label>Date</label>
        <input
          type="date"
          onChange={(e) =>
            setProductDate(new Date(e.target.value).toISOString().split("T")[0])
          }
          value={productDate ? formatDate(productDate) : undefined}
        />
      </div>
      <div className="form-row">
        <button type="submit">{loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}</button>
      </div>
    </>
  );
};

export default ProductsForm;
