import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const OrderForm = ({
  customerSurname,
  customerEmail,
  customerPhone,
  customerAddress,
  orderId,
  loading,
  setMeasurer,
  setCutter,
  setSewer,
  setOrderStatus,
  setOrderNotes,
}) => {
  const [users, setUsers] = useState([]);
  const [order, setOrder] = useState(null);
  const devBaseUrl = "http://localhost:5000";
  const prodBaseUrl = "https://www.khamcotailor.com";
  const currentBaseUrl =
    window.location.hostname === "localhost" ? devBaseUrl : prodBaseUrl;
  const apiURL = `${currentBaseUrl}/api/customer-array`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    const getAllUsers = async () => {
      await axios
        .get(`${currentBaseUrl}/admin/tailors`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setUsers(response.data.users);
        })
        .catch((error) => {
          toast.error(error.message);
        });
    };

    const getOrder = async () => {
      await axios
        .get(`${currentBaseUrl}/orders/${orderId}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setOrder(response.data.order);
        })
        .catch((error) => {
          toast.error(error.message);
        });
    };

    getOrder();
    getAllUsers();
  }, [apiURL, authKey, currentBaseUrl, orderId]);

  return (
    <>
      <div className="form-row">
        <label htmlFor="customer_surname">Customer Surname</label>
        <input
          type="text"
          placeholder="Customer surname"
          readOnly
          defaultValue={customerSurname}
          className="cursor-not-allowed"
        />
      </div>
      <div className="form-row">
        <label htmlFor="customer_email">Email</label>
        <input
          type="email"
          readOnly
          placeholder="Customer email"
          defaultValue={customerEmail}
          className="cursor-not-allowed"
        />
      </div>
      <div className="form-row">
        <label htmlFor="customer_phone">Phone Number</label>
        <input
          type="tel"
          readOnly
          placeholder="Customer phone number"
          defaultValue={customerPhone}
          className="cursor-not-allowed"
        />
      </div>
      <div className="form-row">
        <label htmlFor="customer_address">Address</label>
        <input
          type="text"
          placeholder="Customer address"
          readOnly
          defaultValue={customerAddress}
          className="cursor-not-allowed"
        />
      </div>
      <div className="form-row">
        <label>Measurer</label>
        <select onChange={(e) => setMeasurer(e.target.value)}>
          {users.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label>Cutter</label>
        <select onChange={(e) => setCutter(e.target.value)}>
          {users.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label>Sewer</label>
        <select onChange={(e) => setSewer(e.target.value)}>
          {users.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="form-row">
        <label>Order Status</label>
        <select onChange={(e) => setOrderStatus(e.target.value)}>
          <option label="--Select order status --"></option>
          <option value="Accepted" label="Accepted"></option>
          <option value="In Progress" label="In Progress"></option>
          <option value="Complete" label="Complete"></option>
          <option value="Awaiting Delivery" label="Awaiting Delivery"></option>
          <option value="Delivered" label="Delivered"></option>
        </select>
      </div>
      {order && (
        <div className="form-row">
          <label htmlFor="order_start_time">Order Start Date</label>
          <input
            type="datetime-local"
            name="order_start_time"
            id="order_start_time"
            defaultValue={`${order.order_start_time}`}
          />
        </div>
      )}
      {order && (
        <div className="form-row">
          <label>Other Details</label>
          <textarea
            placeholder="Optional notes"
            onChange={(e) => setOrderNotes(e.target.value)}
          >
            {order.order_notes}
          </textarea>
        </div>
      )}
      <div className="form-row">
        <button type="submit">
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export default OrderForm;
